import type { Authenticator } from '@auth0/web-ui-components-core';
import {
  FACTOR_TYPE_PUSH_NOTIFICATION,
  type MFAType,
  getComponentStyles,
  USER_MFA_SCOPES,
} from '@auth0/web-ui-components-core';
import * as React from 'react';
import { toast } from 'sonner';

import { DeleteFactorConfirmation } from '../../../components/my-account/mfa/delete-factor-confirmation';
import { MFAEmptyState } from '../../../components/my-account/mfa/empty-state';
import { MFAErrorState } from '../../../components/my-account/mfa/error-state';
import { FactorsList } from '../../../components/my-account/mfa/factors-list';
import { UserMFASetupForm } from '../../../components/my-account/mfa/user-mfa-setup-form';
import { Badge } from '../../../components/ui/badge';
import { Button } from '../../../components/ui/button';
import { Card, CardContent, CardDescription, CardTitle } from '../../../components/ui/card';
import { List, ListItem } from '../../../components/ui/list';
import { Spinner } from '../../../components/ui/spinner';
import { withMyAccountService } from '../../../hoc/with-services';
import { useMFA } from '../../../hooks/my-account/mfa/use-mfa';
import { useTheme } from '../../../hooks/use-theme';
import { useTranslator } from '../../../hooks/use-translator';
import { ENROLL } from '../../../lib/mfa-constants';
import type { CONFIRM } from '../../../lib/mfa-constants';
import { cn } from '../../../lib/theme-utils';
import type { UserMFAMgmtProps } from '../../../types/my-account/mfa/mfa-types';

/**
 * UserMFAMgmt Component
 *
 * A component responsible for managing Multi-Factor Authentication (MFA) factors for a user.
 * This component handles fetching the MFA access token, fetching authenticators, enrolling, and deletion of MFA factors, and manages the MFA access token.
 * It operates in both ProxyMode (RWA) and SPA modes for authentication.
 * - **ProxyMode (RWA)**: In this mode, the component interacts with a proxy service to manage MFA
 * - **SPA (Single Page Application)**: In this mode, the component communicates directly with the API to manage MFA factors.
 */
function UserMFAMgmtComponent({
  customMessages = {},
  styling = {
    variables: {
      common: {},
      light: {},
      dark: {},
    },
    classes: {},
  },
  hideHeader = false,
  showActiveOnly = false,
  disableEnroll = false,
  disableDelete = false,
  readOnly = false,
  factorConfig = {},
  onEnroll,
  onDelete,
  onFetch,
  onErrorAction,
  onBeforeAction,
  schema,
}: UserMFAMgmtProps): React.JSX.Element {
  const { t } = useTranslator('mfa', customMessages);
  const { loader, isDarkMode } = useTheme();
  const currentStyles = React.useMemo(
    () => getComponentStyles(styling, isDarkMode),
    [styling, isDarkMode],
  );
  const { fetchFactors, enrollMfa, deleteMfa, confirmEnrollment } = useMFA();

  const [factorsByType, setFactorsByType] = React.useState<Record<MFAType, Authenticator[]>>(
    {} as Record<MFAType, Authenticator[]>,
  );
  const [loading, setLoading] = React.useState(true);
  const [error, setError] = React.useState<string | null>(null);
  const [isDeletingFactor, setIsDeletingFactor] = React.useState(false);
  const [dialogOpen, setDialogOpen] = React.useState(false);
  const [enrollFactor, setEnrollFactor] = React.useState<MFAType | null>(null);
  const [isDeleteDialogOpen, setIsDeleteDialogOpen] = React.useState(false);
  const [factorToDelete, setFactorToDelete] = React.useState<{
    id: string;
    type: MFAType;
  } | null>(null);

  /**
   * Loads the available MFA factors from the API and updates the state.
   * This is called on initial load and when factors need to be refreshed.
   */
  const loadFactors = React.useCallback(async () => {
    setLoading(true);
    setError(null);
    try {
      const factors = await fetchFactors(showActiveOnly);
      setFactorsByType(factors as Record<MFAType, Authenticator[]>);
      onFetch?.();
    } catch (err) {
      setError(t('errors.factors_loading_error'));
    }

    setLoading(false);
  }, [fetchFactors, showActiveOnly, onFetch, onErrorAction]);

  React.useEffect(() => {
    loadFactors();
  }, []);

  /**
   * Get visible factor types based on configuration
   */
  const visibleFactorTypes = React.useMemo(() => {
    return (Object.keys(factorsByType) as MFAType[]).filter(
      (factorType) => factorConfig[factorType]?.visible !== false,
    );
  }, [factorsByType, factorConfig]);

  /**
   * Check if there are no active factors across all visible factor types
   */
  const hasNoActiveFactors = React.useMemo(() => {
    return visibleFactorTypes.every((type) => !factorsByType[type]?.some((f) => f.enrolled));
  }, [visibleFactorTypes, factorsByType]);

  /**
   * Handles the enrollment button click for a specific MFA factor.
   * Opens the enrollment dialog for the chosen factor.
   *
   * @param {MFAType} factor - The MFA factor to be enrolled.
   */
  const handleEnroll = (factor: MFAType) => {
    setEnrollFactor(factor);
    setDialogOpen(true);
  };

  const handleCloseDialog = React.useCallback(() => {
    setDialogOpen(false);

    // Reload factors if closing push notification enrollment
    if (enrollFactor === FACTOR_TYPE_PUSH_NOTIFICATION) {
      loadFactors();
    }

    setEnrollFactor(null);
  }, [enrollFactor, loadFactors]);

  /**
   * Handles the initial click on the delete button for an MFA factor.
   * This function either:
   * 1. Triggers the onBeforeAction callback and proceeds with deletion if approved
   * 2. Opens a confirmation dialog if no onBeforeAction is provided
   *
   * The function prevents deletion if:
   * - Component is in readonly mode
   * - Delete action is disabled
   * - onBeforeAction returns false
   *
   * @param {string} factorId - The unique identifier of the MFA factor to delete
   * @param {MFAType} factorType - The type of MFA factor being deleted (e.g., 'sms', 'email', 'otp')
   * @returns {Promise<void>}
   */
  const handleDeleteFactor = React.useCallback(
    async (factorId: string, factorType: MFAType) => {
      if (readOnly || disableDelete) return;

      if (onBeforeAction) {
        // If onBeforeAction exists, proceed directly
        const canProceed = await onBeforeAction('delete', factorType);
        if (!canProceed) return;
        await handleConfirmDelete(factorId);
      } else {
        setFactorToDelete({ id: factorId, type: factorType });
        setIsDeleteDialogOpen(true);
      }
    },
    [readOnly, disableDelete, onBeforeAction],
  );

  /**
   * Handles the confirmation and execution of MFA factor deletion.
   * This callback is triggered when a user confirms the deletion in the confirmation dialog
   * or when deletion is approved through onBeforeAction.
   *
   * The function:
   * 1. Deletes the MFA factor
   * 2. Reloads the factors list
   * 3. Shows success/error notifications
   * 4. Handles cleanup of dialog and loading states
   *
   * @param {string} factorId - The unique identifier of the MFA factor to delete
   * @throws {Error} When deletion fails or factors cannot be reloaded
   */
  const handleConfirmDelete = React.useCallback(
    async (factorId: string) => {
      setIsDeletingFactor(true);

      const cleanUp = () => {
        setIsDeletingFactor(false);
        setIsDeleteDialogOpen(false);
        setFactorToDelete(null);
      };

      try {
        await deleteMfa(factorId);
      } catch (err) {
        const error = err instanceof Error ? err : new Error(t('errors.delete_factor'));
        toast.error(t('errors.delete_factor'));
        onErrorAction?.(error, 'delete');
        cleanUp();
        return;
      }

      toast.success(t('remove_factor'), {
        duration: 2000,
        onAutoClose: () => onDelete?.(),
      });

      try {
        await loadFactors();
      } catch (err) {
        const error = err instanceof Error ? err : new Error(t('errors.factors_loading_error'));
        onErrorAction?.(error, 'delete');
      } finally {
        cleanUp();
      }
    },
    [deleteMfa, loadFactors, onDelete, onErrorAction, t],
  );

  /**
   * Handles the successful enrollment of an MFA factor.
   * Displays a success message and reloads the factors list.
   */
  const handleEnrollSuccess = React.useCallback(async () => {
    setDialogOpen(false);
    setEnrollFactor(null);
    try {
      toast.success(t('enroll_factor'), {
        duration: 2000,
        onAutoClose: () => {
          onEnroll?.();
        },
      });
      await loadFactors();
    } catch {
      toast.dismiss();
      toast.error(t('errors.factors_loading_error'));
    }
  }, [loadFactors, onEnroll, t]);

  /**
   * Handles errors during the enrollment or confirmation process.
   *
   * @param {Error} error - The error object containing the failure message.
   * @param {string} stage - The stage of the process ('enroll' or 'confirm').
   */
  const handleEnrollError = React.useCallback(
    (error: Error, stage: typeof ENROLL | typeof CONFIRM) => {
      toast.error(
        `${stage === ENROLL ? t('enrollment') : t('confirmation')} ${t('errors.failed', { message: error.message })}`,
      );
      onErrorAction?.(error, stage);
    },
    [onErrorAction, t],
  );

  return (
    <div style={currentStyles.variables}>
      {loading ? (
        <div className="flex items-center justify-center py-16">{loader || <Spinner />}</div>
      ) : (
        <Card
          className={cn('py-10 px-8 sm:py-8 sm:px-6', currentStyles.classes?.['UserMFAMgmt-card'])}
        >
          <CardContent>
            {error ? (
              <MFAErrorState
                title={t('component_error_title')}
                description={t('component_error_description')}
              />
            ) : (
              <>
                {!hideHeader && (
                  <>
                    <CardTitle
                      id="mfa-management-title"
                      className="text-2xl text-(length:--font-size-heading) font-medium text-left"
                    >
                      {t('title')}
                    </CardTitle>
                    <CardDescription
                      id="mfa-management-desc"
                      className="text-sm text-(length:--font-size-paragraph) text-muted-foreground text-left"
                    >
                      {t('description')}
                    </CardDescription>
                  </>
                )}
                {showActiveOnly && hasNoActiveFactors ? (
                  <MFAEmptyState message={t('no_active_mfa')} />
                ) : (
                  <List
                    className="flex flex-col gap-0 w-full"
                    aria-labelledby="mfa-management-title"
                    aria-describedby="mfa-management-desc"
                  >
                    {visibleFactorTypes.map((factorType) => {
                      const factors = factorsByType[factorType] || [];
                      const activeFactors = factors.filter((f) => f.enrolled);
                      const isEnabledFactor = factorConfig?.[factorType]?.enabled !== false;
                      const hasActiveFactors = activeFactors.length > 0;

                      return (
                        <ListItem
                          key={factorType}
                          className={cn(
                            'w-full p-0 m-0 py-6 gap-3',
                            !isEnabledFactor && 'opacity-50 pointer-events-none',
                          )}
                          aria-disabled={!isEnabledFactor}
                          tabIndex={0}
                          aria-label={t(`${factorType}.title`)}
                        >
                          <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-3 mb-3">
                            <div className="flex flex-wrap items-center gap-2 sm:gap-3">
                              <span
                                className={cn(
                                  'break-words text-card-foreground whitespace-normal text-base text-(length:--font-size-body) font-medium',
                                )}
                                id={`factor-title-${factorType}`}
                              >
                                {t(`${factorType}.title`)}
                              </span>

                              {hasActiveFactors && (
                                <Badge
                                  variant="success"
                                  size="sm"
                                  className="shrink-0"
                                  aria-label={t('enabled')}
                                >
                                  {t('enabled')}
                                </Badge>
                              )}
                            </div>

                            {!readOnly && (
                              <Button
                                size="default"
                                variant="outline"
                                className="text-sm w-full sm:w-auto shrink-0"
                                onClick={() => handleEnroll(factorType)}
                                disabled={disableEnroll || !isEnabledFactor}
                                aria-label={t(`${factorType}.button-text`)}
                                aria-describedby={`factor-title-${factorType}`}
                              >
                                {t(`${factorType}.button-text`)}
                              </Button>
                            )}
                          </div>

                          {!hasActiveFactors && (
                            <p
                              className={cn(
                                'font-normal text-sm text-(length:--font-size-paragraph) text-muted-foreground text-left break-words',
                              )}
                              id={`factor-desc-${factorType}`}
                            >
                              {t(`${factorType}.description`)}
                            </p>
                          )}

                          {hasActiveFactors && (
                            <FactorsList
                              factors={activeFactors}
                              factorType={factorType}
                              readOnly={readOnly}
                              isEnabledFactor={isEnabledFactor}
                              onDeleteFactor={handleDeleteFactor}
                              isDeletingFactor={isDeletingFactor}
                              disableDelete={disableDelete}
                              styling={styling}
                              customMessages={customMessages}
                            />
                          )}
                        </ListItem>
                      );
                    })}
                  </List>
                )}
              </>
            )}
          </CardContent>
        </Card>
      )}
      {enrollFactor && (
        <UserMFASetupForm
          open={dialogOpen}
          onClose={handleCloseDialog}
          factorType={enrollFactor}
          enrollMfa={enrollMfa}
          confirmEnrollment={confirmEnrollment}
          onSuccess={handleEnrollSuccess}
          onError={handleEnrollError}
          schema={schema}
          styling={styling}
          customMessages={customMessages}
        />
      )}
      <DeleteFactorConfirmation
        open={isDeleteDialogOpen}
        onOpenChange={(open) => !isDeletingFactor && setIsDeleteDialogOpen(open)}
        factorToDelete={factorToDelete}
        isDeletingFactor={isDeletingFactor}
        onConfirm={handleConfirmDelete}
        onCancel={() => setIsDeleteDialogOpen(false)}
        styling={styling}
        customMessages={customMessages}
      />
    </div>
  );
}

export const UserMFAMgmt = withMyAccountService(UserMFAMgmtComponent, USER_MFA_SCOPES);

import {
  type MFAType,
  FACTOR_TYPE_EMAIL,
  FACTOR_TYPE_PHONE,
  FACTOR_TYPE_TOTP,
  FACTOR_TYPE_PUSH_NOTIFICATION,
  FACTOR_TYPE_RECOVERY_CODE,
  getComponentStyles,
} from '@auth0/web-ui-components-core';
import * as React from 'react';

import AppleLogo from '../../../assets/icons/apple-logo';
import GoogleLogo from '../../../assets/icons/google-logo';
import { useRecoveryCodeGeneration } from '../../../hooks/my-account/mfa/use-recovery-code';
import { useTheme } from '../../../hooks/use-theme';
import { useTranslator } from '../../../hooks/use-translator';
import type { ENTER_OTP } from '../../../lib/mfa-constants';
import {
  ENTER_QR,
  ENTER_CONTACT,
  QR_PHASE_INSTALLATION,
  SHOW_RECOVERY_CODE,
} from '../../../lib/mfa-constants';
import { cn } from '../../../lib/theme-utils';
import type { UserMFASetupFormProps } from '../../../types/my-account/mfa/mfa-types';
import { Button } from '../../ui/button';
import { Card } from '../../ui/card';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '../../ui/dialog';
import { Separator } from '../../ui/separator';

import { ContactInputForm } from './contact-input-form';
import { QRCodeEnrollmentForm } from './qr-code-enrollment-form';
import { ShowRecoveryCode } from './show-recovery-code';

type EnrollmentPhase =
  | typeof ENTER_CONTACT
  | typeof ENTER_OTP
  | typeof ENTER_QR
  | typeof QR_PHASE_INSTALLATION
  | typeof SHOW_RECOVERY_CODE
  | null;

export function UserMFASetupForm({
  open,
  onClose,
  factorType,
  enrollMfa,
  confirmEnrollment,
  onSuccess,
  onError,
  schema,
  styling = {
    variables: {
      common: {},
      light: {},
      dark: {},
    },
    classes: {},
  },
  customMessages = {},
}: UserMFASetupFormProps) {
  const { t } = useTranslator('mfa', customMessages);
  const { isDarkMode } = useTheme();
  const currentStyles = React.useMemo(
    () => getComponentStyles(styling, isDarkMode),
    [styling, isDarkMode],
  );

  // Initialize phase as null, meaning no UI shown by default
  const [phase, setPhase] = React.useState<EnrollmentPhase>(null);
  const { fetchRecoveryCode, loading, recoveryCodeData, resetRecoveryCodeData } =
    useRecoveryCodeGeneration({
      factorType,
      enrollMfa,
      onError,
      onClose,
    });

  React.useEffect(() => {
    if (!open) {
      setPhase(null);
    }
  }, [open]);

  React.useEffect(() => {
    if (!open) return;
    const phaseMap: Partial<Record<MFAType, EnrollmentPhase>> = {
      [FACTOR_TYPE_EMAIL]: ENTER_CONTACT,
      [FACTOR_TYPE_PHONE]: ENTER_CONTACT,
      [FACTOR_TYPE_PUSH_NOTIFICATION]: QR_PHASE_INSTALLATION,
      [FACTOR_TYPE_TOTP]: ENTER_QR,
      [FACTOR_TYPE_RECOVERY_CODE]: SHOW_RECOVERY_CODE,
    };

    setPhase(phaseMap[factorType] ?? null);
  }, [open, factorType]);

  React.useEffect(() => {
    if (phase === SHOW_RECOVERY_CODE) {
      fetchRecoveryCode();
    }
  }, [phase]);

  const renderInstallationPhase = () => (
    <div style={currentStyles?.variables} className="w-full max-w-sm mx-auto">
      <div className="flex flex-col items-center justify-center flex-1 space-y-10">
        <p
          className={cn(
            'text-center text-primary text-sm text-(length:--font-size-paragraph) font-normal',
          )}
        >
          {t('enrollment_form.show_otp.install_guardian_description')}
        </p>
        <div className="flex gap-4 w-full">
          <a
            href="https://apps.apple.com/us/app/auth0-guardian/id1093447833"
            target="_blank"
            rel="noopener noreferrer"
            className="flex-1"
          >
            <Card className="flex flex-col items-center gap-1 min-w-24 p-6 h-full">
              <AppleLogo className="w-8 h-8" />
              <span className={cn('text-sm text-(length:--font-size-paragraph) text-center')}>
                {t('app-store')}
              </span>
            </Card>
          </a>
          <a
            href="https://play.google.com/store/apps/details?id=com.auth0.guardian"
            target="_blank"
            rel="noopener noreferrer"
            className="flex-1"
          >
            <Card className="flex flex-col items-center gap-1 min-w-24 p-6 h-full">
              <GoogleLogo className="w-8 h-8" />
              <span className={cn('text-sm text-(length:--font-size-paragraph) text-center')}>
                {t('google-play')}
              </span>
            </Card>
          </a>
        </div>
        <div className="flex flex-row justify-end gap-3 w-full mt-6 mb-6">
          <Button
            type="button"
            className="text-sm"
            variant="outline"
            size="default"
            onClick={onClose}
          >
            {t('cancel')}
          </Button>
          <Button
            type="button"
            className="text-sm"
            size="default"
            onClick={() => setPhase(ENTER_QR)}
          >
            {t('continue')}
          </Button>
        </div>
      </div>
    </div>
  );

  const renderForm = () => {
    switch (phase) {
      case QR_PHASE_INSTALLATION:
        return renderInstallationPhase();
      case ENTER_CONTACT:
        return (
          <ContactInputForm
            factorType={factorType}
            enrollMfa={enrollMfa}
            confirmEnrollment={confirmEnrollment}
            onError={onError}
            onSuccess={onSuccess}
            onClose={onClose}
            schema={schema}
            styling={styling}
            customMessages={customMessages}
          />
        );
      case ENTER_QR:
        return (
          <QRCodeEnrollmentForm
            factorType={factorType}
            enrollMfa={enrollMfa}
            confirmEnrollment={confirmEnrollment}
            onError={onError}
            onSuccess={onSuccess}
            onClose={onClose}
            styling={styling}
            customMessages={customMessages}
          />
        );
      case SHOW_RECOVERY_CODE:
        return (
          <ShowRecoveryCode
            recoveryCode={recoveryCodeData.recoveryCode}
            authSession={recoveryCodeData.authSession}
            authenticationMethodId={recoveryCodeData.authenticationMethodId}
            confirmEnrollment={confirmEnrollment}
            onSuccess={() => {
              onSuccess();
              resetRecoveryCodeData();
            }}
            onBack={() => {
              resetRecoveryCodeData();
              onClose();
            }}
            styling={styling}
            factorType={factorType}
            loading={loading}
            customMessages={customMessages}
          />
        );
      default:
        return null;
    }
  };

  return (
    <Dialog open={open && Boolean(phase)} onOpenChange={onClose}>
      <DialogContent
        style={currentStyles.variables}
        aria-describedby="mfa-setup-form"
        className={cn(
          'w-[400px] max-h-[90vh]',
          currentStyles.classes?.['UserMFASetupForm-dialogContent'],
        )}
      >
        <DialogHeader>
          <DialogTitle className="text-left font-medium text-xl text-(length:--font-size-title)">
            {t('enrollment_form.enroll_title')}
          </DialogTitle>
          <Separator className="my-2" />
        </DialogHeader>
        {renderForm()}
      </DialogContent>
    </Dialog>
  );
}

import {
  getComponentStyles,
  FACTOR_TYPE_TOTP,
  FACTOR_TYPE_PUSH_NOTIFICATION,
} from '@auth0/web-ui-components-core';
import * as React from 'react';

import { useOtpEnrollment } from '../../../hooks/my-account/mfa/use-otp-enrollment';
import { useTheme } from '../../../hooks/use-theme';
import { useTranslator } from '../../../hooks/use-translator';
import { QR_PHASE_ENTER_OTP, QR_PHASE_SCAN } from '../../../lib/mfa-constants';
import { cn } from '../../../lib/theme-utils';
import type { QRCodeEnrollmentFormProps } from '../../../types/my-account/mfa/mfa-types';
import { Button } from '../../ui/button';
import { CopyableTextField } from '../../ui/copyable-text-field';
import { QRCodeDisplayer } from '../../ui/qr-code';
import { Spinner } from '../../ui/spinner';

import { OTPVerificationForm } from './otp-verification-form';

const PHASES = {
  SCAN: QR_PHASE_SCAN,
  ENTER_OTP: QR_PHASE_ENTER_OTP,
} as const;

type Phase = (typeof PHASES)[keyof typeof PHASES];

export function QRCodeEnrollmentForm({
  factorType,
  enrollMfa,
  confirmEnrollment,
  onError,
  onSuccess,
  onClose,
  styling = {
    variables: {
      common: {},
      light: {},
      dark: {},
    },
    classes: {},
  },
  customMessages = {},
}: QRCodeEnrollmentFormProps) {
  const [phase, setPhase] = React.useState<Phase>(QR_PHASE_SCAN);
  const { t } = useTranslator('mfa', customMessages);
  const { isDarkMode } = useTheme();
  const currentStyles = React.useMemo(
    () => getComponentStyles(styling, isDarkMode),
    [styling, isDarkMode],
  );

  const { fetchOtpEnrollment, otpData, resetOtpData, loading } = useOtpEnrollment({
    factorType,
    enrollMfa,
    onError,
    onClose,
  });

  React.useEffect(() => {
    // Only fetch if we don't have data and we're in scan phase
    if (!otpData?.barcodeUri) {
      fetchOtpEnrollment();
    }
  }, [otpData?.barcodeUri, fetchOtpEnrollment]);

  const handleContinue = React.useCallback(async () => {
    if (factorType === FACTOR_TYPE_PUSH_NOTIFICATION) {
      try {
        await confirmEnrollment(
          factorType,
          otpData.authSession,
          otpData.authenticationMethodId,
          {},
        );

        onSuccess();
        resetOtpData();
        onClose();
      } catch (error) {
        onError(error instanceof Error ? error : new Error('Unknown error'), 'confirm');
      }
    } else {
      setPhase(QR_PHASE_ENTER_OTP);
    }
  }, [factorType, otpData, confirmEnrollment, onSuccess, onError, resetOtpData, onClose]);

  const handleBack = React.useCallback(() => {
    setPhase(QR_PHASE_SCAN);
  }, []);

  const renderQrScreen = () => {
    return (
      <div style={currentStyles.variables} className="w-full">
        {loading ? (
          <div
            className="absolute inset-0 flex items-center justify-center"
            role="status"
            aria-live="polite"
          >
            <Spinner aria-label={t('loading')} />
          </div>
        ) : (
          <div className="w-full max-w-sm mx-auto text-center">
            <div className="mb-6">
              <div className="flex justify-center items-center mb-6">
                <QRCodeDisplayer
                  size={150}
                  value={otpData.barcodeUri}
                  alt={t('enrollment_form.show_otp.qr_code_description')}
                />
              </div>
              <p
                id="qr-description"
                className={cn(
                  'font-normal block text-sm text-center text-(length:--font-size-paragraph) text-primary',
                )}
              >
                {factorType === FACTOR_TYPE_TOTP
                  ? t('enrollment_form.show_otp.title')
                  : t('enrollment_form.show_auth0_guardian_title')}
              </p>
            </div>

            <div aria-describedby="qr-description">
              <CopyableTextField value={otpData.manualInputCode || otpData?.barcodeUri} />

              <div className="mt-3" />

              <div className="flex flex-row justify-end gap-3 mt-6 mb-6">
                <Button
                  type="button"
                  className="text-sm"
                  variant="outline"
                  size="default"
                  onClick={onClose}
                  aria-label={t('cancel')}
                >
                  {t('cancel')}
                </Button>
                <Button
                  type="button"
                  className="text-sm"
                  size="default"
                  onClick={handleContinue}
                  aria-label={t('continue')}
                >
                  {t('continue')}
                </Button>
              </div>
            </div>
          </div>
        )}
      </div>
    );
  };

  const renderOtpScreen = () => (
    <OTPVerificationForm
      factorType={factorType}
      confirmEnrollment={confirmEnrollment}
      onError={onError}
      onSuccess={onSuccess}
      onClose={onClose}
      authSession={otpData.authSession}
      authenticationMethodId={otpData.authenticationMethodId}
      onBack={handleBack}
      styling={styling}
      customMessages={customMessages}
    />
  );

  return phase === QR_PHASE_SCAN ? renderQrScreen() : renderOtpScreen();
}

import {
  type MFAType,
  FACTOR_TYPE_EMAIL,
  FACTOR_TYPE_TOTP,
  FACTOR_TYPE_PUSH_NOTIFICATION,
  getComponentStyles,
} from '@auth0/web-ui-components-core';
import * as React from 'react';
import { useForm } from 'react-hook-form';

import { useOtpConfirmation } from '../../../hooks/my-account/mfa/use-otp-confirmation';
import { useTheme } from '../../../hooks/use-theme';
import { useTranslator } from '../../../hooks/use-translator';
import { cn } from '../../../lib/theme-utils';
import type { OTPVerificationFormProps } from '../../../types/my-account/mfa/mfa-types';
import { Button } from '../../ui/button';
import { Form, FormField, FormItem, FormLabel, FormControl, FormMessage } from '../../ui/form';
import { OTPField } from '../../ui/otp-field';

type OtpForm = {
  userOtp: string;
};

// Mask contact info (email or phone)
/**
 * Masks sensitive contact information for display purposes.
 *
 * For email addresses:
 * - Shows the first 2 characters of the local part
 * - Masks the remaining characters with asterisks
 * - Preserves the @ symbol and domain unchanged
 *
 * For phone numbers:
 * - Shows the first 3 and last 3 characters
 * - Masks the middle characters with asterisks
 *
 * @param {string} contact - The contact information to mask (email or phone number)
 * @param {MFAType} factorType - The type of MFA factor to determine masking strategy
 * @returns {string} The masked contact information
 *
 * @example
 * // Email masking
 * maskContact('john.doe@example.com', 'email') // Returns 'jo******@example.com'
 *
 * @example
 * // Phone number masking
 * maskContact('1234567890', 'sms') // Returns '123****890'
 */
const maskContact = (contact: string, factorType: MFAType): string => {
  if (!contact) return '';

  if (factorType === FACTOR_TYPE_EMAIL) {
    const [local, domain] = contact.split('@');
    if (!domain || !local || local.length <= 2) return contact;
    return `${local.slice(0, 2)}${'*'.repeat(local.length - 2)}@${domain}`;
  }

  return contact.length > 6
    ? `${contact.slice(0, 3)}${'*'.repeat(contact.length - 6)}${contact.slice(-3)}`
    : contact;
};

export function OTPVerificationForm({
  factorType,
  confirmEnrollment,
  onError,
  onSuccess,
  onClose,
  contact,
  authSession,
  authenticationMethodId,
  onBack,
  styling = {
    variables: { common: {}, light: {}, dark: {} },
    classes: {},
  },
  customMessages = {},
}: OTPVerificationFormProps) {
  const { t } = useTranslator('mfa', customMessages);
  const { isDarkMode } = useTheme();
  const currentStyles = React.useMemo(
    () => getComponentStyles(styling, isDarkMode),
    [styling, isDarkMode],
  );

  const { onSubmitOtp, loading } = useOtpConfirmation({
    factorType,
    authSession,
    authenticationMethodId,
    confirmEnrollment,
    onError,
    onSuccess,
    onClose,
  });

  const form = useForm<OtpForm>({ mode: 'onChange' });
  const userOtp = form.watch('userOtp');

  const otpInputRef = React.useRef<HTMLInputElement>(null);

  React.useEffect(() => {
    otpInputRef.current?.focus();
  }, []);

  const handleSubmit = async (data: OtpForm) => {
    await onSubmitOtp(data);
  };

  const maskedContact = React.useMemo(
    () => (contact ? maskContact(contact, factorType) : ''),
    [contact, factorType],
  );

  const buttonText = loading ? t('enrollment_form.show_otp.verifying') : t('submit');

  return (
    <div style={currentStyles.variables} className="w-full max-w-sm mx-auto text-center">
      <Form {...form}>
        <form
          onSubmit={form.handleSubmit(handleSubmit)}
          autoComplete="off"
          className="space-y-6"
          aria-describedby="otp-description"
        >
          <p
            id="otp-description"
            className={cn(
              'text-sm text-primary font-normal text-center',
              'text-(length:--font-size-paragraph)',
            )}
          >
            {[FACTOR_TYPE_PUSH_NOTIFICATION, FACTOR_TYPE_TOTP].includes(factorType)
              ? t('enrollment_form.show_otp.enter_opt_code')
              : t('enrollment_form.show_otp.enter_verify_code', { verifier: maskedContact })}
          </p>

          <FormField
            control={form.control}
            name="userOtp"
            render={({ field }) => (
              <FormItem>
                <FormLabel
                  className="text-sm font-medium text-(length:--font-size-label)"
                  htmlFor="otp-input"
                >
                  {t('enrollment_form.show_otp.one_time_passcode')}
                </FormLabel>
                <FormControl>
                  <OTPField
                    id="otp-input"
                    length={6}
                    separator={{ character: '-', afterEvery: 3 }}
                    onChange={field.onChange}
                    inputRef={otpInputRef}
                    aria-invalid={!!form.formState.errors.userOtp}
                    value={field.value || ''}
                  />
                </FormControl>
                <FormMessage
                  className="text-sm text-left text-(length:--font-size-paragraph)"
                  id="otp-error"
                  role="alert"
                />
              </FormItem>
            )}
          />

          <div className="flex flex-row justify-end gap-3 mt-6 mb-6">
            <Button
              type="button"
              className="text-sm"
              variant="outline"
              size="default"
              onClick={onBack}
              aria-label={t('back')}
            >
              {t('back')}
            </Button>

            <Button
              type="submit"
              className="text-sm"
              size="default"
              disabled={userOtp?.length !== 6 || loading}
              aria-label={buttonText}
            >
              {buttonText}
            </Button>
          </div>
        </form>
      </Form>
    </div>
  );
}

import {
  FACTOR_TYPE_EMAIL,
  createEmailContactSchema,
  createSmsContactSchema,
  type EmailContactForm,
  type SmsContactForm,
