import type {
  MFAType,
  EnrollOptions,
  ConfirmEnrollmentOptions,
} from '@auth0/web-ui-components-core';
import { MFAMappers } from '@auth0/web-ui-components-core';
import { useCallback } from 'react';

import type { UseMFAResult } from '../../../types/my-account/mfa/mfa-types';
import { useCoreClient } from '../../use-core-client';

export function useMFA(): UseMFAResult {
  const { coreClient } = useCoreClient();

  if (!coreClient) {
    throw new Error(
      'useMFA must be used within Auth0ComponentProvider with initialized CoreClient',
    );
  }

  const fetchFactors = useCallback(
    async (onlyActive = false) => {
      const client = coreClient.getMyAccountApiClient();
      const [availableFactorsResponse, enrolledFactorsResponse] = await Promise.all([
        client.factors.list(),
        client.authenticationMethods.list(),
      ]);
      return MFAMappers.fromAPI(availableFactorsResponse, enrolledFactorsResponse, onlyActive);
    },
    [coreClient],
  );

  const enrollMfa = useCallback(
    (factorName: MFAType, options: EnrollOptions = {}) => {
      const client = coreClient.getMyAccountApiClient();
      const params = MFAMappers.buildEnrollParams(factorName, options);
      return client.authenticationMethods.create(params);
    },
    [coreClient],
  );

  const deleteMfa = useCallback(
    (authenticatorId: string) =>
      coreClient.getMyAccountApiClient().authenticationMethods.delete(authenticatorId),
    [coreClient],
  );

  const confirmEnrollment = useCallback(
    (
      factorType: MFAType,
      authSession: string,
      authenticationMethodId: string,
      options: ConfirmEnrollmentOptions,
    ) => {
      const client = coreClient.getMyAccountApiClient();
      const params = MFAMappers.buildConfirmEnrollmentParams(factorType, authSession, options);
      return client.authenticationMethods.verify(authenticationMethodId, params);
    },
    [coreClient],
  );

  return {
    fetchFactors,
    enrollMfa,
    deleteMfa,
    confirmEnrollment,
  };
}

import {
  type CreateAuthenticationMethodResponseContent,
  normalizeError,
  type MFAType,
} from '@auth0/web-ui-components-core';
import { useState, useCallback } from 'react';

import { ENROLL } from '../../../lib/mfa-constants';
import { useTranslator } from '../../use-translator';

type UseOtpEnrollmentProps = {
  factorType: MFAType;
  enrollMfa: (
    factorType: MFAType,
    options: Record<string, string>,
  ) => Promise<CreateAuthenticationMethodResponseContent>;
  onError: (error: Error, stage: typeof ENROLL) => void;
  onClose: () => void;
};

export function useOtpEnrollment({
  factorType,
  enrollMfa,
  onError,
  onClose,
}: UseOtpEnrollmentProps) {
  const { t } = useTranslator('mfa');
  const [loading, setLoading] = useState(false);
  const [otpData, setOtpData] = useState<{
    authSession: string;
    barcodeUri: string;
    authenticationMethodId: string;
    manualInputCode?: string;
  }>({
    authSession: '',
    barcodeUri: '',
    authenticationMethodId: '',
  });

  const fetchOtpEnrollment = useCallback(async () => {
    if (loading) return;
    setLoading(true);
    try {
      const response = await enrollMfa(factorType, {});
      setOtpData({
        authSession: 'auth_session' in response ? response.auth_session : '',
        barcodeUri: 'barcode_uri' in response ? response.barcode_uri : '',
        authenticationMethodId: 'id' in response ? response.id : '',
        manualInputCode: 'manual_input_code' in response ? response.manual_input_code : '',
      });
    } catch (error) {
      const normalizedError = normalizeError(error, {
        resolver: (code) =>
          t(`errors.${factorType}.${code}`, {}, 'An unexpected error occurred during enrollment.'),
      });
      onError(normalizedError, ENROLL);
      onClose();
    } finally {
      setLoading(false);
    }
  }, [loading, factorType, enrollMfa, onError, onClose, t]);

  const resetOtpData = useCallback(() => {
    setOtpData({
      authSession: '',
      barcodeUri: '',
      authenticationMethodId: '',
    });
    setLoading(false);
  }, []);

  return {
    fetchOtpEnrollment,
    otpData,
    resetOtpData,
    loading,
  };
}

import { normalizeError, type MFAType } from '@auth0/web-ui-components-core';
import { useState, useCallback } from 'react';

import { CONFIRM } from '../../../lib/mfa-constants';
import { useTranslator } from '../../use-translator';

type OtpForm = {
  userOtp: string;
};

type UseOtpConfirmationProps = {
  factorType: MFAType;
  authSession: string;
  authenticationMethodId: string;
  confirmEnrollment: (
    factor: MFAType,
    authSession: string,
    authenticationMethodId: string,
    options: { userOtpCode?: string },
  ) => Promise<unknown | null>;
  onError: (error: Error, stage: typeof CONFIRM) => void;
  onSuccess: () => void;
  onClose: () => void;
};

export function useOtpConfirmation({
  factorType,
  authSession,
  authenticationMethodId,
  confirmEnrollment,
  onError,
  onSuccess,
  onClose,
}: UseOtpConfirmationProps) {
  const { t } = useTranslator('mfa');
  const [loading, setLoading] = useState(false);

  const onSubmitOtp = useCallback(
    async (data: OtpForm) => {
      if (loading) return;
      setLoading(true);

      try {
        const options = {
          userOtpCode: data.userOtp,
        };

        const response = await confirmEnrollment(
          factorType,
          authSession,
          authenticationMethodId,
          options,
        );
        if (response) {
          onSuccess();
          onClose();
        }
      } catch (err) {
        const normalizedError = normalizeError(err, {
          resolver: (code) =>
            t(
              `errors.${factorType}.${code}`,
              {},
              'An unexpected error occurred during enrollment.',
            ),
        });
        onError(normalizedError, CONFIRM);
      } finally {
        setLoading(false);
      }
    },
    [loading, factorType, confirmEnrollment, onError, onSuccess, onClose, t],
  );

  return { onSubmitOtp, loading };
}

