import { getComponentStyles, MY_ORG_DETAILS_EDIT_SCOPES } from '@auth0/web-ui-components-core';
import * as React from 'react';

import { OrgDetails } from '../../../components/my-org/org-management/org-details/org-details';
import { Header } from '../../../components/ui/header';
import { Spinner } from '../../../components/ui/spinner';
import { withMyOrgService } from '../../../hoc/with-services';
import { useOrgDetailsEdit } from '../../../hooks/my-org/org-management/use-org-details-edit';
import { useTheme } from '../../../hooks/use-theme';
import { useTranslator } from '../../../hooks/use-translator';
import type { OrgDetailsEditProps } from '../../../types/my-org/org-management/org-details-edit-types';

/**
 * OrgDetailsEdit Component
 *
 * A comprehensive organization editing component that combines organization details
 * editing and deletion capabilities in a single interface. This component provides
 * a complete editing experience with form validation, lifecycle hooks, and user feedback.
 */
function OrgDetailsEditComponent({
  schema,
  customMessages = {},
  styling = {
    variables: { common: {}, light: {}, dark: {} },
    classes: {},
  },
  readOnly = false,
  saveAction,
  cancelAction,
  hideHeader = false,
  backButton,
}: OrgDetailsEditProps): React.JSX.Element {
  const { t } = useTranslator('org_management.org_details_edit', customMessages);
  const { isDarkMode } = useTheme();

  const {
    organization,
    isFetchLoading,
    formActions: enhancedFormActions,
  } = useOrgDetailsEdit({
    saveAction,
    cancelAction,
    readOnly,
    customMessages,
  });

  const currentStyles = React.useMemo(
    () => getComponentStyles(styling, isDarkMode),
    [styling, isDarkMode],
  );

  if (isFetchLoading) {
    return (
      <div
        style={currentStyles.variables}
        className="flex items-center justify-center min-h-96 w-full"
      >
        <Spinner />
      </div>
    );
  }

  return (
    <div style={currentStyles.variables} className="w-full">
      {!hideHeader && (
        <div className="mb-8">
          <Header
            title={t('header.title', {
              orgName: organization.display_name || organization.name || '',
            })}
            backButton={
              backButton && {
                ...backButton,
                text: t('header.back_button_text'),
              }
            }
          />
        </div>
      )}

      <div className="mb-8">
        <OrgDetails
          organization={organization}
          schema={schema?.details}
          customMessages={customMessages.details}
          styling={styling}
          readOnly={readOnly}
          formActions={enhancedFormActions}
        />
      </div>
    </div>
  );
}

export const OrgDetailsEdit = withMyOrgService(OrgDetailsEditComponent, MY_ORG_DETAILS_EDIT_SCOPES);

import type {
  OrganizationDetailsFormValues,
  OrganizationPrivate,
  OrgDetailsSchemas,
} from '@auth0/web-ui-components-core';
import { getComponentStyles, createOrganizationDetailSchema } from '@auth0/web-ui-components-core';
import { zodResolver } from '@hookform/resolvers/zod';
import * as React from 'react';
import { useForm } from 'react-hook-form';

import { useTheme } from '../../../../hooks/use-theme';
import { useTranslator } from '../../../../hooks/use-translator';
import { cn } from '../../../../lib/theme-utils';
import type { OrgDetailsProps } from '../../../../types/my-org/org-management/org-details-types';
import { Card } from '../../../ui/card';
import { Form } from '../../../ui/form';
import { FormActions } from '../../../ui/form-actions';
import { Separator } from '../../../ui/separator';
import { Spinner } from '../../../ui/spinner';

import { BrandingDetails } from './branding-details';
import { SettingsDetails } from './settings-details';

/**
 * OrgDetails Component
 *
 * A presentation component that displays organization details including settings and branding fields.
 * This component renders form fields for organization configuration in a structured layout with sections.
 * All data, validation, and business logic are handled via props passed from parent components.
 *
 */
export function OrgDetails({
  organization,
  isLoading = false,
  schema,
  customMessages = {},
  styling = {
    variables: { common: {}, light: {}, dark: {} },
    classes: {},
  },
  readOnly = false,
  formActions,
}: OrgDetailsProps): React.JSX.Element {
  const { t } = useTranslator('org_management.org_details', customMessages);

  const { isDarkMode } = useTheme();
  const currentStyles = React.useMemo(
    () => getComponentStyles(styling, isDarkMode),
    [styling, isDarkMode],
  );

  const organizationDetailSchema = React.useMemo(() => {
    const mergeFieldConfig = (field: keyof OrgDetailsSchemas, defaultError: string) => {
      const fieldConfig = schema?.[field];
      return fieldConfig
        ? {
            ...fieldConfig,
            errorMessage: fieldConfig.errorMessage || defaultError,
          }
        : {
            errorMessage: defaultError,
          };
    };

    return createOrganizationDetailSchema({
      name: mergeFieldConfig('name', t('sections.settings.fields.name.error')),
      displayName: mergeFieldConfig(
        'displayName',
        t('sections.settings.fields.display_name.error'),
      ),
      primaryColor: mergeFieldConfig(
        'primaryColor',
        t('sections.branding.fields.primary_color.error'),
      ),
      logoURL: mergeFieldConfig('logoURL', t('sections.branding.fields.logo.error')),
      backgroundColor: mergeFieldConfig(
        'backgroundColor',
        t('sections.branding.fields.page_background_color.error'),
      ),
    });
  }, [t, schema]);

  const formValues = React.useMemo(
    (): OrganizationDetailsFormValues => ({
      name: organization.name,
      display_name: organization.display_name,
      branding: {
        logo_url: organization.branding.logo_url,
        colors: {
          primary: organization.branding.colors.primary,
          page_background: organization.branding.colors.page_background,
        },
      },
    }),
    [organization],
  );

  const form = useForm<OrganizationDetailsFormValues>({
    resolver: zodResolver(organizationDetailSchema),
    defaultValues: formValues,
    values: formValues,
  });

  const hasUnsavedChanges = form.formState.isDirty;

  const onValid = React.useCallback(
    async (values: OrganizationDetailsFormValues) => {
      if (formActions?.nextAction?.onClick) {
        const payload: OrganizationPrivate = {
          ...values,
          id: organization.id,
        };

        const success = await formActions.nextAction.onClick(payload);

        if (success) {
          form.reset(values, {
            keepValues: true,
            keepDirty: false,
            keepTouched: false,
          });
        }
      }
    },
    [formActions?.nextAction, organization.id, form],
  );

  const handlePreviousAction = React.useCallback(
    (event: Event) => {
      form.reset();
      formActions?.previousAction?.onClick?.(event);
    },
    [form, formActions?.previousAction?.onClick],
  );

  if (isLoading) {
    return <Spinner />;
  }
  return (
    <div style={currentStyles.variables} className="w-full space-y-6">
      <Form {...form}>
        <form onSubmit={form.handleSubmit(onValid)} className="space-y-6">
          <Card
            data-testid="org-details-card"
            className={cn('p-6', currentStyles.classes?.OrgDetails_Card)}
          >
            <div className="space-y-6">
              <SettingsDetails
                form={form}
                readOnly={readOnly}
                customMessages={customMessages}
                className={currentStyles.classes?.OrgDetails_SettingsDetails}
              />

              <Separator />

              <BrandingDetails
                form={form}
                readOnly={readOnly}
                customMessages={customMessages}
                className={currentStyles.classes?.OrgDetails_BrandingDetails}
              />

              <FormActions
                hasUnsavedChanges={hasUnsavedChanges}
                isLoading={formActions.isLoading}
                nextAction={{
                  label: t('submit_button_label'),
                  disabled:
                    formActions?.nextAction?.disabled ||
                    !hasUnsavedChanges ||
                    formActions.isLoading ||
                    readOnly,
                  type: 'submit',
                }}
                previousAction={{
                  label: t('cancel_button_label'),
                  disabled:
                    formActions?.previousAction?.disabled || formActions.isLoading || readOnly,
                  onClick: handlePreviousAction,
                }}
                showPrevious={formActions?.showPrevious}
                unsavedChangesText={t('unsaved_changes_text')}
                showUnsavedChanges={formActions?.showUnsavedChanges}
                align={formActions?.align}
                className={currentStyles.classes?.OrgDetails_FormActions}
              />
            </div>
          </Card>
        </form>
      </Form>
    </div>
  );
}

import * as React from 'react';

import { useTranslator } from '../../../../hooks/use-translator';
import type { SettingsDetailsProps } from '../../../../types/my-org/org-management/org-details-types';
import {
  FormControl,
  FormDescription,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from '../../../ui/form';
import { Section } from '../../../ui/section';
import { TextField } from '../../../ui/text-field';

/**
 * SettingsDetails Component
 *
 * Renders the organization settings section with name and display name fields.
 * This component is focused purely on the settings-related form fields.
 */
export function SettingsDetails({
  form,
  readOnly = false,
  customMessages = {},
  className,
}: SettingsDetailsProps): React.JSX.Element {
  const { t } = useTranslator('org_management.org_details', customMessages);

  return (
    <div className={className}>
      <Section title={t('sections.settings.title')}>
        <FormField
          control={form.control}
          name="name"
          render={({ field }) => (
            <FormItem>
              <FormLabel
                className="text-sm text-(length:--font-size-label) font-medium"
                htmlFor="organization-name"
              >
                {t('sections.settings.fields.name.label')}
              </FormLabel>
              <FormControl>
                <TextField
                  id="organization-name"
                  placeholder={t('sections.settings.fields.name.placeholder')}
                  error={Boolean(form.formState.errors.name)}
                  aria-invalid={Boolean(form.formState.errors.name)}
                  readOnly={readOnly}
                  {...field}
                />
              </FormControl>
              <FormMessage
                className="text-left text-sm text-(length:--font-size-paragraph)"
                role="alert"
              />
              <FormDescription className="text-sm text-(length:--font-size-paragraph) font-normal text-left">
                {t('sections.settings.fields.name.helper_text')}
              </FormDescription>
            </FormItem>
          )}
        />

        <FormField
          control={form.control}
          name="display_name"
          render={({ field }) => (
            <FormItem>
              <FormLabel
                className="text-sm text-(length:--font-size-label) font-medium"
                htmlFor="organization-display-name"
              >
                {t('sections.settings.fields.display_name.label')}
              </FormLabel>
              <FormControl>
                <TextField
                  id="organization-display-name"
                  placeholder={t('sections.settings.fields.display_name.placeholder')}
                  error={Boolean(form.formState.errors.display_name)}
                  aria-invalid={Boolean(form.formState.errors.display_name)}
                  readOnly={readOnly}
                  {...field}
                />
              </FormControl>
              <FormMessage
                className="text-left text-sm text-(length:--font-size-paragraph)"
                role="alert"
              />
              <FormDescription className="text-sm text-(length:--font-size-paragraph) font-normal text-left">
                {t('sections.settings.fields.display_name.helper_text')}
              </FormDescription>
            </FormItem>
          )}
        />
      </Section>
    </div>
  );
}

import { LinkIcon } from 'lucide-react';
import * as React from 'react';

import { useTranslator } from '../../../../hooks/use-translator';
import type { BrandingDetailsProps } from '../../../../types/my-org/org-management/org-details-types';
import { ColorPickerInput } from '../../../ui/color-picker';
import {
  FormControl,
  FormDescription,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from '../../../ui/form';
import { ImagePreviewField } from '../../../ui/image-preview-field';
import { Section } from '../../../ui/section';

/**
 * BrandingDetails Component
 *
 * Renders the organization branding section with logo and color fields.
 * This component is focused purely on the branding-related form fields.
 */
