export type ColorPickerFormatProps = HTMLAttributes<HTMLDivElement> & {
  alpha?: boolean;
};
export const ColorPickerFormat = ({ className, alpha, ...props }: ColorPickerFormatProps) => {
  const { hsva, mode } = useColorPicker();

  if (mode === 'hex') {
    const hex = hsvaToHex(hsva);
    const alphaValue = Math.round(hsva.a * 100);
    return (
      <div className={cn('relative flex w-full shrink items-center gap-1', className)} {...props}>
        <TextField
          readOnly
          type="text"
          value={hex}
          className={cn('h-8 flex-1 rounded-lg', className)}
        />
        {alpha && <PercentageInput value={alphaValue} />}
      </div>
    );
  }

  if (mode === 'rgb') {
    const rgba = hsvaToRgba(hsva);
    const alphaValue = Math.round(hsva.a * 100);
    return (
      <div className={cn('flex shrink items-center gap-1', className)} {...props}>
        {[rgba.r, rgba.g, rgba.b].map((value, index) => (
          <TextField
            className={cn('h-8 flex-1 rounded-lg', className)}
            key={index}
            readOnly
            type="text"
            value={Math.round(value)}
          />
        ))}
        {alpha && <PercentageInput value={alphaValue} />}
      </div>
    );
  }

  if (mode === 'hsl') {
    const { h, s, v, a } = hsva;
    const l = (v * (100 - s)) / 100;
    const sPercent = s;
    const lPercent = l;
    const alphaValue = Math.round(a * 100);

    return (
      <div className={cn('flex shrink items-center gap-1', className)} {...props}>
        {[Math.round(h), Math.round(sPercent), Math.round(lPercent)].map((value, index) => (
          <TextField
            className={cn('h-8 flex-1 rounded-lg', className)}
            key={index}
            readOnly
            type="text"
            value={value}
          />
        ))}
        {alpha && <PercentageInput value={alphaValue} />}
      </div>
    );
  }

  return null;
};
ColorPickerFormat.displayName = 'ColorPickerFormat';

export type ColorPickerInputProps = {
  name?: string;
  value?: string;
  defaultValue?: string;
  onChange?: (value: string) => void;
  onChangeHsva?: (hsva: HsvaColor) => void;
  placeholder?: string;
  className?: string;
  disabled?: boolean;
  variant?: 'default' | 'input';
  children?: React.ReactNode;
  showTextInput?: boolean;
  showColorPicker?: boolean;
  alpha?: boolean;
};

export const ColorPickerInput = ({
  name,
  value,
  defaultValue = '#000000',
  onChange,
  onChangeHsva,
  placeholder = 'Enter color value',
  className,
  disabled,
  variant = 'input',
  children,
  showTextInput = true,
  showColorPicker = true,
  alpha = false,
  ...props
}: ColorPickerInputProps) => {
  const [open, setOpen] = useState(false);
  const [hsva, setHsva] = useState<HsvaColor>(() => {
    try {
      if (typeof value === 'string') {
        return hexToHsva(value);
      } else if (value && typeof value === 'object') {
        return value;
      } else if (typeof defaultValue === 'string') {
        return hexToHsva(defaultValue);
      } else if (defaultValue && typeof defaultValue === 'object') {
        return defaultValue;
      }
      return { h: 0, s: 0, v: 0, a: 1 };
    } catch {
      return { h: 0, s: 0, v: 0, a: 1 };
    }
  });
  const [textValue, setTextValue] = useState(() => {
    try {
      if (typeof value === 'string') {
        return value;
      } else if (value && typeof value === 'object') {
        return hsvaToHex(value);
      } else if (typeof defaultValue === 'string') {
        return defaultValue;
      } else if (defaultValue && typeof defaultValue === 'object') {
        return hsvaToHex(defaultValue);
      }
      return '#000000';
    } catch {
      return '#000000';
    }
  });
  const [isValidColor, setIsValidColor] = useState(true);
  const inputRef = useRef<HTMLInputElement>(null);
  const triggerRef = useRef<HTMLButtonElement>(null);

  useEffect(() => {
    if (value !== undefined) {
      try {
        let newHsva: HsvaColor;
        if (typeof value === 'string') {
          newHsva = hexToHsva(value);
        } else if (value && typeof value === 'object') {
          newHsva = value;
        } else {
          return;
        }
        setHsva(newHsva);
        setTextValue(hsvaToHex(newHsva));
      } catch (error) {
        // Intentionally ignore parse errors while avoiding an empty catch block
        void error;
      }
    }
  }, [value]);

  const currentValue = hsvaToHex(hsva);

  const validateColor = (colorValue: string): boolean => {
    return /^#([A-Fa-f0-9]{6}|[A-Fa-f0-9]{3})$/.test(colorValue);
  };

  /**
   * Formats hex color input with smart expansion rules:
   * - Single letter: repeat 6 times (e.g., 'a' -> '#aaaaaa')
   * - Two letters: repeat 3 times (e.g., 'ab' -> '#ababab')
   * - Three letters: expand each to 2 (e.g., 'abc' -> '#aabbcc')
   * - Automatically adds # prefix and filters invalid characters
   */
  const formatHexInput = (input: string): string => {
    let cleanInput = input.replace(/^#+/, '').toLowerCase();

    cleanInput = cleanInput.replace(/[^0-9a-f]/g, '');

    if (cleanInput.length === 0) return '';

    if (cleanInput.length === 1) {
      cleanInput = cleanInput.repeat(6);
    } else if (cleanInput.length === 2) {
      cleanInput = cleanInput.repeat(3);
    } else if (cleanInput.length === 3) {
      cleanInput = cleanInput
        .split('')
        .map((char) => char + char)
        .join('');
    } else if (cleanInput.length > 6) {
      cleanInput = cleanInput.substring(0, 6);
    }

    return `#${cleanInput}`;
  };

  const handleTextChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const rawValue = event.target.value;

    setTextValue(rawValue);
    setIsValidColor(true);
  };

  const handleTextKeyDown = (event: React.KeyboardEvent<HTMLInputElement>) => {
    if (event.key === 'Enter') {
      event.preventDefault();

      const formattedValue = formatHexInput(textValue);
      setTextValue(formattedValue);

      const isValid = validateColor(formattedValue);
      setIsValidColor(isValid);

      if (isValid) {
        try {
          const newHsva = hexToHsva(formattedValue);
          const preservedHsva = {
            ...newHsva,
            h: newHsva.s < 1 || newHsva.v < 1 ? hsva.h : newHsva.h,
          };
          setHsva(preservedHsva);
          onChangeHsva?.(preservedHsva);
          if (onChange) {
            onChange(hsvaToHex(preservedHsva));
          }
        } catch {
          // Invalid color, keep current
        }
      }
    }
  };

  const handleTextBlur = () => {
    const formattedValue = formatHexInput(textValue);
    setTextValue(formattedValue);

    const isValid = validateColor(formattedValue);
    setIsValidColor(isValid);

    if (isValid) {
      try {
        const newHsva = hexToHsva(formattedValue);

        const preservedHsva = {
          ...newHsva,
          h: newHsva.s < 1 || newHsva.v < 1 ? hsva.h : newHsva.h,
        };
        setHsva(preservedHsva);
        onChangeHsva?.(preservedHsva);
        if (onChange) {
          onChange(hsvaToHex(preservedHsva));
        }
      } catch (error) {
        // Intentionally ignore parse errors while avoiding an empty catch block
        void error;
      }
    } else if (textValue) {
      setTextValue(hsvaToHex(hsva));
      setIsValidColor(true);
    }
  };

  const handlePickerOpenChange = (isOpen: boolean) => {
    setOpen(isOpen);

    if (!isOpen) {
      setTextValue(hsvaToHex(hsva));
    }
  };

  if (variant === 'default') {
    return (
      <ColorPicker
        value={hsva}
        onChangeHsva={(next) => {
          setHsva(next);
          onChangeHsva?.(next);
          onChange?.(hsvaToHex(next));
        }}
        className={className}
        {...props}
      >
        {children}
      </ColorPicker>
    );
  }

  return (
    <div className={cn(className)} {...props}>
      {showTextInput && (
        <div className={cn('relative', className)}>
          <TextField
            ref={inputRef}
            name={name}
            value={textValue}
            onChange={handleTextChange}
            onKeyDown={handleTextKeyDown}
            onBlur={handleTextBlur}
            placeholder={placeholder}
            disabled={disabled}
            error={!isValidColor && textValue !== ''}
            startAdornment={
              showColorPicker ? (
                <div className="mx-1 flex h-6 w-6 items-center justify-center">
                  <button
                    ref={triggerRef}
                    type="button"
                    className="hover:ring-primary/20 border-primary/10 h-5 w-5 cursor-pointer items-center justify-center rounded-md border transition-all hover:ring-2"
                    style={{ backgroundColor: isValidColor ? currentValue : '#ccc' }}
                    onClick={() => setOpen(!open)}
                    disabled={disabled}
                  />
                </div>
              ) : (
                <div
                  className="h-6 w-6 rounded border"
                  style={{ backgroundColor: isValidColor ? currentValue : '#ccc' }}
                />
              )
            }
          />
        </div>
      )}
      {showColorPicker && (
        <Popover open={open} onOpenChange={handlePickerOpenChange}>
          <PopoverTrigger ref={triggerRef} asChild>
            <div className="sr-only" />
          </PopoverTrigger>
          <PopoverContent
            className="w-full max-w-72 p-0"
            align="start"
            side="bottom"
            sideOffset={0}
          >
            <div className="p-4">
              <ColorPicker
                value={hsva}
                onChangeHsva={(next) => {
                  setHsva(next);
                  onChangeHsva?.(next);
                }}
                onChange={(hex) => {
                  onChange?.(hex);
                }}
              >
                {children || (
                  <div className="space-y-2">
                    <ColorPickerSelection className="h-64 w-full" />
                    <div className="flex items-center justify-center gap-4">
                      <div className="flex flex-1 flex-col gap-2">
                        <ColorPickerHue />
                        {alpha && <ColorPickerAlpha />}
                      </div>

                      <ColorPickerEyeDropper />
                    </div>
                    <div className="flex items-center gap-2">
                      <ColorPickerOutput />
                      <ColorPickerFormat alpha={alpha} />
                    </div>
                  </div>
                )}
              </ColorPicker>
            </div>
          </PopoverContent>
        </Popover>
      )}
    </div>
  );
};

'use client';

import * as DialogPrimitive from '@radix-ui/react-dialog';
import { XIcon } from 'lucide-react';
import * as React from 'react';

import { cn } from '../../lib/theme-utils';

import { Button } from './button';

function Dialog({ ...props }: React.ComponentProps<typeof DialogPrimitive.Root>) {
  return <DialogPrimitive.Root data-slot="dialog" {...props} />;
}

function DialogTrigger({ ...props }: React.ComponentProps<typeof DialogPrimitive.Trigger>) {
  const { children, asChild = false, ...elementProps } = props;

  if (asChild && React.isValidElement(children)) {
    return React.cloneElement(children, {
      ...(typeof children.props === 'object' ? children.props : {}),
      ...elementProps,
    });
  }

  return (
    <DialogPrimitive.Trigger data-slot="dialog-trigger" asChild {...props}>
      <Button {...elementProps} data-slot="dialog-trigger">
        {children}
      </Button>
    </DialogPrimitive.Trigger>
  );
}

function DialogPortal({ ...props }: React.ComponentProps<typeof DialogPrimitive.Portal>) {
  return <DialogPrimitive.Portal data-slot="dialog-portal" {...props} />;
}

function DialogClose({ ...props }: React.ComponentProps<typeof DialogPrimitive.Close>) {
  const { children } = props;

  return (
    <DialogPrimitive.Close data-slot="dialog-close" asChild>
      <Button variant="outline" data-slot="dialog-close">
        {children}
      </Button>
    </DialogPrimitive.Close>
  );
}

function DialogOverlay({
  className,
  ...props
}: React.ComponentProps<typeof DialogPrimitive.Overlay>) {
  return (
    <DialogPrimitive.Overlay
      data-slot="dialog-overlay"
      className={cn(
        'data-[state=open]:animate-in data-[state=closed]:animate-out data-[state=closed]:fade-out-0 data-[state=open]:fade-in-0 fixed inset-0 z-50 bg-black/50 duration-200 ease-in-out',
        className,
      )}
      {...props}
    />
  );
}

function DialogContent({
  className,
  children,
  showCloseButton = true,
  ...props
}: React.ComponentProps<typeof DialogPrimitive.Content> & {
  showCloseButton?: boolean;
}) {
  return (
    <DialogPortal data-slot="dialog-portal">
      <DialogOverlay />
      <DialogPrimitive.Content
        data-slot="dialog-content"
        className={cn(
          'bg-background data-[state=open]:animate-in data-[state=closed]:animate-out data-[state=closed]:fade-out-0 data-[state=open]:fade-in-0 data-[state=closed]:zoom-out-95 data-[state=open]:zoom-in-95 shadow-bevel-2xl rounded-5xl fixed top-[50%] left-[50%] z-[999] grid w-full translate-x-[-50%] translate-y-[-50%] gap-4 p-6 duration-200 ease-in-out sm:max-w-lg',
          className,
        )}
        {...props}
      >
        {children}
        {showCloseButton && (
          <DialogPrimitive.Close
            asChild
            data-slot="dialog-close"
            className="ring-offset-background absolute top-4 right-4"
          >
            <Button variant="ghost" size="icon">
              <XIcon />
              <span className="sr-only">Close</span>
            </Button>
          </DialogPrimitive.Close>
        )}
      </DialogPrimitive.Content>
    </DialogPortal>
  );
}

function DialogHeader({ className, ...props }: React.ComponentProps<'div'>) {
  return (
    <div
      data-slot="dialog-header"
      className={cn('flex flex-col gap-2 text-center sm:text-left', className)}
