import { Switch } from './switch';
import { Tooltip, TooltipTrigger, TooltipContent } from './tooltip';

export interface TooltipProps {
  content: React.ReactNode | string;
}

export interface WithTooltipProps {
  trigger: React.ReactNode | string;
  tooltip: TooltipProps;
}

export interface BaseActionProps extends Omit<CoreActionButton, 'icon' | 'onClick'> {
  icon?: LucideIcon;
  className?: string;
  hidden?: boolean;
  tooltip?: TooltipProps;
}

export interface ButtonActionProps extends BaseActionProps {
  type: 'button';
  onClick: (e: React.MouseEvent<HTMLButtonElement>) => void;
}

export interface SwitchActionProps extends Omit<BaseActionProps, 'type' | 'label'> {
  type: 'switch';
  checked?: boolean;
  onCheckedChange: (checked: boolean) => void;
  'aria-label'?: string;
}

export type ActionProps = ButtonActionProps | SwitchActionProps;

export interface HeaderProps {
  title?: string;
  description?: string;
  backButton?: {
    text?: string;
    icon?: LucideIcon;
    onClick: (e: React.MouseEvent<HTMLButtonElement>) => void;
  };
  actions?: ActionProps[];
  isLoading?: boolean;
  className?: string;
}

const WithTooltip: React.FC<WithTooltipProps> = ({ trigger, tooltip }) => (
  <>
    <Tooltip>
      <TooltipTrigger asChild>
        <div>{trigger}</div>
      </TooltipTrigger>
      <TooltipContent>{tooltip.content}</TooltipContent>
    </Tooltip>
  </>
);

const ButtonAction: React.FC<ButtonActionProps> = ({
  icon: Icon,
  className,
  label,
  onClick,
  disabled,
  variant,
  size,
}) => (
  <Button
    onClick={onClick}
    disabled={disabled}
    variant={variant}
    size={size}
    className={cn('flex items-center gap-2 w-full sm:w-auto sm:min-w-fit', className)}
    aria-label={label}
  >
    {Icon && <Icon className="h-4 w-4 flex-shrink-0" aria-hidden="true" />}
    <span className="truncate">{label}</span>
  </Button>
);

const SwitchAction: React.FC<SwitchActionProps> = ({
  className,
  'aria-label': ariaLabel,
  checked,
  onCheckedChange,
  disabled,
}) => (
  <div className={cn('flex items-center gap-2', className)}>
    <Switch
      checked={checked}
      onCheckedChange={onCheckedChange}
      disabled={disabled}
      aria-label={ariaLabel}
    />
  </div>
);

export const Header = React.forwardRef<
  HTMLDivElement,
  HeaderProps & React.HTMLAttributes<HTMLDivElement>
>(({ title, description, backButton, actions, isLoading, className, ...props }, ref) => {
  const BackIcon = backButton?.icon || ArrowLeft;

  const renderAction = (action: ActionProps, index: number) => {
    const key = `action-${index}`;
    if (isLoading) {
      return <Spinner className="w-4 h-4" />;
    }
    if (action.hidden) {
      return null;
    }
    const actionElement =
      action.type === 'switch' ? (
        <SwitchAction key={key} {...action} />
      ) : (
        <ButtonAction key={key} {...action} />
      );
    if (action.tooltip) {
      return (
        <WithTooltip key={`tooltip-${key}`} trigger={actionElement} tooltip={action.tooltip} />
      );
    }
    return actionElement;
  };

  return (
    <div
      ref={ref}
      className={cn('w-full mb-8', className)}
      role="banner"
      aria-label={title ? `${title} header` : 'Header'}
      {...props}
    >
      {backButton && (
        <Button
          variant="link"
          onClick={backButton.onClick}
          size="default"
          className="flex items-center text-sm mb-3"
          aria-label={backButton.text || 'Go back'}
        >
          <BackIcon className="h-4 w-4" aria-hidden="true" />
          {backButton.text && <span>{backButton.text}</span>}
        </Button>
      )}

      <div className="flex items-start justify-between gap-4">
        <div className="flex flex-col min-w-0 flex-1">
          {title && (
            <h1
              className={cn(
                'text-xl sm:text-2xl text-primary md:text-4xl font-bold leading-tight break-words text-left text-(length:--font-size-page-header) mb-0',
              )}
            >
              {title}
            </h1>
          )}
          {description && (
            <p
              className={cn(
                'text-base text-muted-foreground leading-relaxed break-words text-left text-(length:--font-size-page-description) mt-2',
              )}
            >
              {description}
            </p>
          )}
        </div>

        {actions && actions.length > 0 && (
          <div className="flex-shrink-0 flex items-start gap-2 mt-1">
            {actions.map(renderAction)}
          </div>
        )}
      </div>
    </div>
  );
});

Header.displayName = 'Header';

import { ImageIcon } from 'lucide-react';
import * as React from 'react';

import { TextField } from '../../components/ui/text-field';
import type { TextFieldProps } from '../../components/ui/text-field';
import { cn } from '../../lib/theme-utils';

export interface ImagePreviewFieldProps extends Omit<TextFieldProps, 'onChange'> {
  previewClassName?: string;
  placeholder?: string;
  previewPlaceholder?: string;
  previewIcon?: React.ReactNode;
  imgSizes?: string;
  imgWidth?: number;
  imgHeight?: number;
  onImageLoad?: (imageUrl: string) => void;
  onImageError?: (error: Error) => void;
  onChange?: (e: React.ChangeEvent<HTMLInputElement>) => void;
}

function ImagePreviewField({
  placeholder = 'Enter image URL',
  previewPlaceholder = 'Paste an image URL to see a preview',
  previewIcon = <ImageIcon size={24} />,
  previewClassName,
  imgSizes,
  imgWidth,
  imgHeight,
  onImageLoad,
  onImageError,
  onChange,
  value,
  error,
  ...textFieldProps
}: ImagePreviewFieldProps) {
  const [imageUrl, setImageUrl] = React.useState<string>(value?.toString() || '');
  const [imageError, setImageError] = React.useState<boolean>(false);
  const [isValidUrl, setIsValidUrl] = React.useState<boolean>(true);

  const isValidImageUrl = (url: string): boolean => {
    if (!url.trim()) return true;

    try {
      const urlObj = new URL(url);
      return urlObj.protocol === 'http:' || urlObj.protocol === 'https:';
    } catch {
      return false;
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const newValue = e.target.value;
    const isValid = isValidImageUrl(newValue);

    setIsValidUrl(isValid);
    setImageUrl(newValue);
    setImageError(false);

    onChange?.(e);
  };

  const handleImageLoad = () => {
    setImageError(false);
    onImageLoad?.(imageUrl);
  };

  const handleImageError = () => {
    setImageError(true);
    onImageError?.(new Error('Failed to load image'));
  };

  const hasError = error || (!isValidUrl && imageUrl.trim() !== '') || imageError;
  const showPreview = imageUrl.trim() !== '' && isValidUrl && !imageError;

  return (
    <div className="space-y-2">
      <div
        className={cn(
          'border-border/50 bg-muted/50 flex h-24 w-full items-center justify-center overflow-hidden rounded-lg border',
          showPreview && 'bg-background p-0',
          previewClassName,
        )}
      >
        {showPreview ? (
          <img
            loading="lazy"
            srcSet={imageUrl}
            src={imageUrl}
            sizes={imgSizes}
            decoding="async"
            alt="Preview"
            className="animate-in fade-in-0 max-h-24 max-w-24 object-contain blur-none transition-all duration-200 ease-in-out"
            height={imgHeight}
            width={imgWidth}
            onLoad={handleImageLoad}
            onError={handleImageError}
          />
        ) : (
          <div className="text-muted-foreground flex h-full w-full flex-col items-center justify-center gap-2 text-sm">
            {previewIcon}
            {imageUrl.trim() !== '' && !isValidUrl ? 'Invalid URL' : previewPlaceholder}
          </div>
        )}
      </div>

      <TextField
        {...textFieldProps}
        value={value}
        onChange={handleChange}
        error={hasError}
        placeholder={placeholder}
      />
    </div>
  );
}

export { ImagePreviewField };

