import {
  type Domain,
  getComponentStyles,
  MY_ORGANIZATION_DOMAIN_SCOPES,
} from '@auth0/universal-components-core';
import { Plus } from 'lucide-react';
import * as React from 'react';

import { DomainConfigureProvidersModal } from '../../../components/my-organization/domain-management/domain-configure/domain-configure-providers-modal';
import { DomainCreateModal } from '../../../components/my-organization/domain-management/domain-create/domain-create-modal';
import { DomainDeleteModal } from '../../../components/my-organization/domain-management/domain-delete/domain-delete-modal';
import { DomainTableActionsColumn } from '../../../components/my-organization/domain-management/domain-table/domain-table-actions-column';
import { DomainVerifyModal } from '../../../components/my-organization/domain-management/domain-verify/domain-verify-modal';
import { Badge } from '../../../components/ui/badge';
import { DataTable, type Column } from '../../../components/ui/data-table';
import { Header } from '../../../components/ui/header';
import { withMyOrganizationService } from '../../../hoc/with-services';
import { useDomainTable } from '../../../hooks/my-organization/domain-management/use-domain-table';
import { useDomainTableLogic } from '../../../hooks/my-organization/domain-management/use-domain-table-logic';
import { useTheme } from '../../../hooks/use-theme';
import { useTranslator } from '../../../hooks/use-translator';
import { getStatusBadgeVariant } from '../../../lib/my-organization/domain-management';
import type { DomainTableProps } from '../../../types/my-organization/domain-management/domain-table-types';

/**
 * DomainTable Component
 */
function DomainTableComponent({
  customMessages = {},
  schema,
  styling = {
    variables: { common: {}, light: {}, dark: {} },
    classes: {},
  },
  hideHeader = false,
  readOnly = false,
  createAction,
  verifyAction,
  deleteAction,
  associateToProviderAction,
  deleteFromProviderAction,
  onOpenProvider,
  onCreateProvider,
}: DomainTableProps) {
  const { isDarkMode } = useTheme();
  const { t } = useTranslator('domain_management', customMessages);

  const {
    domains,
    providers,
    isFetching,
    isCreating,
    isVerifying,
    isDeleting,
    isLoadingProviders,
    fetchProviders,
    fetchDomains,
    onCreateDomain,
    onVerifyDomain,
    onDeleteDomain,
    onAssociateToProvider,
    onDeleteFromProvider,
  } = useDomainTable({
    createAction,
    verifyAction,
    deleteAction,
    associateToProviderAction,
    deleteFromProviderAction,
    customMessages,
  });

  const {
    showCreateModal,
    showConfigureModal,
    showVerifyModal,
    showDeleteModal,
    verifyError,
    selectedDomain,
    setShowCreateModal,
    setShowConfigureModal,
    setShowDeleteModal,
    handleCreate,
    handleVerify,
    handleDelete,
    handleToggleSwitch,
    handleCloseVerifyModal,
    handleCreateClick,
    handleConfigureClick,
    handleVerifyClick,
    handleDeleteClick,
  } = useDomainTableLogic({
    t,
    onCreateDomain,
    onVerifyDomain,
    onDeleteDomain,
    onAssociateToProvider,
    onDeleteFromProvider,
    fetchProviders,
    fetchDomains,
  });

  const currentStyles = React.useMemo(
    () => getComponentStyles(styling, isDarkMode),
    [styling, isDarkMode],
  );

  const columns: Column<Domain>[] = React.useMemo(
    () => [
      {
        type: 'text',
        accessorKey: 'domain',
        title: t('domain_table.table.columns.domain'),
        width: '35%',
        render: (domain) => <div className="font-medium">{domain.domain}</div>,
      },
      {
        type: 'text',
        accessorKey: 'status',
        title: t('domain_table.table.columns.status'),
        width: '25%',
        render: (domain) => (
          <Badge variant={getStatusBadgeVariant(domain.status)} size={'sm'}>
            {t(`shared.domain_statuses.${domain.status}`)}
          </Badge>
        ),
      },
      {
        type: 'actions',
        title: '',
        width: '20%',
        render: (domain) => (
          <DomainTableActionsColumn
            domain={domain}
            readOnly={readOnly}
            customMessages={customMessages}
            onView={handleConfigureClick}
            onConfigure={handleConfigureClick}
            onVerify={handleVerifyClick}
            onDelete={handleDeleteClick}
          />
        ),
      },
    ],
    [t, readOnly, customMessages, handleConfigureClick, handleVerifyClick, handleDeleteClick],
  );

  return (
    <div style={currentStyles.variables}>
      {!hideHeader && (
        <div className={currentStyles.classes?.['DomainTable-header']}>
          <Header
            title={t('domain_table.header.title')}
            description={t('domain_table.header.description')}
            actions={[
              {
                type: 'button',
                label: t('domain_table.header.create_button_text'),
                onClick: () => handleCreateClick(),
                icon: Plus,
                disabled: createAction?.disabled || readOnly || isFetching,
              },
            ]}
          />
        </div>
      )}

      <DataTable
        columns={columns}
        data={domains}
        loading={isFetching}
        emptyState={{ title: t('domain_table.table.empty_message') }}
        className={currentStyles.classes?.['DomainTable-table']}
      />

      <DomainCreateModal
        className={currentStyles.classes?.['DomainTable-createModal']}
        isOpen={showCreateModal}
        isLoading={isCreating}
        schema={schema?.create}
        onClose={() => setShowCreateModal(false)}
        onCreate={handleCreate}
        customMessages={customMessages.create}
      />

      <DomainConfigureProvidersModal
        className={currentStyles.classes?.['DomainTable-configureModal']}
        domain={selectedDomain}
        providers={providers}
        isOpen={showConfigureModal}
        isLoading={isLoadingProviders}
        isLoadingSwitch={false}
        onClose={() => setShowConfigureModal(false)}
        onToggleSwitch={handleToggleSwitch}
        onOpenProvider={onOpenProvider}
        onCreateProvider={onCreateProvider}
        customMessages={customMessages.configure}
      />

      <DomainVerifyModal
        className={currentStyles.classes?.['DomainTable-verifyModal']}
        isOpen={showVerifyModal}
        isLoading={isVerifying}
        domain={selectedDomain}
        error={verifyError}
        onClose={handleCloseVerifyModal}
        onVerify={handleVerify}
        onDelete={handleDeleteClick}
        customMessages={customMessages.verify}
      />

      <DomainDeleteModal
        className={currentStyles.classes?.['DomainTable-deleteModal']}
        domain={selectedDomain}
        isOpen={showDeleteModal}
        isLoading={isDeleting}
        onClose={() => setShowDeleteModal(false)}
        onDelete={handleDelete}
        customMessages={customMessages.delete}
      />
    </div>
  );
}

export const DomainTable = withMyOrganizationService(
  DomainTableComponent,
  MY_ORGANIZATION_DOMAIN_SCOPES,
);

import { MoreHorizontal, Trash2, PencilLine, Eye, RefreshCcw } from 'lucide-react';
import * as React from 'react';

import { useTranslator } from '../../../../hooks/use-translator';
import type { DomainTableActionsColumnProps } from '../../../../types/my-organization/domain-management/domain-table-types';
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
  DropdownMenuPortal,
} from '../../../ui/dropdown-menu';

/**
 * DomainTableActionsColumn Component
 * Handles the actions column for Domain table with dropdown menu
 */
export function DomainTableActionsColumn({
  customMessages = {},
  readOnly = false,
  domain,
  onView,
  onConfigure,
  onVerify,
  onDelete,
}: DomainTableActionsColumnProps) {
  const { t } = useTranslator('domain_management.domain_table', customMessages);

  const handleView = React.useCallback(() => {
    onConfigure(domain);
  }, [domain, onView]);

  const handleConfigure = React.useCallback(() => {
    onConfigure(domain);
  }, [domain, onConfigure]);

  const handleVerify = React.useCallback(() => {
    onVerify(domain);
  }, [domain, onVerify]);

  const handleDelete = React.useCallback(() => {
    onDelete(domain);
  }, [domain, onDelete]);

  return (
    <div className="flex items-center justify-end gap-4 min-w-0">
      <DropdownMenu>
        <DropdownMenuTrigger className="h-8 w-8 p-0 rounded-xl bg-white dark:bg-gray-900 border border-gray-200 dark:border-gray-700 shadow-sm transition-all duration-200 hover:bg-gray-50 dark:hover:bg-gray-800 hover:shadow-md focus:outline-none focus:ring-2 focus:ring-blue-500">
          <MoreHorizontal className="h-4 w-4 text-gray-600 dark:text-gray-400" />
        </DropdownMenuTrigger>
        <DropdownMenuPortal>
          <DropdownMenuContent align="end">
            {domain.status === 'verified' && (
              <DropdownMenuItem onClick={handleConfigure} disabled={readOnly}>
                <PencilLine className="mr-2 h-4 w-4" />
                {t('table.actions.configure_button_text')}
              </DropdownMenuItem>
            )}
            {domain.status === 'pending' && (
              <>
                <DropdownMenuItem onClick={handleView} disabled={readOnly}>
                  <Eye className="mr-2 h-4 w-4" />
                  {t('table.actions.view_button_text')}
                </DropdownMenuItem>
                <DropdownMenuItem onClick={handleVerify} disabled={readOnly}>
                  <RefreshCcw className="mr-2 h-4 w-4" />
                  {t('table.actions.verify_button_text')}
                </DropdownMenuItem>
              </>
            )}
            <DropdownMenuItem
              onClick={handleDelete}
              className="text-destructive-foreground focus:text-destructive-foreground"
              disabled={readOnly}
            >
              <Trash2 className="mr-2 h-4 w-4 text-destructive-foreground focus:text-destructive-foreground" />
              {t('table.actions.delete_button_text')}
            </DropdownMenuItem>
          </DropdownMenuContent>
        </DropdownMenuPortal>
      </DropdownMenu>
    </div>
  );
}

import {
  createDomainCreateSchema,
  type InternalDomainCreateFormValues,
} from '@auth0/universal-components-core';
import { zodResolver } from '@hookform/resolvers/zod';
import * as React from 'react';
import { useForm } from 'react-hook-form';

import { useTranslator } from '../../../../hooks/use-translator';
import { cn } from '../../../../lib/theme-utils';
import type { DomainCreateModalProps } from '../../../../types/my-organization/domain-management/domain-create-types';
import { Form, FormControl, FormField, FormItem, FormMessage } from '../../../ui/form';
import { Label } from '../../../ui/label';
import { Modal } from '../../../ui/modal';
import { TextField } from '../../../ui/text-field';

export function DomainCreateModal({
  translatorKey = 'domain_management.domain_create.modal',
  className,
  customMessages,
  isOpen,
  isLoading,
  schema,
  onClose,
  onCreate,
}: DomainCreateModalProps) {
  const { t } = useTranslator(translatorKey, customMessages);

  const domainCreateSchema = React.useMemo(
    () => createDomainCreateSchema(schema, t('field.error')),
    [schema, t],
  );

  const form = useForm<InternalDomainCreateFormValues>({
    resolver: zodResolver(domainCreateSchema),
    defaultValues: {
      domain_url: '',
    },
    mode: 'onChange',
  });

  const handleCreate = React.useCallback(
    async (values: InternalDomainCreateFormValues) => {
      if (!values.domain_url) return;
      await onCreate(values.domain_url);
      form.reset();
    },
    [form, onCreate],
  );

  const handleClose = React.useCallback(() => {
    form.reset();
    onClose();
  }, [form, onClose]);

  const onSubmit = React.useCallback(
    (e: React.FormEvent<HTMLFormElement>) => {
      e.preventDefault();
      form.handleSubmit(handleCreate)();
    },
    [form, handleCreate],
  );

  return (
    <Modal
      open={isOpen}
      onOpenChange={(open) => !open && handleClose()}
      className={cn('p-10', className)}
      title={t('title')}
      content={
        <div>
          <Form {...form}>
            <form id="domain-create-form" onSubmit={onSubmit}>
              <FormField
                control={form.control}
                name="domain_url"
                render={({ field }) => (
                  <FormItem>
                    <Label
                      htmlFor="domain-url"
                      className="text-sm text-(length:--font-size-label) font-medium"
                    >
                      {t('field.label')}
                    </Label>
                    <FormControl>
                      <TextField
                        id="domain-url"
                        type="text"
                        placeholder={t('field.placeholder')}
                        className="mt-2"
                        {...field}
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </form>
          </Form>
        </div>
      }
      modalActions={{
        isLoading,
        nextAction: {
          type: 'button',
          label: t('actions.create_button_text'),
          variant: 'primary',
          disabled: isLoading,
          onClick: (e) => {
            e.preventDefault();
            form.handleSubmit(handleCreate)();
          },
        },
        previousAction: {
          label: t('actions.cancel_button_text'),
          onClick: handleClose,
        },
      }}
    />
  );
}

import { MessageSquare } from 'lucide-react';
import React from 'react';

import { useTranslator } from '../../../../hooks/use-translator';
import { cn } from '../../../../lib/theme-utils';
import type { DomainVerifyModalProps } from '../../../../types/my-organization/domain-management/domain-verify-types';
import { Alert, AlertDescription } from '../../../ui/alert';
import { Badge } from '../../../ui/badge';
import { Button } from '../../../ui/button';
import { CopyableTextField } from '../../../ui/copyable-text-field';
import { Label } from '../../../ui/label';
import { Modal } from '../../../ui/modal';
import { Spinner } from '../../../ui/spinner';

export function DomainVerifyModal({
  translatorKey = 'domain_management.domain_verify.modal',
  className,
  customMessages,
  isOpen,
  isLoading,
  domain,
  error,
  onClose,
  onVerify,
  onDelete,
}: DomainVerifyModalProps) {
  const { t } = useTranslator(translatorKey, customMessages);

  const handleVerify = React.useCallback(() => {
    if (domain) {
      onVerify(domain);
    }
  }, [onVerify, domain]);

  const handleDelete = React.useCallback(() => {
    if (domain) {
      onDelete(domain);
    }
  }, [onDelete, domain]);

  return (
    <Modal
      open={isOpen}
      onOpenChange={(open) => !open && onClose()}
      className="p-10"
      title={t('title', { domainName: domain?.domain })}
      content={
        <div className={cn('space-y-4', className)}>
          {error && (
            <Alert variant="destructive">
              <MessageSquare className="h-4 w-4" />
              <AlertDescription className="text-destructive-foreground">{error}</AlertDescription>
            </Alert>
          )}

          <div className="space-y-2">
            <Label htmlFor="txt-record-name" className="text-sm font-medium">
              {t('txt_record_name.label')}
            </Label>
            <CopyableTextField
              id="txt-record-name"
              value={domain?.verification_host || ''}
              readOnly
              className="text-sm"
            />
          </div>

          <div className="space-y-2">
            <Label htmlFor="txt-record-content" className="text-sm font-medium">
              {t('txt_record_content.label')}
            </Label>
            <CopyableTextField
              id="txt-record-content"
              value={domain?.verification_txt || ''}
              readOnly
              className="text-sm"
            />
          </div>

          <div className="space-y-2 pt-4 border-t border-gray-200">
            <div className="flex items-center gap-2">
              <span className="text-sm text-foreground font-medium">
                {t('verification_status.label')}
              </span>
              <Badge variant="warning">{t('verification_status.pending')}</Badge>
            </div>
            <p className={cn('text-sm text-muted-foreground text-(length:--font-size-paragraph')}>
              {t('verification_status.description')}
            </p>
          </div>

          <div className="flex gap-2 pt-2">
            <Button variant="outline" onClick={handleVerify} disabled={isLoading}>
              {isLoading ? <Spinner size="sm" /> : t('actions.verify_button_text')}
            </Button>
            <Button variant="destructive" onClick={handleDelete} disabled={isLoading}>
              {t('actions.delete_button_text')}
            </Button>
          </div>
        </div>
      }
      modalActions={{
        showNext: false,
        previousAction: {
          label: t('actions.done_button_text'),
          onClick: onClose,
          variant: 'primary',
        },
      }}
    />
  );
}

import type { IdentityProviderAssociatedWithDomain } from '@auth0/universal-components-core';
import React from 'react';

import { useTranslator } from '../../../../hooks/use-translator';
import { cn } from '../../../../lib/theme-utils';
import type { DomainConfigureProvidersModalProps } from '../../../../types/my-organization/domain-management/domain-configure-types';
import { Button } from '../../../ui/button';
import { type Column, DataTable } from '../../../ui/data-table';
import { Modal } from '../../../ui/modal';
import { Switch } from '../../../ui/switch';

export function DomainConfigureProvidersModal({
  className,
  customMessages,
  domain,
  providers,
  isOpen,
  isLoading,
  isLoadingSwitch,
  onClose,
  onToggleSwitch,
  onOpenProvider,
  onCreateProvider,
}: DomainConfigureProvidersModalProps) {
  const { t } = useTranslator('domain_management.domain_configure_providers.modal', customMessages);

  const handleToggleSwitch = React.useCallback(
    (provider: IdentityProviderAssociatedWithDomain, newCheckedValue: boolean) => {
      onToggleSwitch(domain!, provider, newCheckedValue); // Switch component is not rendered if domain is null
    },
    [domain, onToggleSwitch],
  );

  const columns: Column<IdentityProviderAssociatedWithDomain>[] = React.useMemo(
    () => [
      {
        type: 'text',
        accessorKey: 'display_name',
        title: t('table.columns.name'),
        width: '25%',
        render: (provider) => <div className="font-medium">{provider.display_name}</div>,
      },
      {
        type: 'text',
        accessorKey: 'strategy',
        title: t('table.columns.provider'),
        width: '40%',
        render: (provider) => <div className="text-muted-foreground">{provider.strategy}</div>,
      },
      {
        type: 'actions',
        title: '',
        width: '30%',
        render: (provider) => (
          <div className="flex items-center justify-end gap-4 min-w-0">
            {onOpenProvider && (
              <Button
                type="button"
                variant={'outline'}
                size={'sm'}
                onClick={() => onOpenProvider(provider)}
              >
                {t('table.actions.view_provider_button_text')}
              </Button>
            )}
            <Switch
              checked={provider.is_associated ?? false}
              onCheckedChange={(checked) => handleToggleSwitch(provider, checked)}
              disabled={isLoadingSwitch}
            />
          </div>
        ),
      },
    ],
    [t, onOpenProvider, isLoadingSwitch, handleToggleSwitch],
  );

  return (
    <Modal
      open={isOpen}
      onOpenChange={(open) => !open && onClose()}
      className="p-10"
      title={t('title', { domain: domain?.domain ?? '' })}
      content={
        domain && (
          <div className={cn('space-y-6', className)}>
            <p className="text-sm text-muted-foreground text-(length:--font-size-paragraph)">
              {t('description', { domain: domain?.domain ?? '' })}
            </p>
            <DataTable
              columns={columns}
              data={providers}
              loading={isLoading}
              emptyState={{
                title: t('table.empty_message'),
                action: onCreateProvider
                  ? {
                      label: t('table.actions.add_provider_button_text'),
                      variant: 'outline',
                      onClick: onCreateProvider,
                    }
                  : undefined,
              }}
            />
          </div>
        )
      }
      modalActions={{
        showNext: false,
        previousAction: {
          label: t('actions.close_button_text'),
          onClick: onClose,
        },
      }}
    />
  );
}

import type { Domain } from '@auth0/universal-components-core';
import React from 'react';

import { useTranslator } from '../../../../hooks/use-translator';
import { cn } from '../../../../lib/theme-utils';
import type { DomainDeleteModalProps } from '../../../../types/my-organization/domain-management/domain-delete-types';
import { Modal } from '../../../ui/modal';

const getDescriptionKey = (domain: Domain | null) => {
  return domain?.status === 'pending' ? 'description.pending' : 'description.verified';
};

export function DomainDeleteModal({
  translatorKey = 'domain_management.domain_delete.modal',
  className,
  customMessages,
  domain,
  isOpen,
  isLoading,
  onClose,
  onDelete,
}: DomainDeleteModalProps) {
  const { t } = useTranslator(translatorKey, customMessages);

  const handleDelete = React.useCallback(() => {
    if (domain) {
      onDelete(domain);
    }
  }, [onDelete, domain]);

  return (
    <Modal
      open={isOpen}
      onOpenChange={(open) => !open && onClose()}
      className={cn('p-10', className)}
      title={t('title')}
      content={
        <div className={'space-y-6'}>
          <p className="text-sm text-muted-foreground text-(length:--font-size-paragraph)">
            {t(getDescriptionKey(domain), { domainName: domain?.domain })}
          </p>
        </div>
      }
      modalActions={{
        isLoading,
        nextAction: {
          type: 'button',
          label: t('actions.delete_button_text'),
          variant: 'destructive',
          disabled: isLoading,
          onClick: handleDelete,
        },
        previousAction: {
          label: t('actions.cancel_button_text'),
          onClick: onClose,
        },
      }}
    />
  );
}

import { cva, type VariantProps } from 'class-variance-authority';
import * as React from 'react';

import { cn } from '../../lib/theme-utils';

const alertVariants = cva(
  'shadow-input-hover bg-input relative grid w-full grid-cols-[0_1fr] items-start gap-y-0.5 overflow-clip rounded-3xl p-3 text-sm has-[>svg]:grid-cols-[calc(var(--spacing)*4)_1fr] has-[>svg]:gap-x-3 [&>svg]:size-4 [&>svg]:translate-y-0.5 [&>svg]:text-current',
  {
    variants: {
      variant: {
        default: 'text-foreground border-b-2 border-transparent',
        info: 'text-info-foreground [&>svg]:text-info-foreground',
        success: 'text-success-foreground [&>svg]:text-success-foreground',
        warning: 'text-warning-foreground [&>svg]:text-warning-foreground',
        destructive: 'text-destructive-foreground [&>svg]:text-destructive-foreground',
      },
    },
    defaultVariants: {
      variant: 'default',
    },
  },
);

function Alert({
  className,
  variant,
  ...props
}: React.ComponentProps<'div'> & VariantProps<typeof alertVariants>) {
  return (
    <div
      data-slot="alert"
      role="alert"
      className={cn(alertVariants({ variant }), className)}
      {...props}
    />
  );
}

function AlertTitle({ className, ...props }: React.ComponentProps<'div'>) {
  return (
    <div
      data-slot="alert-title"
      className={cn(
        'col-start-2 line-clamp-1 flex h-auto min-h-4 leading-4.5 font-medium tracking-tight',
        className,
      )}
      {...props}
    />
  );
}
function AlertDescription({ className, ...props }: React.ComponentProps<'div'>) {
  return (
    <div
      data-slot="alert-description"
      className={cn(
        'text-muted-foreground col-start-2 grid justify-items-start gap-1 text-sm [&_p]:leading-relaxed',
        className,
      )}
      {...props}
    />
  );
}

function AlertAction({ className, ...props }: React.ComponentProps<'div'>) {
  return (
    <div
      className={cn('absolute top-2 right-2 flex items-center space-x-4', className)}
      {...props}
    />
  );
}

export { Alert, AlertTitle, AlertDescription, AlertAction };

import { Slot } from '@radix-ui/react-slot';
import { cva, type VariantProps } from 'class-variance-authority';
import * as React from 'react';

import { cn } from '../../lib/theme-utils';

const badgeVariants = cva(
  'theme-default:shadow-xs box-border inline-flex items-center overflow-clip rounded-2xl border border-transparent font-medium',
  {
    variants: {
      variant: {
        default: 'bg-primary text-primary-foreground theme-default:border-primary',
        secondary: 'bg-muted text-muted-foreground theme-default:border-muted-foreground/25',
        outline: 'border-border',
        info: 'bg-info text-info-foreground theme-default:border-info-foreground/25',
        success: 'bg-success theme-default:border-success-foreground/25 text-success-foreground',
        warning: 'bg-warning theme-default:border-warning-foreground/25 text-warning-foreground',
        destructive:
          'bg-destructive theme-default:border-destructive-foreground/25 text-destructive-foreground',
      },
      size: {
        sm: 'px-1.5 py-0.5 text-xs',
        md: 'px-2 py-1 text-sm',
        lg: 'px-2.5 py-1.5 text-sm',
