import {
  type Domain,
  type IdentityProvider,
  type CreateOrganizationDomainRequestContent,
  type IdentityProviderAssociatedWithDomain,
  BusinessError,
} from '@auth0/universal-components-core';
import { useCallback, useState } from 'react';

import type {
  UseDomainTableOptions,
  UseDomainTableResult,
} from '../../../types/my-organization/domain-management/domain-table-types';
import { useCoreClient } from '../../use-core-client';
import { useTranslator } from '../../use-translator';

/**
 * Custom hook for managing domain table data and actions.
 */
export function useDomainTable({
  createAction,
  deleteAction,
  verifyAction,
  associateToProviderAction,
  deleteFromProviderAction,
  customMessages,
}: UseDomainTableOptions): UseDomainTableResult {
  const { t } = useTranslator('domain_management.domain_table.notifications', customMessages);
  const { coreClient } = useCoreClient();

  const [domains, setDomains] = useState<Domain[]>([]);
  const [providers, setProviders] = useState<IdentityProviderAssociatedWithDomain[]>([]);

  const [isCreating, setIsCreating] = useState(false);
  const [isFetching, setIsFetching] = useState(false);
  const [isLoadingProviders, setIsLoadingProviders] = useState(false);
  const [isDeleting, setIsDeleting] = useState(false);
  const [isVerifying, setIsVerifying] = useState(false);

  const fetchProviders = useCallback(
    async (domain: Domain): Promise<void> => {
      try {
        setIsLoadingProviders(true);
        // Get all providers
        const responseAllProviders = await coreClient!
          .getMyOrganizationApiClient()
          .organization.identityProviders.list();
        const allProviders = responseAllProviders?.identity_providers ?? [];

        // Get associated providers
        const responseAssociatedProviders = await coreClient!
          .getMyOrganizationApiClient()
          .organization.domains.identityProviders.get(domain.id);
        const associatedProviders = responseAssociatedProviders?.identity_providers ?? [];

        // Match associated providers
        const providersWithAssociation: IdentityProviderAssociatedWithDomain[] = allProviders.map(
          (provider) => {
            const is_associated = associatedProviders.some(
              (assocProvider) => assocProvider.id === provider.id,
            );
            return {
              ...provider,
              is_associated,
            };
          },
        );

        setProviders(providersWithAssociation);
      } finally {
        setIsLoadingProviders(false);
      }
    },
    [coreClient, t],
  );

  const fetchDomains = useCallback(async (): Promise<void> => {
    try {
      setIsFetching(true);
      const response = await coreClient!.getMyOrganizationApiClient().organization.domains.list();
      const domains = response?.organization_domains ?? [];
      setDomains(domains);
    } finally {
      setIsFetching(false);
    }
  }, [coreClient, t]);

  const onCreateDomain = useCallback(
    async (data: CreateOrganizationDomainRequestContent): Promise<Domain | null> => {
      setIsCreating(true);

      if (createAction?.onBefore) {
        const canProceed = createAction.onBefore(data as Domain);
        if (!canProceed) {
          setIsCreating(false);
          throw new BusinessError({ message: t('domain_create.on_before') });
        }
      }

      try {
        const result: Domain = await coreClient!
          .getMyOrganizationApiClient()
          .organization.domains.create(data);

        createAction?.onAfter?.(result);
        await fetchDomains();
        return result;
      } finally {
        setIsCreating(false);
      }
    },
    [coreClient, createAction, t],
  );

  const onVerifyDomain = useCallback(
    async (selectedDomain: Domain): Promise<boolean> => {
      setIsVerifying(true);

      if (verifyAction?.onBefore) {
        const canProceed = verifyAction.onBefore(selectedDomain);
        if (!canProceed) {
          setIsVerifying(false);
          throw new BusinessError({ message: t('domain_verify.on_before') });
        }
      }

      try {
        const response = await coreClient!
          .getMyOrganizationApiClient()
          .organization.domains.verify.create(selectedDomain.id);

        if (verifyAction?.onAfter) {
          await verifyAction.onAfter(selectedDomain);
        }

        await fetchDomains();

        return response.status === 'verified';
      } finally {
        setIsVerifying(false);
      }
    },
    [verifyAction, t, coreClient, fetchDomains],
  );

  const onDeleteDomain = useCallback(
    async (selectedDomain: Domain) => {
      setIsDeleting(true);

      if (deleteAction?.onBefore) {
        const canProceed = deleteAction.onBefore(selectedDomain);
        if (!canProceed) {
          setIsDeleting(false);
          throw new BusinessError({ message: t('domain_delete.on_before') });
        }
      }

      try {
        await coreClient!
          .getMyOrganizationApiClient()
          .organization.domains.delete(selectedDomain.id);

        if (deleteAction?.onAfter) {
          await deleteAction.onAfter(selectedDomain);
        }

        await fetchDomains();
      } finally {
        setIsDeleting(false);
      }
    },
    [deleteAction, fetchDomains, t, coreClient],
  );

  const onAssociateToProvider = useCallback(
    async (selectedDomain: Domain, provider: IdentityProvider) => {
      if (associateToProviderAction?.onBefore) {
        const canProceed = associateToProviderAction.onBefore(selectedDomain, provider);
        if (!canProceed) {
          throw new BusinessError({ message: t('domain_associate_provider.on_before') });
        }
      }

      await coreClient!
        .getMyOrganizationApiClient()
        .organization.identityProviders.domains.create(provider.id!, {
          domain: selectedDomain.domain,
        });

      if (associateToProviderAction?.onAfter) {
        await associateToProviderAction.onAfter(selectedDomain, provider);
      }

      await fetchProviders(selectedDomain);
    },
    [associateToProviderAction, t, coreClient, fetchProviders],
  );

  const onDeleteFromProvider = useCallback(
    async (selectedDomain: Domain, provider: IdentityProvider) => {
      if (deleteFromProviderAction?.onBefore) {
        const canProceed = deleteFromProviderAction.onBefore(selectedDomain, provider);
        if (!canProceed) {
          throw new BusinessError({ message: t('domain_delete_provider.on_before') });
        }
      }

      await coreClient!
        .getMyOrganizationApiClient()
        .organization.identityProviders.domains.delete(provider.id!, selectedDomain.domain);

      if (deleteFromProviderAction?.onAfter) {
        await deleteFromProviderAction.onAfter(selectedDomain, provider);
      }

      await fetchProviders(selectedDomain);
    },
    [deleteFromProviderAction, t, coreClient, fetchProviders],
  );

  return {
    domains,
    providers,
    isFetching,
    isCreating,
    isDeleting,
    isVerifying,
    isLoadingProviders,
    fetchProviders,
    fetchDomains,
    onCreateDomain,
    onVerifyDomain,
    onDeleteDomain,
    onAssociateToProvider,
    onDeleteFromProvider,
  };
}

import { type Domain, type IdentityProvider } from '@auth0/universal-components-core';
import { useCallback, useEffect, useState } from 'react';

import { showToast } from '../../../components/ui/toast';
import type {
  UseDomainTableLogicOptions,
  UseDomainTableLogicResult,
} from '../../../types/my-organization/domain-management/domain-table-types';
import { useErrorHandler } from '../../use-error-handler';

export function useDomainTableLogic({
  t,
  onCreateDomain,
  onVerifyDomain,
  onDeleteDomain,
  onAssociateToProvider,
  onDeleteFromProvider,
  fetchProviders,
  fetchDomains,
}: UseDomainTableLogicOptions): UseDomainTableLogicResult {
  const { handleError } = useErrorHandler();
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [showConfigureModal, setShowConfigureModal] = useState(false);
  const [showVerifyModal, setShowVerifyModal] = useState(false);
  const [verifyError, setVerifyError] = useState<string | undefined>(undefined);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [selectedDomain, setSelectedDomain] = useState<Domain | null>(null);

  const handleCreate = useCallback(
    async (domainUrl: string) => {
      try {
        const newDomain = await onCreateDomain({ domain: domainUrl });
        showToast({
          type: 'success',
          message: t('domain_table.notifications.domain_create.success', {
            domainName: newDomain?.domain,
          }),
        });
        setSelectedDomain(newDomain);
        setShowCreateModal(false);
        setShowVerifyModal(true);
      } catch (error) {
        handleError(error, {
          fallbackMessage: t('domain_table.notifications.domain_create.error'),
        });
      }
    },
    [onCreateDomain, t, handleError],
  );

  const handleVerify = useCallback(
    async (domain: Domain) => {
      try {
        const isVerified = await onVerifyDomain(domain);
        if (isVerified) {
          setShowVerifyModal(false);
          showToast({
            type: 'success',
            message: t('domain_table.notifications.domain_verify.success', {
              domainName: domain.domain,
            }),
          });
        } else {
          setVerifyError(
            t('domain_verify.modal.errors.verification_failed', { domainName: domain.domain }),
          );
        }
      } catch (error) {
        handleError(error, {
          fallbackMessage: t('domain_table.notifications.domain_verify.error'),
        });
      }
    },
    [onVerifyDomain, t, handleError],
  );

  const handleDelete = useCallback(
    async (domain: Domain) => {
      try {
        await onDeleteDomain(domain);
        showToast({
          type: 'success',
          message: t('domain_table.notifications.domain_delete.success', {
            domainName: domain.domain,
          }),
        });
        setShowDeleteModal(false);
        setShowVerifyModal(false);
      } catch (error) {
        handleError(error, {
          fallbackMessage: t('domain_table.notifications.domain_delete.error'),
        });
      }
    },
    [onDeleteDomain, t, handleError],
  );

  const handleToggleSwitch = useCallback(
    async (domain: Domain, provider: IdentityProvider, newCheckedValue: boolean) => {
      if (newCheckedValue) {
        try {
          await onAssociateToProvider(domain, provider);
          showToast({
            type: 'success',
            message: t('domain_table.notifications.domain_associate_provider.success', {
              domain: domain.domain,
              idp: provider.name,
            }),
          });
        } catch (error) {
          handleError(error, {
            fallbackMessage: t('domain_table.notifications.domain_associate_provider.error'),
          });
        }
      } else {
        try {
          await onDeleteFromProvider(domain, provider);
          showToast({
            type: 'success',
            message: t('domain_table.notifications.domain_delete_provider.success', {
              domain: domain.domain,
              idp: provider.name,
            }),
          });
        } catch (error) {
          handleError(error, {
            fallbackMessage: t('domain_table.notifications.domain_delete_provider.error'),
          });
        }
      }
    },
    [onAssociateToProvider, onDeleteFromProvider, t, handleError],
  );

  const handleCloseVerifyModal = useCallback(() => {
    setShowVerifyModal(false);
    setVerifyError(undefined);
  }, []);

  const handleCreateClick = useCallback(() => {
    setShowCreateModal(true);
  }, []);

  const handleConfigureClick = useCallback(
    async (domain: Domain) => {
      setSelectedDomain(domain);
      if (domain.status !== 'verified') {
        setShowVerifyModal(true);
      } else {
        try {
          await fetchProviders(domain);
          setShowConfigureModal(true);
        } catch (error) {
          handleError(error, {
            fallbackMessage: t('domain_table.notifications.fetch_providers_error'),
          });
        }
      }
    },
    [fetchProviders, t, handleError],
  );

  const handleVerifyClick = useCallback(
    async (domain: Domain) => {
      setSelectedDomain(domain);
      try {
        const isVerified = await onVerifyDomain(domain);
        if (isVerified) {
          setShowConfigureModal(true);
          showToast({
            type: 'success',
            message: t('domain_table.notifications.domain_verify.success', {
              domainName: domain.domain,
            }),
          });
        } else {
          showToast({
            type: 'error',
            message: t('domain_table.notifications.domain_verify.verification_failed', {
              domainName: domain.domain,
            }),
          });
        }
      } catch (error) {
        handleError(error, {
          fallbackMessage: t('domain_table.notifications.domain_verify.error'),
        });
      }
    },
    [onVerifyDomain, t, handleError],
  );

  const handleDeleteClick = useCallback((domain: Domain) => {
    setSelectedDomain(domain);
    setShowVerifyModal(false);
    setShowDeleteModal(true);
  }, []);

  // Initialization
  useEffect(() => {
    try {
      fetchDomains();
    } catch (error) {
      handleError(error, {
        fallbackMessage: t('domain_table.notifications.fetch_domains_error'),
      });
    }
  }, []);

  return {
    // Modal state
    showCreateModal,
    showConfigureModal,
    showVerifyModal,
    showDeleteModal,
    verifyError,
    selectedDomain,

    // State setters
    setShowCreateModal,
    setShowConfigureModal,
    setShowVerifyModal,
    setShowDeleteModal,

    // Handlers
    handleCreate,
    handleVerify,
    handleDelete,
    handleToggleSwitch,
    handleCloseVerifyModal,
    handleCreateClick,
    handleConfigureClick,
    handleVerifyClick,
    handleDeleteClick,
  };
}

'use client';

import * as React from 'react';

import { Toaster } from '../components/ui/sonner';
import { Spinner } from '../components/ui/spinner';
import { CoreClientContext } from '../hooks/use-core-client';
import { useCoreClientInitialization } from '../hooks/use-core-client-initialization';
import type { Auth0ComponentProviderProps } from '../types/auth-types';

import { ScopeManagerProvider } from './scope-manager-provider';
import { ThemeProvider } from './theme-provider';

/**
 * Auth0 Proxy Provider for Next.js and server-side authentication
 *
 * Use this when you have a backend proxy that handles Auth0 authentication.
 *
 * @example
 * ```tsx
 * <Auth0ProxyProvider
 *   authDetails={{ authProxyUrl: '/api/auth' }}
 *   themeSettings={{ mode: 'dark', theme: 'rounded' }}
 * >
 *   <YourApp />
 * </Auth0ProxyProvider>
 * ```
 */
export const Auth0ComponentProvider = ({
  i18n,
  authDetails,
  themeSettings = {
    theme: 'default',
    mode: 'light',
    variables: {
      common: {},
      light: {},
      dark: {},
    },
  },
  loader,
  children,
}: Auth0ComponentProviderProps & { children: React.ReactNode }) => {
  const memoizedAuthDetails = React.useMemo(
    () => ({
      ...authDetails,
      contextInterface: undefined,
    }),
    [authDetails],
  );

  const coreClient = useCoreClientInitialization({
    authDetails: memoizedAuthDetails,
    i18nOptions: i18n,
  });

  const coreClientValue = React.useMemo(
    () => ({
      coreClient,
    }),
    [coreClient],
  );

  return (
    <ThemeProvider
      themeSettings={{
        mode: themeSettings.mode,
        variables: themeSettings.variables,
        loader,
        theme: themeSettings.theme,
      }}
    >
      <Toaster position="top-right" />
      <React.Suspense
        fallback={
          loader || (
            <div className="flex items-center justify-center min-h-[200px]">
              <Spinner />
            </div>
          )
        }
      >
        <CoreClientContext.Provider value={coreClientValue}>
          <ScopeManagerProvider>{children}</ScopeManagerProvider>
        </CoreClientContext.Provider>
      </React.Suspense>
    </ThemeProvider>
  );
};

export default Auth0ComponentProvider;

'use client';

import { useAuth0 } from '@auth0/auth0-react';
import type { BasicAuth0ContextInterface } from '@auth0/universal-components-core';
import * as React from 'react';

import { Toaster } from '../components/ui/sonner';
import { Spinner } from '../components/ui/spinner';
import { CoreClientContext } from '../hooks/use-core-client';
import { useCoreClientInitialization } from '../hooks/use-core-client-initialization';
import type { Auth0ComponentProviderProps } from '../types/auth-types';

import { ScopeManagerProvider } from './scope-manager-provider';
import { ThemeProvider } from './theme-provider';

export const Auth0ComponentProvider = ({
  i18n,
  authDetails,
  themeSettings = {
    theme: 'default',
    mode: 'light',
    variables: {
      common: {},
      light: {},
      dark: {},
    },
  },
  loader,
  children,
}: Auth0ComponentProviderProps & { children: React.ReactNode }) => {
  const auth0ReactContext = useAuth0();

  const auth0ContextInterface = React.useMemo(() => {
    if (auth0ReactContext && 'isAuthenticated' in auth0ReactContext) {
      return auth0ReactContext as BasicAuth0ContextInterface;
    }

    if (authDetails?.contextInterface) {
      return authDetails.contextInterface;
    }

    throw new Error(
      'Auth0ContextInterface is not available. Make sure you wrap your app with Auth0Provider from @auth0/auth0-react, or pass a contextInterface via authDetails.',
    );
  }, [auth0ReactContext, authDetails?.contextInterface]);

  const memoizedAuthDetails = React.useMemo(
    () => ({
      ...authDetails,
      contextInterface: auth0ContextInterface,
    }),
    [authDetails, auth0ContextInterface],
  );

  const coreClient = useCoreClientInitialization({
    authDetails: memoizedAuthDetails,
    i18nOptions: i18n,
  });

  const coreClientValue = React.useMemo(
    () => ({
      coreClient,
    }),
    [coreClient],
  );

  return (
    <ThemeProvider
      themeSettings={{
        mode: themeSettings.mode,
        variables: themeSettings.variables,
        loader,
        theme: themeSettings.theme,
      }}
    >
      <Toaster position="top-right" />
      <React.Suspense
        fallback={
          loader || (
            <div className="flex items-center justify-center min-h-[200px]">
              <Spinner />
            </div>
          )
        }
      >
        <CoreClientContext.Provider value={coreClientValue}>
          <ScopeManagerProvider>{children}</ScopeManagerProvider>
        </CoreClientContext.Provider>
      </React.Suspense>
    </ThemeProvider>
  );
};

