    --shadow-button-outlined-hover: none;

    --shadow-input-resting: none;
    --shadow-input-hover: none;
    --shadow-input-destructive-resting: none;
    --shadow-input-destructive-hover: none;
    --shadow-input-destructive-focus: none;

    --shadow-checkbox-resting: none;
    --shadow-checkbox-hover: none;

    --shadow-switch-resting: none;
    --shadow-switch-hover: none;
    --shadow-switch-focus: none;
    --shadow-switch-thumb: none;

    --radius-xs: 4px;
    --radius-sm: 6px;
    --radius-md: 8px;
    --radius-lg: 10px;
    --radius-xl: 12px;
    --radius-2xl: 14px;
    --radius-3xl: 16px;
    --radius-4xl: 20px;
    --radius-5xl: 24px;
    --radius-6xl: 32px;
    --radius-7xl: 40px;
    --radius-8xl: 48px;
    --radius-9xl: 56px;
