import {
  getComponentStyles,
  type IdpStrategy,
  type ProviderDetailsFormValues,
  type ProviderConfigureFormValues,
  MY_ORGANIZATION_SSO_PROVIDER_CREATE_SCOPES,
} from '@auth0/universal-components-core';
import React, { useState, useRef, useCallback, useMemo } from 'react';

import ProviderConfigure, {
  type ProviderConfigureHandle,
} from '../../../components/my-organization/idp-management/sso-provider-create/provider-configure/provider-configure';
import {
  ProviderDetails,
  type ProviderDetailsFormHandle,
} from '../../../components/my-organization/idp-management/sso-provider-create/provider-details';
import { ProviderSelect } from '../../../components/my-organization/idp-management/sso-provider-create/provider-select';
import { Header } from '../../../components/ui/header';
import { Wizard } from '../../../components/ui/wizard';
import type { StepProps } from '../../../components/ui/wizard';
import { withMyOrganizationService } from '../../../hoc/with-services';
import { useConfig } from '../../../hooks/my-organization/config/use-config';
import { useIdpConfig } from '../../../hooks/my-organization/config/use-idp-config';
import { useSsoProviderCreate } from '../../../hooks/my-organization/idp-management/use-sso-provider-create';
import { useTheme } from '../../../hooks/use-theme';
import { useTranslator } from '../../../hooks/use-translator';
import type { SsoProviderCreateProps } from '../../../types/my-organization/idp-management/sso-provider/sso-provider-create-types';

type FormState = {
  strategy?: IdpStrategy;
  details?: ProviderDetailsFormValues | null;
  configure?: ProviderConfigureFormValues | null;
};

export function SsoProviderCreateComponent({
  createAction,
  backButton,
  onNext,
  onPrevious,
  customMessages = {},
  styling = {
    variables: { common: {}, light: {}, dark: {} },
    classes: {},
  },
}: SsoProviderCreateProps) {
  const { t } = useTranslator('idp_management.create_sso_provider', customMessages);
  const { isDarkMode } = useTheme();

  const [formData, setFormData] = useState<FormState>({});
  const { strategy, details, configure } = formData;

  const { createProvider, isCreating } = useSsoProviderCreate({
    createAction,
    customMessages,
  });
  const { isLoadingConfig, filteredStrategies } = useConfig();
  const { isLoadingIdpConfig, idpConfig } = useIdpConfig();

  const detailsRef = useRef<ProviderDetailsFormHandle>(null);
  const configureRef = useRef<ProviderConfigureHandle>(null);

  const currentStyles = useMemo(
    () => getComponentStyles(styling, isDarkMode),
    [styling, isDarkMode],
  );

  const createStepActions = useCallback(
    (
      stepId: 'provider_details' | 'provider_configure',
      ref: React.RefObject<ProviderDetailsFormHandle | ProviderConfigureHandle | null>,
    ) => {
      const dataKey = stepId === 'provider_details' ? 'details' : 'configure';

      const handleAction = async (
        handler: typeof onNext | typeof onPrevious | undefined,
        shouldValidate = false,
      ): Promise<boolean> => {
        if (shouldValidate) {
          const isValid = await ref.current?.validate();
          if (!isValid) return false;
        }

        const currentData = ref.current?.getData() ?? null;
        setFormData((prev) => ({ ...prev, [dataKey]: currentData }));
        if (!handler) return true;
        const fullPayload = { ...formData, [dataKey]: currentData };
        return handler(stepId, fullPayload);
      };

      return {
        onNextAction: () => handleAction(onNext, true),
        onPreviousAction: () => handleAction(onPrevious, false),
      };
    },
    [formData, onNext, onPrevious],
  );

  const handleCreate = useCallback(async () => {
    const finalConfigureData = configureRef.current?.getData();

    await createProvider({
      strategy: strategy!,
      ...details!,
      ...finalConfigureData,
    });
  }, [strategy, details, configure, createProvider]);

  const wizardSteps = useMemo(
    () => [
      {
        id: 'provider_select',
        title: t('steps.one'),
        content: ({ onNext: navigate }: StepProps) => (
          <ProviderSelect
            isLoading={isLoadingConfig}
            strategyList={filteredStrategies}
            onClickStrategy={(selected) => {
              setFormData((prev) => ({
                strategy: selected,
                details: prev.strategy === selected ? prev.details : null,
                configure: prev.strategy === selected ? prev.configure : null,
              }));

              onNext?.('provider_select', { strategy: selected });
              navigate?.();
            }}
            selectedStrategy={strategy}
            customMessages={customMessages.provider_select}
            className={currentStyles?.classes?.['ProviderSelect-root']}
          />
        ),
        actions: {
          showNext: false,
        },
      },
      {
        id: 'provider_details',
        title: t('steps.two'),
        content: () => (
          <ProviderDetails
            mode="create"
            ref={detailsRef}
            initialData={details ?? undefined}
            customMessages={customMessages.provider_details}
            styling={styling}
            className={currentStyles?.classes?.['ProviderDetails-root']}
          />
        ),
        actions: createStepActions('provider_details', detailsRef),
      },
      {
        id: 'provider_configure',
        title: t('steps.three'),
        content: () =>
          strategy ? (
            <ProviderConfigure
              ref={configureRef}
              strategy={strategy}
              isLoading={isLoadingIdpConfig}
              initialData={configure ?? undefined}
              customMessages={customMessages.provider_configure}
              idpConfig={idpConfig}
              className={currentStyles?.classes?.['ProviderConfigure-root']}
            />
          ) : null,
        actions: createStepActions('provider_configure', configureRef),
      },
    ],
    [
      t,
      strategy,
      details,
      configure,
      onNext,
      onPrevious,
      customMessages,
      currentStyles,
      styling,
      createStepActions,
    ],
  );

  return (
    <div style={currentStyles.variables} className="w-full">
      <Header
        title={t('header.title')}
        backButton={
          backButton && {
            ...backButton,
            text: t('header.back_button_text'),
          }
        }
        className={currentStyles?.classes?.['SsoProviderCreate-header']}
      />
      <div className="sso-provider-create__content" data-testid="sso-provider-create-content">
        <Wizard
          isLoading={isCreating}
          hideStepperNumbers
          steps={wizardSteps}
          onComplete={handleCreate}
          formActionLabels={{
            nextButtonLabel: t('nextButtonLabel'),
            previousButtonLabel: t('previousButtonLabel'),
            completeButtonLabel: t('completeButtonLabel'),
          }}
          className={currentStyles?.classes?.['SsoProviderCreate-wizard']}
        />
      </div>
    </div>
  );
}

export const SsoProviderCreate = withMyOrganizationService(
  SsoProviderCreateComponent,
  MY_ORGANIZATION_SSO_PROVIDER_CREATE_SCOPES,
);

import type { IdpStrategy } from '@auth0/universal-components-core';
import { STRATEGY_DISPLAY_NAMES, STRATEGIES } from '@auth0/universal-components-core';
import React from 'react';

import EntraIdLogo from '../../../../assets/icons/entraid-logo';
import GoogleLogo from '../../../../assets/icons/google-logo';
import MicrosoftLogo from '../../../../assets/icons/microsoft-logo';
import OidcLogo from '../../../../assets/icons/oidc-logo';
import OktaLogo from '../../../../assets/icons/okta-logo';
import PingIdLogo from '../../../../assets/icons/pingid-logo';
import SamlpLogo from '../../../../assets/icons/sampl-logo';
import { Button } from '../../../../components/ui/button';
import { Section } from '../../../../components/ui/section';
import { Spinner } from '../../../../components/ui/spinner';
import { useTranslator } from '../../../../hooks/use-translator';
import type { ProviderSelectProps } from '../../../../types/my-organization/idp-management/sso-provider/sso-provider-create-types';

const STRATEGY_ICONS: Record<IdpStrategy, React.FC<{ className?: string }>> = {
  [STRATEGIES.OKTA]: OktaLogo,
  [STRATEGIES.GOOGLE_APPS]: GoogleLogo,
  [STRATEGIES.WAAD]: EntraIdLogo,
  [STRATEGIES.ADFS]: MicrosoftLogo,
  [STRATEGIES.PINGFEDERATE]: PingIdLogo,
  [STRATEGIES.SAMLP]: SamlpLogo,
  [STRATEGIES.OIDC]: OidcLogo,
};

function ProviderSelect({
  isLoading,
  strategyList,
  onClickStrategy,
  customMessages = {},
  className,
}: ProviderSelectProps) {
  const { t } = useTranslator('idp_management.create_sso_provider.provider_select', customMessages);

  return (
    <Section title={t('title')} description={t('description')} className={className}>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {isLoading ? (
          <div className="col-span-full flex items-center justify-center min-h-[100px]">
            <Spinner />
          </div>
        ) : (
          strategyList.map((strategyType) => {
            const IconComponent = STRATEGY_ICONS[strategyType as IdpStrategy];
            const displayName = STRATEGY_DISPLAY_NAMES[strategyType as IdpStrategy];
            return (
              <Button
                key={strategyType}
                variant="outline"
                className="justify-start h-10 py-2.5 px-3"
                onClick={() => onClickStrategy(strategyType)}
              >
                <div className="flex items-center gap-3 w-full text-left">
                  <IconComponent className="w-4 h-4 flex-shrink-0 text-primary" />
                  <div className="flex flex-col gap-1">
                    <span className="font-medium">{displayName}</span>
                  </div>
                </div>
              </Button>
            );
          })
        )}
      </div>
    </Section>
  );
}

export { ProviderSelect };

import {
  createProviderDetailsSchema,
  type ProviderDetailsFormValues,
} from '@auth0/universal-components-core';
import { zodResolver } from '@hookform/resolvers/zod';
import * as React from 'react';
import { useForm } from 'react-hook-form';

import { useTranslator } from '../../../../hooks/use-translator';
import type { ProviderDetailsProps } from '../../../../types/my-organization/idp-management/sso-provider/sso-provider-create-types';
import {
  Form,
  FormControl,
  FormDescription,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from '../../../ui/form';
import { Section } from '../../../ui/section';
import { TextField } from '../../../ui/text-field';

export interface ProviderDetailsFormHandle {
  validate: () => Promise<boolean>;
  getData: () => ProviderDetailsFormValues;
  isDirty: () => boolean;
  reset: (data?: ProviderDetailsFormValues) => void;
}

export const ProviderDetails = React.forwardRef<ProviderDetailsFormHandle, ProviderDetailsProps>(
  function ProviderDetails(
    { initialData, mode, readOnly = false, customMessages = {}, className, onFormDirty },
    ref,
  ) {
    const { t } = useTranslator(
      'idp_management.create_sso_provider.provider_details',
      customMessages,
    );

    const form = useForm<ProviderDetailsFormValues>({
      resolver: zodResolver(createProviderDetailsSchema()),
      mode: 'onSubmit',
      reValidateMode: 'onChange',
      defaultValues: {
        name: initialData?.name || '',
        display_name: initialData?.display_name || '',
      },
    });

    const { isDirty } = form.formState;

    React.useEffect(() => {
      onFormDirty?.(isDirty);
    }, [isDirty, onFormDirty]);

    React.useImperativeHandle(ref, () => ({
      validate: async () => {
        return await form.trigger();
      },
      getData: () => form.getValues(),
      isDirty: () => form.formState.isDirty,
      reset: (data) => {
        if (data) {
          form.reset(data);
        } else {
          form.reset();
        }
      },
    }));

    return (
      <Form {...form}>
        <div className={className}>
          <Section title={t('title')} description={t('description')}>
            <FormField
              control={form.control}
              name="name"
              render={({ field, fieldState }) => (
                <FormItem>
                  <FormLabel className="text-sm text-(length:--font-size-label) font-medium">
                    {t('fields.name.label')}
                  </FormLabel>
                  <FormControl>
                    <TextField
                      placeholder={t('fields.name.placeholder')}
                      error={Boolean(fieldState.error)}
                      readOnly={mode === 'edit' ? true : readOnly}
                      {...field}
                    />
                  </FormControl>
                  <FormMessage
                    className="text-left text-sm text-(length:--font-size-paragraph)"
                    role="alert"
                  />
                  <FormDescription className="text-sm text-(length:--font-size-paragraph) font-normal text-left">
                    {t('fields.name.helper_text')}
                  </FormDescription>
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="display_name"
              render={({ field, fieldState }) => (
                <FormItem>
                  <FormLabel className="text-sm text-(length:--font-size-label) font-medium">
                    {t('fields.display_name.label')}
                  </FormLabel>
                  <FormControl>
                    <TextField
                      placeholder={t('fields.display_name.placeholder')}
                      error={Boolean(fieldState.error)}
                      readOnly={readOnly}
                      {...field}
                    />
                  </FormControl>
                  <FormMessage
                    className="text-left text-sm text-(length:--font-size-paragraph)"
                    role="alert"
                  />
                  <FormDescription className="text-sm text-(length:--font-size-paragraph) font-normal text-left">
                    {t('fields.display_name.helper_text')}
                  </FormDescription>
                </FormItem>
              )}
            />
          </Section>
        </div>
      </Form>
    );
  },
);

import type { ProviderConfigureFormValues } from '@auth0/universal-components-core';
import * as React from 'react';

import { useTranslator } from '../../../../../hooks/use-translator';
import type { ProviderConfigureProps } from '../../../../../types/my-organization/idp-management/sso-provider/sso-provider-create-types';
import { Section } from '../../../../ui/section';
import { Spinner } from '../../../../ui/spinner';

import {
  ProviderConfigureFields,
  type ProviderConfigureFormHandle,
} from './provider-configure-fields';

export interface ProviderConfigureHandle {
  validate: () => Promise<boolean>;
  getData: () => ProviderConfigureFormValues;
}

export const ProviderConfigure = React.forwardRef<ProviderConfigureHandle, ProviderConfigureProps>(
  function ProviderConfigure(
    {
      strategy,
      initialData,
      readOnly = false,
      customMessages = {},
      className,
      idpConfig,
      isLoading,
    },
    ref,
  ) {
    const { t } = useTranslator(
      'idp_management.create_sso_provider.provider_configure',
      customMessages,
    );

    const formRef = React.useRef<ProviderConfigureFormHandle>(null);

    React.useImperativeHandle(ref, () => ({
      validate: async () => {
        return (await formRef.current?.validate()) ?? false;
      },
      getData: () => {
        return formRef.current?.getData() as ProviderConfigureFormValues;
      },
    }));

    if (isLoading) {
      return (
        <div className="flex justify-center items-center p-8">
          <Spinner />
        </div>
      );
    }

    return (
      <div className={className}>
        <Section title={t('title')} description={t('description')}>
          <ProviderConfigureFields
            ref={formRef}
            strategy={strategy}
            initialData={initialData}
            readOnly={readOnly}
            customMessages={customMessages.fields}
            idpConfig={idpConfig}
          />
        </Section>
      </div>
    );
  },
);

export default ProviderConfigure;

import { STRATEGIES } from '@auth0/universal-components-core';
import * as React from 'react';

import { cn } from '../../../../../lib/theme-utils';
import type { ProviderConfigureFieldsProps } from '../../../../../types/my-organization/idp-management/sso-provider/sso-provider-create-types';

import { AdfsProviderForm, type AdfsConfigureFormHandle } from './adfs-sso-configure-form';
import {
  GoogleAppsProviderForm,
  type GoogleAppsConfigureFormHandle,
} from './google-apps-sso-configure-form';
import { OidcProviderForm, type OidcConfigureFormHandle } from './oidc-sso-configure-form';
import { OktaProviderForm, type OktaConfigureFormHandle } from './okta-sso-configure-form';
import {
  PingFederateProviderForm,
  type PingFederateConfigureFormHandle,
} from './ping-federate-sso-configure-form';
import { SamlpProviderForm, type SamlpConfigureFormHandle } from './samlp-sso-configure-form';
import { WaadProviderForm, type WaadConfigureFormHandle } from './waad-sso-configure-form';

export type ProviderConfigureFormHandle =
  | OktaConfigureFormHandle
  | GoogleAppsConfigureFormHandle
  | WaadConfigureFormHandle
  | PingFederateConfigureFormHandle
  | AdfsConfigureFormHandle
  | SamlpConfigureFormHandle
  | OidcConfigureFormHandle;

export const ProviderConfigureFields = React.forwardRef<
  ProviderConfigureFormHandle,
  ProviderConfigureFieldsProps
>(function ProviderConfigureFields({ strategy, className, ...props }, ref) {
  const renderProviderForm = () => {
    switch (strategy) {
      case STRATEGIES.OKTA:
        return (
          <OktaProviderForm ref={ref as React.ForwardedRef<OktaConfigureFormHandle>} {...props} />
        );
      case STRATEGIES.GOOGLE_APPS:
        return (
          <GoogleAppsProviderForm
            ref={ref as React.ForwardedRef<GoogleAppsConfigureFormHandle>}
            {...props}
          />
        );
      case STRATEGIES.WAAD:
        return (
          <WaadProviderForm ref={ref as React.ForwardedRef<WaadConfigureFormHandle>} {...props} />
        );
      case STRATEGIES.PINGFEDERATE:
        return (
          <PingFederateProviderForm
            ref={ref as React.ForwardedRef<PingFederateConfigureFormHandle>}
            {...props}
          />
        );
      case STRATEGIES.ADFS:
        return (
          <AdfsProviderForm ref={ref as React.ForwardedRef<AdfsConfigureFormHandle>} {...props} />
        );
      case STRATEGIES.SAMLP:
        return (
          <SamlpProviderForm ref={ref as React.ForwardedRef<SamlpConfigureFormHandle>} {...props} />
        );
      case STRATEGIES.OIDC:
        return (
          <OidcProviderForm ref={ref as React.ForwardedRef<OidcConfigureFormHandle>} {...props} />
        );
      default:
        return null;
    }
  };

  return <div className={cn('space-y-6', className)}>{renderProviderForm()}</div>;
});

import {
  createProviderConfigureSchema,
  type AdfsConfigureFormValues,
} from '@auth0/universal-components-core';
import { zodResolver } from '@hookform/resolvers/zod';
import * as React from 'react';
import { useState } from 'react';
import { useForm } from 'react-hook-form';

import { useTranslator } from '../../../../../hooks/use-translator';
import { cn } from '../../../../../lib/theme-utils';
import type { ProviderConfigureFieldsProps } from '../../../../../types/my-organization/idp-management/sso-provider/sso-provider-create-types';
import { CopyableTextField } from '../../../../ui/copyable-text-field';
import { FileUpload } from '../../../../ui/file-upload';
import {
  Form,
  FormField,
  FormItem,
  FormLabel,
  FormControl,
  FormMessage,
  FormDescription,
} from '../../../../ui/form';
import { Label } from '../../../../ui/label';
import { RadioGroup, RadioGroupItem } from '../../../../ui/radio-group';
import { TextField } from '../../../../ui/text-field';

import { CommonConfigureFields } from './common-configure-fields';

export interface AdfsConfigureFormHandle {
  validate: () => Promise<boolean>;
  getData: () => AdfsConfigureFormValues;
  isDirty: () => boolean;
  reset: (data?: AdfsConfigureFormValues) => void;
}

interface AdfsConfigureFormProps extends Omit<ProviderConfigureFieldsProps, 'strategy'> {}

export const AdfsProviderForm = React.forwardRef<AdfsConfigureFormHandle, AdfsConfigureFormProps>(
  function AdfsProviderForm(
    { initialData, readOnly = false, customMessages = {}, className, onFormDirty, idpConfig },
    ref,
  ) {
    const { t } = useTranslator(
      'idp_management.create_sso_provider.provider_configure',
      customMessages,
    );

    const [uploadedFiles, setUploadedFiles] = useState<File[]>([]);

    const adfsData = initialData as AdfsConfigureFormValues | undefined;

    const form = useForm<AdfsConfigureFormValues>({
      resolver: zodResolver(createProviderConfigureSchema('adfs')),
      mode: 'onSubmit',
      reValidateMode: 'onChange',
      defaultValues: {
        meta_data_source: adfsData?.meta_data_source || 'meta_data_url',
        meta_data_location_url: adfsData?.meta_data_location_url || '',
        adfs_server: adfsData?.adfs_server || '',
        fedMetadataXml: adfsData?.fedMetadataXml || '',
      },
    });

    const { isDirty } = form.formState;

    React.useEffect(() => {
      onFormDirty?.(isDirty);
    }, [isDirty, onFormDirty]);

    React.useImperativeHandle(ref, () => ({
      validate: async () => {
        return await form.trigger();
      },
      getData: () => form.getValues(),
      isDirty: () => form.formState.isDirty,
      reset: (data) => {
        if (data) {
          form.reset(data);
        } else {
          form.reset();
        }
      },
    }));

    const typeValue = form.watch('meta_data_source');
    const showFederationMetadataFile = typeValue === 'meta_data_file';

    const handleFileUpload = async (files: File[]) => {
      setUploadedFiles(files);

      const file = files[0];
      if (file) {
        try {
          const content = await file.text();
          form.setValue('fedMetadataXml', content);
        } catch (error) {
          console.error('Error reading file:', error);
        }
      }
    };

    return (
      <Form {...form}>
        <div className={cn('space-y-6', className)}>
          <FormField
            control={form.control}
            name="meta_data_source"
            render={({ field }) => (
              <FormItem>
                <FormLabel className="text-sm font-medium text-(length:--font-size-label)">
                  {t('fields.adfs.meta_data_source.label')}
                </FormLabel>
                <FormControl>
                  <RadioGroup
                    value={field.value}
                    onValueChange={field.onChange}
                    disabled={readOnly}
                    className="flex flex-col space-y-2"
                  >
                    <div className="flex items-center space-x-2">
                      <RadioGroupItem value="meta_data_url" id="meta_data_url" />
                      <Label htmlFor="meta_data_url" className="text-sm font-normal cursor-pointer">
                        {t('fields.adfs.meta_data_source.options.meta_data_url.label')}
                      </Label>
                    </div>
                    <div className="flex items-center space-x-2">
                      <RadioGroupItem value="meta_data_file" id="meta_data_file" />
                      <Label
                        htmlFor="meta_data_file"
                        className="text-sm font-normal cursor-pointer"
                      >
                        {t('fields.adfs.meta_data_source.options.meta_data_file.label')}
                      </Label>
                    </div>
                  </RadioGroup>
                </FormControl>
                <FormMessage
                  role="alert"
                  className="text-sm text-left text-(length:--font-size-paragraph)"
                />
              </FormItem>
            )}
          />

          {typeValue === 'meta_data_url' && (
            <>
              <FormField
                control={form.control}
                name="adfs_server"
                render={({ field, fieldState }) => (
                  <FormItem>
                    <FormLabel className="text-sm font-medium text-(length:--font-size-label)">
                      {t('fields.adfs.meta_data_url.label')}
                    </FormLabel>
                    <FormControl>
                      <TextField
                        type="url"
                        placeholder={t('fields.adfs.meta_data_url.placeholder')}
                        error={Boolean(fieldState.error)}
                        readOnly={readOnly}
                        aria-required={true}
                        aria-invalid={Boolean(fieldState.error)}
                        {...field}
                      />
                    </FormControl>
                    <FormMessage
                      role="alert"
                      className="text-sm text-left text-(length:--font-size-paragraph)"
                    />
                    <FormDescription className="text-sm text-(length:--font-size-paragraph) font-normal text-left">
                      {t('fields.adfs.meta_data_url.helper_text')}
                    </FormDescription>
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="meta_data_location_url"
                render={({ field, fieldState }) => (
                  <FormItem>
                    <FormLabel className="text-sm font-medium text-(length:--font-size-label)">
                      {t('fields.adfs.meta_data_location_url.label')}
                    </FormLabel>
                    <FormControl>
                      <CopyableTextField
                        type="url"
                        placeholder={t('fields.adfs.meta_data_location_url.placeholder')}
                        error={Boolean(fieldState.error)}
                        readOnly={readOnly}
                        {...field}
                      />
                    </FormControl>
                    <FormMessage
                      role="alert"
                      className="text-sm text-left text-(length:--font-size-paragraph)"
                    />
                    <FormDescription className="text-sm text-(length:--font-size-paragraph) font-normal text-left">
                      {t('fields.adfs.meta_data_location_url.helper_text')}
                    </FormDescription>
                  </FormItem>
                )}
              />
            </>
          )}

          {showFederationMetadataFile && (
            <FormField
              control={form.control}
              name="fedMetadataXml"
              render={() => (
                <FormItem>
                  <FormLabel className="text-sm font-medium text-(length:--font-size-label)">
                    {t('fields.adfs.federation_metadata_file.label')}
