import { Copy } from 'lucide-react';
import * as React from 'react';

import { useTranslator } from '../../hooks/use-translator';
import { cn } from '../../lib/theme-utils';

import { Button } from './button';
import { TextField, type TextFieldProps } from './text-field';
import { Tooltip, TooltipTrigger, TooltipContent } from './tooltip';

export interface CopyableTextFieldProps extends TextFieldProps {
  onCopy?: () => void;
  copyButtonClassName?: string;
  tooltipSide?: 'top' | 'bottom' | 'left' | 'right';
  tooltipAlign?: 'start' | 'center' | 'end';
  showCopyButton?: boolean;
}

const CopyableTextField = React.forwardRef<HTMLInputElement, CopyableTextFieldProps>(
  (
    {
      onCopy,
      copyButtonClassName,
      tooltipSide = 'top',
      tooltipAlign = 'end',
      readOnly = true,
      endAdornment,
      showCopyButton = true,
      ...props
    },
    ref,
  ) => {
    const { t } = useTranslator('common');
    const [tooltipText, setTooltipText] = React.useState(t('copy'));
    const [tooltipOpen, setTooltipOpen] = React.useState(false);

    const handleCopy = async () => {
      if (props.value) {
        await navigator.clipboard.writeText(String(props.value));
        setTooltipText(t('copied'));
        setTooltipOpen(true);
        setTimeout(() => {
          setTooltipText(t('copy'));
          setTooltipOpen(false);
        }, 1000);
        onCopy?.();
      }
    };

    const copyButton = (
      <Tooltip open={tooltipOpen} onOpenChange={setTooltipOpen}>
        <TooltipTrigger asChild>
          <Button
            type="button"
            variant="ghost"
            size="icon"
            className={cn('h-8 w-8', copyButtonClassName)}
            onClick={handleCopy}
            aria-label={t('copy')}
          >
            <Copy className="h-4 w-4" aria-hidden="true" />
          </Button>
        </TooltipTrigger>
        <TooltipContent side={tooltipSide} align={tooltipAlign} sideOffset={5} className="z-[1000]">
          {tooltipText}
        </TooltipContent>
      </Tooltip>
    );

    return (
      <TextField
        ref={ref}
        readOnly={readOnly}
        {...props}
        endAdornment={
          showCopyButton ? (
            endAdornment ? (
              <div className="flex items-center gap-1">
                {endAdornment}
                {copyButton}
              </div>
            ) : (
              copyButton
            )
          ) : (
            endAdornment
          )
        }
      />
    );
  },
);

CopyableTextField.displayName = 'CopyableTextField';

export { CopyableTextField };

import { Upload, X } from 'lucide-react';
import * as React from 'react';

import { cn } from '../../lib/theme-utils';

export interface FileUploadProps
  extends Omit<React.InputHTMLAttributes<HTMLInputElement>, 'value' | 'onChange'> {
  value?: File[];
  onChange?: (files: File[]) => void;
  onRemove?: (file: File) => void;
  maxSize?: number;
  accept?: string;
  maxFiles?: number;
  preview?: boolean;
  className?: string;
}

export function FileUpload({
  value = [],
  onChange,
  onRemove,
  maxSize,
  accept,
  maxFiles = 1,
  preview = true,
  className,
  disabled,
  ...props
}: FileUploadProps) {
  const inputRef = React.useRef<HTMLInputElement>(null);
  const [dragActive, setDragActive] = React.useState(false);

  const handleDrag = React.useCallback((e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === 'dragenter' || e.type === 'dragover') {
      setDragActive(true);
    } else if (e.type === 'dragleave') {
      setDragActive(false);
    }
  }, []);

  const handleDrop = React.useCallback((e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);

    const files = Array.from(e.dataTransfer.files);
    handleFiles(files);
  }, []);

  const handleChange = React.useCallback((e: React.ChangeEvent<HTMLInputElement>) => {
    const files = Array.from(e.target.files || []);
    handleFiles(files);
  }, []);

  const handleFiles = React.useCallback(
    (files: File[]) => {
      if (disabled) return;
      const validFiles = files.filter((file) => {
        if (maxSize && file.size > maxSize) return false;
        if (
          accept &&
          !accept.split(',').some((type) => {
            const fileType = type.trim().toLowerCase();
            const fileName = file.name.toLowerCase();
            const fileMimeType = file.type.toLowerCase();
            if (fileType.startsWith('.')) {
              return fileName.endsWith(fileType);
            }

            if (fileType === fileMimeType) {
              return true;
            }

            if (fileType.endsWith('/*')) {
              const baseType = fileType.slice(0, -2);
              return fileMimeType.startsWith(`${baseType}/`);
            }

            return false;
          })
        )
          return false;
        return true;
      });

      if (maxFiles === 1) {
        onChange?.(validFiles.slice(0, 1));
      } else {
        onChange?.([...value, ...validFiles].slice(0, maxFiles));
      }

      if (inputRef.current) inputRef.current.value = '';
    },
    [disabled, maxSize, accept, maxFiles, onChange, value],
  );

  const removeFile = React.useCallback(
    (file: File) => {
      onRemove?.(file);
      onChange?.(value.filter((f) => f !== file));
    },
    [onChange, onRemove, value],
  );

  return (
    <div className={cn('space-y-4', className)}>
      <div
        onDragEnter={handleDrag}
        onDragLeave={handleDrag}
        onDragOver={handleDrag}
        onDrop={handleDrop}
        className={cn(
          'border-muted-foreground/25 relative flex min-h-[150px] cursor-pointer items-center justify-center rounded-lg border-2 border-dashed p-4 text-center transition-colors',
          dragActive && 'border-primary',
          disabled && 'cursor-not-allowed opacity-60',
          className,
        )}
      >
        <input
          ref={inputRef}
          type="file"
          multiple={maxFiles !== 1}
          accept={accept}
          onChange={handleChange}
          disabled={disabled}
          className="absolute inset-0 cursor-pointer opacity-0 disabled:cursor-not-allowed"
          {...props}
        />
        <div className="flex flex-col items-center gap-2">
          <Upload className="text-muted-foreground h-8 w-8" />
          <p className="text-muted-foreground text-sm">
            Drag & drop files here, or click to select files
          </p>
          {accept && <p className="text-muted-foreground text-xs">Allowed files: {accept}</p>}
          {maxSize && (
            <p className="text-muted-foreground text-xs">
              Max size: {(maxSize / 1024 / 1024).toFixed(2)}MB
            </p>
          )}
        </div>
      </div>

      {preview && value.length > 0 && (
        <div className="grid gap-4">
          {value.map((file, i) => (
            <div key={i} className="flex items-center gap-2 rounded-md border p-2">
              <div className="flex-1 truncate">
                <p className="text-sm font-medium">{file.name}</p>
                <p className="text-muted-foreground text-xs">{(file.size / 1024).toFixed(2)}KB</p>
              </div>
              <button
                type="button"
                onClick={() => removeFile(file)}
                className="hover:bg-muted shrink-0 rounded-md p-1 transition-colors"
                disabled={disabled}
              >
                <X className="size-4" />
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

'use client';

import type * as LabelPrimitive from '@radix-ui/react-label';
import { Slot } from '@radix-ui/react-slot';
import * as React from 'react';
import {
  Controller,
  FormProvider,
  useFormContext,
  useFormState,
  type ControllerProps,
  type FieldPath,
  type FieldValues,
} from 'react-hook-form';

import { cn } from '../../lib/theme-utils';

import { Label } from './label';

const Form: typeof FormProvider = FormProvider;

type FormFieldContextValue<
  TFieldValues extends FieldValues = FieldValues,
  TName extends FieldPath<TFieldValues> = FieldPath<TFieldValues>,
> = {
  name: TName;
};

const FormFieldContext = React.createContext<FormFieldContextValue>({} as FormFieldContextValue);

const FormField = <
  TFieldValues extends FieldValues = FieldValues,
  TName extends FieldPath<TFieldValues> = FieldPath<TFieldValues>,
>({
  ...props
}: ControllerProps<TFieldValues, TName>) => {
  return (
    <FormFieldContext.Provider value={{ name: props.name }}>
      <Controller
        {...props}
        render={({ field, fieldState, formState }) => {
          const { onChange, onBlur, ...rest } = field;
          return props.render({
            field: {
              ...rest,
              onChange: (...args: unknown[]) => {
                onChange(...(args as Parameters<typeof onChange>));
              },
              onBlur: (...args: unknown[]) => {
                onBlur(...(args as Parameters<typeof onBlur>));
              },
            },
            fieldState,
            formState,
          });
        }}
      />
    </FormFieldContext.Provider>
  );
};

const useFormField = () => {
  const fieldContext = React.useContext(FormFieldContext);
  const itemContext = React.useContext(FormItemContext);
  const { getFieldState } = useFormContext();
  const formState = useFormState({ name: fieldContext.name });
  const fieldState = getFieldState(fieldContext.name, formState);

  if (!fieldContext) {
    throw new Error('useFormField should be used within <FormField>');
  }

  const { id } = itemContext;

  return {
    id,
    name: fieldContext.name,
    formItemId: `${id}-form-item`,
    formDescriptionId: `${id}-form-item-description`,
    formMessageId: `${id}-form-item-message`,
    ...fieldState,
  };
};

type FormItemContextValue = {
  id: string;
};

const FormItemContext = React.createContext<FormItemContextValue>({} as FormItemContextValue);

function FormItem({ className, ...props }: React.ComponentProps<'div'>) {
  const id = React.useId();

  return (
    <FormItemContext.Provider value={{ id }}>
      <div data-slot="form-item" className={cn('grid gap-2', className)} {...props} />
    </FormItemContext.Provider>
  );
}

function FormLabel({ className, ...props }: React.ComponentProps<typeof LabelPrimitive.Root>) {
  const { error, formItemId } = useFormField();

  return (
    <Label
      data-slot="form-label"
      data-error={!!error}
      className={cn('data-[error=true]:text-destructive-foreground', className)}
      htmlFor={formItemId}
      {...props}
    />
  );
}

function FormControl({ ...props }: React.ComponentProps<typeof Slot>) {
  const { error, formItemId, formDescriptionId, formMessageId } = useFormField();

  return (
    <Slot
      data-slot="form-control"
      id={formItemId}
      aria-describedby={!error ? `${formDescriptionId}` : `${formDescriptionId} ${formMessageId}`}
      aria-invalid={!!error}
      {...props}
    />
  );
}

function FormDescription({ className, ...props }: React.ComponentProps<'p'>) {
  const { formDescriptionId } = useFormField();

  return (
    <p
      data-slot="form-description"
      id={formDescriptionId}
      className={cn('text-muted-foreground text-sm', className)}
      {...props}
    />
  );
}

function FormMessage({ className, ...props }: React.ComponentProps<'p'>) {
  const { error, formMessageId } = useFormField();
  const body = error ? String(error?.message ?? '') : props.children;

  if (!body) {
    return null;
  }

  return (
    <p
      data-slot="form-message"
      id={formMessageId}
      className={cn('text-destructive-foreground text-sm', className)}
      {...props}
    >
      {body}
    </p>
  );
}

export {
  useFormField,
  Form,
  FormItem,
  FormLabel,
  FormControl,
  FormDescription,
  FormMessage,
  FormField,
};

import type { ActionButton } from '@auth0/universal-components-core';
import * as React from 'react';

import { cn } from '../../lib/theme-utils';

import { Button } from './button';
import { Spinner } from './spinner';

export interface FormActionsProps<T = void> {
  hasUnsavedChanges?: boolean;
  isLoading?: boolean;
  nextAction?: Partial<ActionButton<T>>;
  previousAction?: Partial<ActionButton<T>>;
  showPrevious?: boolean;
  showNext?: boolean;
  showUnsavedChanges?: boolean;
  align?: 'left' | 'right';
  className?: string;
  unsavedChangesText?: string;
}

const DEFAULT_NEXT_ACTION: ActionButton = {
  type: 'submit',
  label: 'Save',
  variant: 'primary',
  onClick: () => {},
};

const DEFAULT_PREVIOUS_ACTION: ActionButton = {
  label: 'Cancel',
  variant: 'outline',
  onClick: () => {},
};

export const FormActions: React.FC<FormActionsProps> = ({
  hasUnsavedChanges = false,
  isLoading = false,
  nextAction,
  previousAction,
  className,
  showPrevious = true,
  showNext = true,
  showUnsavedChanges = true,
  align = 'right',
  unsavedChangesText = 'Unsaved changes',
}) => {
  const nextButtonProps = React.useMemo(
    () => ({ ...DEFAULT_NEXT_ACTION, ...nextAction }),
    [nextAction],
  );

  const previousButtonProps = React.useMemo(
    () => ({ ...DEFAULT_PREVIOUS_ACTION, ...previousAction }),
    [previousAction],
  );

  const handleNextClick = React.useCallback(
    (e: React.MouseEvent<HTMLButtonElement>) => {
      nextButtonProps.onClick(e.nativeEvent);
    },
    [nextButtonProps.onClick],
  );

  const handlePreviousClick = React.useCallback(
    (e: React.MouseEvent<HTMLButtonElement>) => {
      previousButtonProps.onClick(e.nativeEvent);
    },
    [previousButtonProps.onClick],
  );

  const showUnsavedIndicator = showUnsavedChanges && hasUnsavedChanges;

  return (
    <div
      className={cn(
        'flex flex-row items-center gap-2 p-2',
        align === 'right' ? 'justify-end' : 'justify-start',
        className,
      )}
    >
      {showUnsavedIndicator && (
        <div className="flex items-center gap-2">
          <div
            className="w-2 h-2 rounded-full bg-orange-500 transition-colors"
            aria-hidden="true"
          />
          <span className="text-sm text-muted-foreground">{unsavedChangesText}</span>
        </div>
      )}

      {showPrevious && (
        <Button
          type="button"
          variant={previousButtonProps.variant}
          size={previousButtonProps.size}
          onClick={handlePreviousClick}
          disabled={previousButtonProps.disabled || isLoading}
          className="FormActions-previous"
        >
          {previousButtonProps.label}
        </Button>
      )}

      {showNext && (
        <Button
          type={nextButtonProps.type}
          variant={nextButtonProps.variant}
          size={nextButtonProps.size}
          disabled={nextButtonProps.disabled || isLoading}
          className="FormActions-next"
          {...(nextButtonProps.type !== 'submit' && { onClick: handleNextClick })}
        >
          {isLoading ? (
            <Spinner
              colorScheme={nextButtonProps.variant === 'destructive' ? 'primary' : 'foreground'}
              size="sm"
              aria-hidden="true"
            />
          ) : (
            <>
              {nextButtonProps.icon && (
                <span className="mr-2" aria-hidden="true">
                  {nextButtonProps.icon as React.ReactNode}
                </span>
              )}
              {nextButtonProps.label}
            </>
          )}
        </Button>
      )}
    </div>
  );
};

import type { ActionButton as CoreActionButton } from '@auth0/universal-components-core';
import type { LucideIcon } from 'lucide-react';
import { ArrowLeft } from 'lucide-react';
import * as React from 'react';

import { cn } from '../../lib/theme-utils';

import { Button } from './button';
import { Spinner } from './spinner';
import { Switch } from './switch';
import { Tooltip, TooltipTrigger, TooltipContent } from './tooltip';

export interface TooltipProps {
  content: React.ReactNode | string;
}

export interface WithTooltipProps {
  trigger: React.ReactNode | string;
  tooltip: TooltipProps;
}

export interface BaseActionProps extends Omit<CoreActionButton, 'icon' | 'onClick'> {
  icon?: LucideIcon;
  className?: string;
  hidden?: boolean;
  tooltip?: TooltipProps;
}

export interface ButtonActionProps extends BaseActionProps {
  type: 'button';
  onClick: (e: React.MouseEvent<HTMLButtonElement>) => void;
}

export interface SwitchActionProps extends Omit<BaseActionProps, 'type' | 'label'> {
  type: 'switch';
  checked?: boolean;
  onCheckedChange: (checked: boolean) => void;
  'aria-label'?: string;
}

export type ActionProps = ButtonActionProps | SwitchActionProps;

export interface HeaderProps {
  title?: string;
  description?: string;
  backButton?: {
    text?: string;
    icon?: LucideIcon;
    onClick: (e: React.MouseEvent<HTMLButtonElement>) => void;
  };
  actions?: ActionProps[];
  isLoading?: boolean;
  className?: string;
}

const WithTooltip: React.FC<WithTooltipProps> = ({ trigger, tooltip }) => (
  <>
    <Tooltip>
      <TooltipTrigger asChild>
        <div>{trigger}</div>
      </TooltipTrigger>
      <TooltipContent>{tooltip.content}</TooltipContent>
    </Tooltip>
  </>
);

const ButtonAction: React.FC<ButtonActionProps> = ({
  icon: Icon,
  className,
  label,
  onClick,
  disabled,
  variant,
  size,
}) => (
  <Button
    onClick={onClick}
    disabled={disabled}
    variant={variant}
    size={size}
    className={cn('flex items-center gap-2 w-full sm:w-auto sm:min-w-fit', className)}
    aria-label={label}
  >
    {Icon && <Icon className="h-4 w-4 flex-shrink-0" aria-hidden="true" />}
    <span className="truncate">{label}</span>
  </Button>
);

const SwitchAction: React.FC<SwitchActionProps> = ({
  className,
  'aria-label': ariaLabel,
  checked,
  onCheckedChange,
  disabled,
}) => (
  <div className={cn('flex items-center gap-2', className)}>
    <Switch
      checked={checked}
      onCheckedChange={onCheckedChange}
      disabled={disabled}
      aria-label={ariaLabel}
    />
  </div>
);

export const Header = React.forwardRef<
  HTMLDivElement,
  HeaderProps & React.HTMLAttributes<HTMLDivElement>
>(({ title, description, backButton, actions, isLoading, className, ...props }, ref) => {
  const BackIcon = backButton?.icon || ArrowLeft;

  const renderAction = (action: ActionProps, index: number) => {
    const key = `action-${index}`;
    if (isLoading) {
      return <Spinner className="w-4 h-4" />;
    }
    if (action.hidden) {
      return null;
    }
    const actionElement =
      action.type === 'switch' ? (
        <SwitchAction key={key} {...action} />
      ) : (
        <ButtonAction key={key} {...action} />
      );
    if (action.tooltip) {
      return (
        <WithTooltip key={`tooltip-${key}`} trigger={actionElement} tooltip={action.tooltip} />
      );
    }
    return actionElement;
  };

  return (
    <div
      ref={ref}
      className={cn('w-full mb-8', className)}
      role="banner"
      aria-label={title ? `${title} header` : 'Header'}
      {...props}
    >
      {backButton && (
        <Button
          variant="link"
          onClick={backButton.onClick}
          size="default"
          className="flex items-center text-sm mb-3"
          aria-label={backButton.text || 'Go back'}
        >
          <BackIcon className="h-4 w-4" aria-hidden="true" />
          {backButton.text && <span>{backButton.text}</span>}
        </Button>
      )}

      <div className="flex items-start justify-between gap-4">
        <div className="flex flex-col min-w-0 flex-1">
          {title && (
            <h1
              className={cn(
                'text-xl sm:text-2xl text-primary md:text-4xl font-bold leading-tight break-words text-left text-(length:--font-size-page-header) mb-0',
              )}
            >
              {title}
            </h1>
          )}
          {description && (
            <p
              className={cn(
                'text-base text-muted-foreground leading-relaxed break-words text-left text-(length:--font-size-page-description) mt-2',
              )}
            >
              {description}
            </p>
          )}
        </div>

        {actions && actions.length > 0 && (
          <div className="flex-shrink-0 flex items-start gap-2 mt-1">
            {actions.map(renderAction)}
          </div>
        )}
      </div>
    </div>
  );
});

Header.displayName = 'Header';

