import { applyStyleOverrides, type StylingVariables } from '@auth0/universal-components-core';
import * as React from 'react';

import type { ThemeContextValue, ThemeInput } from '../types/theme-types';

/**
 * Default empty customer overrides. (later may be UL branding)
 */
const defaultStyleOverrides: StylingVariables = { common: {}, light: {}, dark: {} };

/**
 * ThemeContext
 *
 * Provides access to customer overrides and a merged theme object for convenience.
 */
export const ThemeContext = React.createContext<ThemeContextValue>({
  isDarkMode: false,
  variables: defaultStyleOverrides,
  loader: null,
});

/**
 * ThemeProvider
 *
 * Provides theme configuration via React Context to all components in the tree.
 * It merges optional styling overrides (CSS variables).
 *
 * @param themeSettings - Optional styling overrides
 * @param children - The components that will have access to the theme
 *
 * @example
 * ```tsx
 * <ThemeProvider
 *   themeSettings={{
 *     theme: "default" | "minimal" | "rounded";
 *     mode: 'dark',
 *     variables: {
 *       common: {
 *         "--font-size-heading": "1.5rem",
 *         "--font-size-title": "1.25rem",
 *       },
 *       light: {
 *         "--color-primary": "blue",
 *       },
 *       dark: {
 *         "--color-primary": "red",
 *       },
 *     },
 *     loader: <CustomSpinner />
 *   }}
 * >
 *   <App />
 * </ThemeProvider>
 * ```
 */
export const ThemeProvider: React.FC<{
  themeSettings?: ThemeInput;
  children: React.ReactNode;
}> = ({ themeSettings, children }) => {
  const { variables, loader, mode, theme } = React.useMemo(
    () => ({
      variables: themeSettings?.variables ?? defaultStyleOverrides,
      loader: themeSettings?.loader ?? null,
      mode: themeSettings?.mode,
      theme: themeSettings?.theme,
    }),
    [themeSettings],
  );

  React.useEffect(() => {
    applyStyleOverrides(variables, mode, theme);
  }, [variables, mode, theme]);

  return (
    <ThemeContext.Provider value={{ isDarkMode: mode === 'dark', variables, loader }}>
      {children}
    </ThemeContext.Provider>
  );
};

import React, { useState, useCallback, useEffect, type ReactNode } from 'react';

import { useCoreClient } from '../hooks/use-core-client';
import { ScopeManagerContext, type Audience } from '../hooks/use-scope-manager';

export const ScopeManagerProvider: React.FC<{ children: ReactNode }> = ({ children }) => {
  const { coreClient } = useCoreClient();

  const [scopeRegistry, setScopeRegistry] = useState<Record<Audience, Set<string>>>(() => ({
    me: new Set(),
    'my-org': new Set(),
  }));

  const [ensured, setEnsured] = useState<Record<Audience, string>>({
    me: '',
    'my-org': '',
  });

  const [isReady, setIsReady] = useState(false);

  const registerScopes = useCallback((audience: Audience, scopes: string) => {
    if (!scopes?.trim()) return;

    const newScopes = scopes
      .split(/\s+/)
      .map((s) => s.trim())
      .filter(Boolean);

    if (newScopes.length === 0) return;

    setScopeRegistry((currentRegistry) => {
      const audienceSet = currentRegistry[audience];
      let changed = false;
      const nextAudienceSet = new Set(audienceSet);

      newScopes.forEach((scope) => {
        if (!nextAudienceSet.has(scope)) {
          nextAudienceSet.add(scope);
          changed = true;
        }
      });

      if (changed) {
        return {
          ...currentRegistry,
          [audience]: nextAudienceSet,
        };
      }
      return currentRegistry;
    });
  }, []);

  useEffect(() => {
    if (!coreClient) return;

    const ensureAllScopesSequential = async () => {
      let hasScopes = false;
      let anyUpdated = false;

      for (const audience of ['me', 'my-org'] as const) {
        const scopes = Array.from(scopeRegistry[audience]).sort();
        const scopeString = scopes.join(' ');

        if (scopes.length > 0 && scopeString.trim()) {
          hasScopes = true;

          if (scopeString !== ensured[audience]) {
            try {
              await coreClient.ensureScopes(scopeString, audience);
              anyUpdated = true;
            } catch (error) {
              console.error(`Failed to ensure scopes for ${audience}: ${scopeString}`, error);
            }
          }
        }
      }

      // Update ensured state to match current registry
      if (anyUpdated) {
        setEnsured({
          me: Array.from(scopeRegistry.me).sort().join(' '),
          'my-org': Array.from(scopeRegistry['my-org']).sort().join(' '),
        });
      }

      setIsReady(hasScopes);
    };

    ensureAllScopesSequential();
  }, [coreClient, scopeRegistry, ensured]);

  const contextValue = React.useMemo(
    () => ({ registerScopes, isReady, ensured }),
    [registerScopes, isReady, ensured],
  );

  return (
    <ScopeManagerContext.Provider value={contextValue}>{children}</ScopeManagerContext.Provider>
  );
};

import {
  AVAILABLE_STRATEGY_LIST,
  hasApiErrorBody,
  type GetConfigurationResponseContent,
  type IdpStrategy,
} from '@auth0/universal-components-core';
import { useCallback, useEffect, useMemo, useState } from 'react';

import type { UseConfigResult } from '../../../types/my-organization/config/config-types';
import { useCoreClient } from '../../use-core-client';

export function useConfig(): UseConfigResult {
  const { coreClient } = useCoreClient();
  const [config, setConfig] = useState<GetConfigurationResponseContent | null>(null);
  const [isLoadingConfig, setIsLoadingConfig] = useState(false);
  const [isConfigValid, setIsConfigValid] = useState(true);

  const fetchConfig = useCallback(async (): Promise<void> => {
    if (!coreClient) {
      return;
    }
    setIsLoadingConfig(true);

    try {
      const result: GetConfigurationResponseContent = await coreClient
        .getMyOrganizationApiClient()
        .organization.configuration.get();
      setConfig(result);

      // Validate the config after fetching
      const hasAllowedStrategies =
        result.allowed_strategies && result.allowed_strategies.length > 0;
      setIsConfigValid(!!hasAllowedStrategies);
    } catch (error) {
      // If config is not set
      if (hasApiErrorBody(error) && error.body?.status === 404) {
        setConfig(null);
        setIsConfigValid(false);
      }
    } finally {
      setIsLoadingConfig(false);
    }
  }, [coreClient]);

  const filteredStrategies = useMemo((): IdpStrategy[] => {
    const allowedStrategies = config?.allowed_strategies;

    if (!allowedStrategies) {
      return AVAILABLE_STRATEGY_LIST;
    }

    return AVAILABLE_STRATEGY_LIST.filter((strategy) => allowedStrategies.includes(strategy));
  }, [config]);

  const shouldAllowDeletion = useMemo((): boolean => {
    return (
      config?.connection_deletion_behavior === 'allow' ||
      config?.connection_deletion_behavior === 'allow_if_empty'
    );
  }, [config]);

  // Fetch config on mount
  useEffect(() => {
    fetchConfig();
  }, []);

  return {
    config,
    isLoadingConfig,
    fetchConfig,
    filteredStrategies,
    shouldAllowDeletion,
    isConfigValid,
  };
}

import { hasApiErrorBody, type IdpStrategy } from '@auth0/universal-components-core';
import { useCallback, useEffect, useState } from 'react';

import type {
  IdpConfig,
  UseConfigIdpResult,
} from '../../../types/my-organization/config/config-idp-types';
import { useCoreClient } from '../../use-core-client';

export function useIdpConfig(): UseConfigIdpResult {
  const { coreClient } = useCoreClient();
  const [idpConfig, setIdpConfig] = useState<IdpConfig | null>(null);
  const [isLoadingIdpConfig, setIsLoadingIdpConfig] = useState(false);
  const [isIdpConfigValid, setIsIdpConfigValid] = useState(true);

  const fetchIdpConfig = useCallback(async (): Promise<void> => {
    if (!coreClient) {
      return;
    }
    setIsLoadingIdpConfig(true);

    try {
      const result = (await coreClient
        .getMyOrganizationApiClient()
        .organization.configuration.identityProviders.get()) as unknown as IdpConfig;

      setIdpConfig(result);

      // Validate the idpConfig after fetching
      const hasStrategies = result.strategies && Object.keys(result.strategies).length > 0;
      setIsIdpConfigValid(!!hasStrategies);
    } catch (error) {
      // If config is not set
      if (hasApiErrorBody(error) && error.body?.status === 404) {
        setIdpConfig(null);
        setIsIdpConfigValid(false);
      }
    } finally {
      setIsLoadingIdpConfig(false);
    }
  }, [coreClient]);

  const isProvisioningEnabled = useCallback(
    (strategy: IdpStrategy | undefined): boolean => {
      if (!strategy || !idpConfig?.strategies?.[strategy]) {
        return false;
      }
      return idpConfig.strategies[strategy].enabled_features.includes('provisioning');
    },
    [idpConfig],
  );

  const isProvisioningMethodEnabled = useCallback(
    (strategy: IdpStrategy | undefined): boolean => {
      if (!strategy || !idpConfig?.strategies?.[strategy]) {
        return false;
      }
      return idpConfig.strategies[strategy].provisioning_methods.includes('scim');
    },
    [idpConfig],
  );

  // Fetch config on mount
  useEffect(() => {
    fetchIdpConfig();
  }, []);

  return {
    idpConfig,
    isLoadingIdpConfig,
    fetchIdpConfig,
    isProvisioningEnabled,
    isProvisioningMethodEnabled,
    isIdpConfigValid,
  };
}

