@import 'tailwindcss';

@import './font-sizes.css';

@import './themes/default.css';
@import './themes/minimal.css';
@import './themes/rounded.css';

:root {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

@layer base {
  body {
    @apply antialiased;
    font-feature-settings:
      'rlig' 1,
      'calt' 1;
  }

  .color-scheme-transitioning *,
  .color-scheme-transitioning *::before,
  .color-scheme-transitioning *::after {
    transition: var(--color-scheme-transition) !important;
    transition-delay: 0ms !important;
  }
}

:root {
  * {
    scrollbar-width: thin;
    scrollbar-color: hsl(var(--border)) transparent;
  }

  ::-webkit-scrollbar {
    width: 6px;
  }

  ::-webkit-scrollbar-thumb {
    background: hsl(var(--border));
    border-radius: 6px;
  }

  ::-webkit-scrollbar-track {
    background: transparent;
  }
}

@theme inline {
  /**
   * STANDARD SHADCN VARIABLES (with auth0 fallbacks)
   * Map shadcn standard variable names defined by the user to auth0 ones
   */
  --color-background: var(--background, var(--color-neutral-min));
  --color-foreground: var(--foreground, var(--color-neutral-12));
  --color-card: var(--card, var(--color-neutral-min));
  --color-card-foreground: var(--card-foreground, var(--color-neutral-max));
  --color-primary: var(--primary, var(--color-carbon-9));
  --color-primary-foreground: var(--primary-foreground, var(--color-neutral-min));
  --color-secondary: var(--secondary, var(--color-neutral-3));
  --color-secondary-foreground: var(--secondary-foreground, var(--color-neutral-1));
  --color-accent: var(--accent, var(--color-neutral-2));
  --color-accent-foreground: var(--accent-foreground, var(--color-neutral-12));
  --color-muted: var(--muted, var(--color-neutral-2));
  --color-muted-foreground: var(--muted-foreground, var(--color-neutral-11));
  --color-destructive: var(--destructive, var(--color-red-3));
  --color-destructive-foreground: var(--destructive-foreground, var(--color-red-9));
  --color-popover: var(--popover, var(--color-neutral-1));
  --color-popover-foreground: var(--popover-foreground, var(--color-neutral-12));
  --color-input: var(--input, var(--color-neutral-1));
  --color-border: var(--border, var(--color-neutral-6));
  --color-ring: var(--ring, hsla(var(--color-neutral-7), 0.2));

  /**
   * CUSTOM AUTH0 VARIABLES (no shadcn equivalents)
   * These are auth0-specific and don't map to standard shadcn variables
   */

  /* Page background */
  --color-page: var(--color-neutral-2);

  /* Status colors */
  --color-info: var(--color-blue-3);
  --color-info-foreground: var(--color-blue-9);
  --color-success: var(--color-green-3);
  --color-success-foreground: var(--color-green-9);
  --color-warning: var(--color-yellow-3);
  --color-warning-foreground: var(--color-yellow-11);

  /* Extended semantic colors */
  --color-destructive-border: var(--color-red-10);
  --color-popover-border: var(--color-neutral-6);
  --color-input-foreground: var(--color-neutral-12);
  --color-input-muted: var(--color-neutral-2);

  /* Shadows */
  --shadow-bevel-xs: var(--shadow-bevel-xs);
  --shadow-bevel-sm: var(--shadow-bevel-sm);
  --shadow-bevel-md: var(--shadow-bevel-md);
  --shadow-bevel-lg: var(--shadow-bevel-md);
  --shadow-bevel-xl: var(--shadow-bevel-xl);
  --shadow-bevel-2xl: var(--shadow-bevel-2xl);

  --shadow-button-resting: var(--shadow-button-resting);
  --shadow-button-hover: var(--shadow-button-hover);
  --shadow-button-focus: var(--shadow-button-hover);

  --shadow-button-destructive-resting: var(--shadow-button-destructive-resting);
  --shadow-button-destructive-hover: var(--shadow-button-destructive-hover);
  --shadow-button-destructive-focus: var(--shadow-button-destructive-focus);

  --shadow-button-outlined-resting: var(--shadow-button-outlined-resting);
  --shadow-button-outlined-hover: var(--shadow-button-outlined-resting);
  --shadow-button-outlined-focus: var(--shadow-button-outlined-resting);

  --shadow-input-resting: var(--shadow-input-resting);
  --shadow-input-hover: var(--shadow-input-hover);
  --shadow-input-focus: var(--shadow-input-focus);
  --shadow-input-destructive-resting: var(--shadow-input-destructive-resting);
  --shadow-input-destructive-hover: var(--shadow-input-destructive-hover);
  --shadow-input-destructive-focus: var(--shadow-input-destructive-focus);

  --shadow-checkbox-resting: var(--shadow-checkbox-resting);
  --shadow-checkbox-hover: var(--shadow-checkbox-hover);

  --shadow-switch-resting: var(--shadow-switch-resting);
  --shadow-switch-hover: var(--shadow-switch-resting);
  --shadow-switch-focus: var(--shadow-switch-focus);
  --shadow-switch-thumb: var(--shadow-switch-thumb);
  --shadow-switch-thumb-dark: var(--shadow-switch-thumb-dark);

  /* Border radius */
  --radius-xs: var(--radius-xs);
  --radius-sm: var(--radius-sm);
  --radius-md: var(--radius-md);
  --radius-lg: var(--radius-lg);
  --radius-xl: var(--radius-xl);
  --radius-2xl: var(--radius-2xl);
  --radius-3xl: var(--radius-3xl);
  --radius-4xl: var(--radius-4xl);
  --radius-5xl: var(--radius-5xl);
  --radius-6xl: var(--radius-6xl);
  --radius-7xl: var(--radius-7xl);
  --radius-8xl: var(--radius-8xl);
  --radius-9xl: var(--radius-9xl);

  /* Defined in font-sizes.css */
  --font-size-page-header: var(--font-size-page-header);
  --font-size-page-description: var(--font-size-page-description);
  --font-size-heading: var(--font-size-heading);
  --font-size-title: var(--font-size-title);
  --font-size-subtitle: var(--font-size-subtitle);
  --font-size-body: var(--font-size-body);
  --font-size-paragraph: var(--font-size-paragraph);
  --font-size-label: var(--font-size-label);
}

