      0px 2px 1px -2px oklch(from var(--color-neutral-max) l c h / 5%),
      0px 4px 2px -4px oklch(from var(--color-neutral-max) l c h / 5%),
      0px 6px 3px -6px oklch(from var(--color-neutral-max) l c h / 10%);
    --shadow-bevel-xl:
      0px 0px 0px 1px oklch(from var(--color-neutral-max) l c h / 10%),
      0px 1px 1px -1px oklch(from var(--color-neutral-max) l c h / 5%),
      0px 2px 1px -2px oklch(from var(--color-neutral-max) l c h / 5%),
      0px 4px 2px -4px oklch(from var(--color-neutral-max) l c h / 5%),
      0px 6px 3px -6px oklch(from var(--color-neutral-max) l c h / 10%),
      0px 12px 6px -12px oklch(from var(--color-neutral-max) l c h / 10%);
    --shadow-bevel-2xl:
      0px 0px 0px 1px oklch(from var(--color-neutral-max) l c h / 10%),
      0px 1px 1px -1px oklch(from var(--color-neutral-max) l c h / 5%),
      0px 2px 1px -2px oklch(from var(--color-neutral-max) l c h / 5%),
      0px 4px 2px -4px oklch(from var(--color-neutral-max) l c h / 5%),
      0px 6px 3px -6px oklch(from var(--color-neutral-max) l c h / 10%),
      0px 12px 6px -12px oklch(from var(--color-neutral-max) l c h / 10%),
      0px 24px 12px -24px oklch(from var(--color-neutral-max) l c h / 10%);

    --shadow-button-resting: none;
    --shadow-button-hover: none;

    --shadow-button-destructive-resting: none;
    --shadow-button-destructive-hover: none;

    --shadow-button-outlined-resting: none;
    --shadow-button-outlined-hover: none;

    --shadow-input-resting: 0px 0px 0px 1px oklch(from var(--color-neutral-max) l c h / 10%);
    --shadow-input-hover: 0px 0px 0px 1px oklch(from var(--color-neutral-max) l c h / 20%);
    --shadow-input-destructive-resting: 0px 0px 0px 1px
      oklch(from var(--color-destructive-border) l c h / 25%);
    --shadow-input-destructive-hover: 0px 0px 0px 1px
      oklch(from var(--color-destructive-border) l c h / 50%);

    --shadow-checkbox-resting: none;
    --shadow-checkbox-hover: none;

    --shadow-switch-resting: none;
    --shadow-switch-hover: none;
    --shadow-switch-focus: none;
    --shadow-switch-thumb: none;

    --radius-xs: 8px;
    --radius-sm: 10px;
    --radius-md: 12px;
    --radius-lg: 14px;
    --radius-xl: 16px;
    --radius-2xl: 20px;
    --radius-3xl: 24px;
    --radius-4xl: 32px;
    --radius-5xl: 36px;
    --radius-6xl: 40px;
    --radius-7xl: 48px;
    --radius-8xl: 56px;
    --radius-9xl: 64px;
  }
}
