'use client';

import { getComponentStyles, MY_ORG_SSO_PROVIDER_EDIT_SCOPES } from '@auth0/web-ui-components-core';
import React, { useState } from 'react';

import { SsoDomainTab } from '../../../components/my-org/idp-management/sso-provider-edit/sso-domain-tab';
import { SsoProviderTab } from '../../../components/my-org/idp-management/sso-provider-edit/sso-provider-tab';
import { SsoProvisioningTab } from '../../../components/my-org/idp-management/sso-provider-edit/sso-provisioning/sso-provisioning-tab';
import { Header } from '../../../components/ui/header';
import { Spinner } from '../../../components/ui/spinner';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '../../../components/ui/tabs';
import { withMyOrgService } from '../../../hoc/with-services';
import { useConfig } from '../../../hooks/my-org/config/use-config';
import { useIdpConfig } from '../../../hooks/my-org/config/use-idp-config';
import { useSsoProviderEdit } from '../../../hooks/my-org/idp-management/use-sso-provider-edit';
import { useTheme } from '../../../hooks/use-theme';
import { useTranslator } from '../../../hooks/use-translator';
import { cn } from '../../../lib/theme-utils';
import type { SsoProviderEditProps } from '../../../types/my-org/idp-management/sso-provider/sso-provider-edit-types';

export function SsoProviderEditComponent({
  providerId,
  backButton,
  sso,
  provisioning,
  domains,
  hideHeader = false,
  customMessages = {},
  styling = {
    variables: { common: {}, light: {}, dark: {} },
    classes: {},
  },
  schema,
  readOnly = false,
}: SsoProviderEditProps) {
  const { t } = useTranslator('idp_management.edit_sso_provider', customMessages);
  const { isDarkMode } = useTheme();

  const {
    provider,
    organization,
    isLoading,
    isUpdating,
    isDeleting,
    isRemoving,
    isProvisioningUpdating,
    isProvisioningDeleting,
    isScimTokensLoading,
    isScimTokenCreating,
    isScimTokenDeleting,
    updateProvider,
    createProvisioning: createProvisioningAction,
    deleteProvisioning: deleteProvisioningAction,
    listScimTokens,
    createScimToken: createScimTokenAction,
    deleteScimToken: deleteScimTokenAction,
    onDeleteConfirm,
    onRemoveConfirm,
  } = useSsoProviderEdit(providerId, {
    sso,
    provisioning,
    domains,
    customMessages,
  });
  const { shouldAllowDeletion, isLoadingConfig } = useConfig();
  const { idpConfig, isLoadingIdpConfig, isProvisioningEnabled, isProvisioningMethodEnabled } =
    useIdpConfig();

  const showProvisioningTab =
    isProvisioningEnabled(provider?.strategy) && isProvisioningMethodEnabled(provider?.strategy);

  const [activeTab, setActiveTab] = useState('sso');

  const currentStyles = React.useMemo(
    () => getComponentStyles(styling, isDarkMode),
    [styling, isDarkMode],
  );

  const handleToggleProvider = async (enabled: boolean) => {
    if (!provider?.strategy) return;

    await updateProvider({
      strategy: provider.strategy,
      is_enabled: enabled,
    });
  };

  if (isLoading || isLoadingConfig || isLoadingIdpConfig) {
    return (
      <div className="flex justify-center items-center p-8">
        <Spinner />
      </div>
    );
  }

  return (
    <div style={currentStyles.variables} className="w-full">
      {!hideHeader && (
        <Header
          title={provider?.display_name || provider?.name || ''}
          backButton={
            backButton && {
              ...backButton,
              text: t('header.back_button_text'),
            }
          }
          isLoading={isUpdating}
          actions={[
            {
              type: 'switch',
              checked: provider?.is_enabled ?? false,
              onCheckedChange: handleToggleProvider,
              disabled: isUpdating,
            },
          ]}
          className={currentStyles?.classes?.['SsoProviderEdit-header']}
        />
      )}

      <Tabs
        value={activeTab}
        onValueChange={setActiveTab}
        className={cn('space-y-10', currentStyles?.classes?.['SsoProviderEdit-tabs'])}
      >
        <TabsList
          className={cn('grid w-full', showProvisioningTab ? 'grid-cols-3' : 'grid-cols-2')}
        >
          <TabsTrigger value="sso" className="text-sm">
            {t('tabs.sso.name')}
          </TabsTrigger>
          {showProvisioningTab && (
            <TabsTrigger value="provisioning" className="text-sm">
              {t('tabs.provisioning.name')}
            </TabsTrigger>
          )}
          <TabsTrigger value="domain" className="text-sm">
            {t('tabs.domains.name')}
          </TabsTrigger>
        </TabsList>

        <TabsContent value="sso">
          <SsoProviderTab
            provider={provider}
            organization={organization}
            onDelete={onDeleteConfirm}
            onRemove={onRemoveConfirm}
            isDeleting={isDeleting}
            isRemoving={isRemoving}
            idpConfig={idpConfig}
            shouldAllowDeletion={shouldAllowDeletion}
            customMessages={customMessages.tabs?.sso?.content}
            styling={styling}
            formActions={{
              isLoading: isUpdating,
              nextAction: {
                disabled: isUpdating || !provider || isLoading,
                onClick: updateProvider,
              },
            }}
            readOnly={readOnly}
          />
        </TabsContent>

        {showProvisioningTab && (
          <TabsContent value="provisioning">
            <SsoProvisioningTab
              provider={provider!}
              isProvisioningUpdating={isProvisioningUpdating}
              isProvisioningDeleting={isProvisioningDeleting}
              isScimTokensLoading={isScimTokensLoading}
              isScimTokenCreating={isScimTokenCreating}
              isScimTokenDeleting={isScimTokenDeleting}
              onCreateProvisioning={createProvisioningAction}
              onDeleteProvisioning={deleteProvisioningAction}
              onListScimTokens={listScimTokens}
              onCreateScimToken={createScimTokenAction}
              onDeleteScimToken={deleteScimTokenAction}
              customMessages={customMessages.tabs?.provisioning?.content}
              styling={styling}
            />
          </TabsContent>
        )}

        <TabsContent value="domain">
          <SsoDomainTab
            customMessages={customMessages.tabs?.domains?.content}
            styling={styling}
            domains={domains}
            schema={schema?.domains}
            idpId={providerId}
            provider={provider}
            readOnly={readOnly}
          />
        </TabsContent>
      </Tabs>
    </div>
  );
}

export const SsoProviderEdit = withMyOrgService(
  SsoProviderEditComponent,
  MY_ORG_SSO_PROVIDER_EDIT_SCOPES,
);

import type { Domain } from '@auth0/web-ui-components-core';
import { getComponentStyles } from '@auth0/web-ui-components-core';
import { Plus } from 'lucide-react';
import React from 'react';

import { Badge } from '../../../../components/ui/badge';
import { DataTable, type Column } from '../../../../components/ui/data-table';
import { Header } from '../../../../components/ui/header';
import { useSsoDomainTab } from '../../../../hooks/my-org/idp-management/use-sso-domain-tab';
import { useTheme } from '../../../../hooks/use-theme';
import { useTranslator } from '../../../../hooks/use-translator';
import { getStatusBadgeVariant } from '../../../../lib/my-org/domain-management';
import type { SsoDomainsTabProps } from '../../../../types/my-org/idp-management/sso-domain/sso-domain-tab-types';
import { DomainCreateModal } from '../../domain-management/domain-create/domain-create-modal';
import { DomainDeleteModal } from '../../domain-management/domain-delete/domain-delete-modal';
import { DomainVerifyModal } from '../../domain-management/domain-verify/domain-verify-modal';

import { SsoDomainTabActionsColumn } from './sso-domain-tab-action-column';

/**
 * SsoDomainTab Component
 */
export function SsoDomainTab({
  customMessages = {},
  styling = {
    variables: { common: {}, light: {}, dark: {} },
    classes: {},
  },
  readOnly = false,
  schema,
  idpId,
  domains,
  provider,
}: SsoDomainsTabProps) {
  const { t } = useTranslator(
    'idp_management.edit_sso_provider.tabs.domains.content',
    customMessages,
  );

  const { isDarkMode } = useTheme();
  const currentStyles = React.useMemo(
    () => getComponentStyles(styling, isDarkMode),
    [styling, isDarkMode],
  );

  const {
    domainsList,
    isCreating,
    selectedDomain,
    showVerifyModal,
    isVerifying,
    showDeleteModal,
    verifyError,
    handleCloseVerifyModal,
    handleVerify,
    handleDeleteClick,
    isLoading,
    isDeleting,
    setShowDeleteModal,
    handleDelete,
    setShowCreateModal,
    showCreateModal,
    handleCreate,
    idpDomains,
    handleVerifyActionColumn,
    isUpdating,
    isUpdatingId,
    handleToggleSwitch,
  } = useSsoDomainTab(idpId, {
    customMessages,
    domains,
    provider,
  });

  const columns: Column<Domain>[] = React.useMemo(
    () => [
      {
        type: 'text',
        accessorKey: 'domain',
        title: t('table.columns.name'),
        render: (domain) => <div className="font-medium">{domain.domain}</div>,
      },
      {
        type: 'text',
        accessorKey: 'status',
        title: t('table.columns.status'),
        render: (domain) => (
          <Badge variant={getStatusBadgeVariant(domain.status)} size={'sm'}>
            {t(`table.domain_statuses.${domain.status}`)}
          </Badge>
        ),
      },
      {
        type: 'actions',
        title: '',
        render: (domain) => (
          <SsoDomainTabActionsColumn
            translatorKey="idp_management.edit_sso_provider.tabs.domains.content"
            idpDomains={idpDomains}
            readOnly={readOnly}
            isUpdating={isUpdating}
            isUpdatingId={isUpdatingId}
            customMessages={customMessages}
            onToggle={handleToggleSwitch}
            handleVerify={handleVerifyActionColumn}
            domain={domain}
          />
        ),
      },
    ],
    [t],
  );

  return (
    <div style={currentStyles.variables} className="space-y-8">
      <div className={currentStyles.classes?.['SsoDomainsTab-header']}>
        <Header
          title={t('title')}
          description={t('description')}
          actions={[
            {
              type: 'button',
              label: t('create_button_text'),
              onClick: () => setShowCreateModal(true),
              icon: Plus,
              disabled: domains?.createAction?.disabled || readOnly,
            },
          ]}
        />
      </div>
      <DataTable
        columns={columns}
        data={domainsList}
        loading={isLoading}
        emptyState={{ title: t('table.empty_message') }}
        className={currentStyles.classes?.['SsoDomainsTab-table']}
      />

      <DomainCreateModal
        translatorKey="idp_management.edit_sso_provider.tabs.domains.content.domain_create.modal"
        className={currentStyles.classes?.['SsoDomainsTab-createModal']}
        isOpen={showCreateModal}
        isLoading={isCreating}
        schema={schema?.create}
        onClose={() => setShowCreateModal(false)}
        onCreate={handleCreate}
        customMessages={customMessages.domain_create}
      />

      <DomainVerifyModal
        translatorKey="idp_management.edit_sso_provider.tabs.domains.content.domain_verify.modal"
        className={currentStyles.classes?.['SsoDomainsTab-verifyModal']}
        isOpen={showVerifyModal}
        isLoading={isVerifying}
        domain={selectedDomain}
        error={verifyError}
        onClose={handleCloseVerifyModal}
        onVerify={handleVerify}
        onDelete={handleDeleteClick}
        customMessages={customMessages.domain_verify}
      />

      <DomainDeleteModal
        translatorKey="idp_management.edit_sso_provider.tabs.domains.content.domain_delete.modal"
        className={currentStyles.classes?.['SsoDomainsTab-deleteModal']}
        domain={selectedDomain}
        isOpen={showDeleteModal}
        isLoading={isDeleting}
        onClose={() => setShowDeleteModal(false)}
        onDelete={handleDelete}
        customMessages={customMessages.domain_delete}
      />
    </div>
  );
}

import { useTranslator } from '../../../../hooks/use-translator';
import type { SsoDomainTabActionColumn } from '../../../../types/my-org/idp-management/sso-domain/sso-domain-tab-types';
import { Button } from '../../../ui/button';
import { Spinner } from '../../../ui/spinner';
import { Switch } from '../../../ui/switch';

/**
 * SsoDomainTabActionsColumn component
 * Handles the actions column for SSO provider edit on domain tab table
 * with enable/disable toggle and verify button
 */
export function SsoDomainTabActionsColumn({
  translatorKey = 'idp_management.edit_sso_provider.tabs.domains',
  customMessages = {},
  readOnly,
  idpDomains,
  domain,
  handleVerify,
  isUpdating,
  isUpdatingId,
  onToggle,
}: SsoDomainTabActionColumn) {
  const { t } = useTranslator(translatorKey, customMessages);

  const providerHasDomain = idpDomains.includes(domain.id);

  if (isUpdating && isUpdatingId === domain.id) {
    return (
      <div className="flex items-center justify-end gap-4 min-w-0">
        <Spinner size="sm" />
      </div>
    );
  }
  return (
    <div className="flex items-center justify-end gap-4 min-w-0">
      {domain.status === 'verified' ? (
        <Switch
          checked={providerHasDomain}
          onCheckedChange={(checked) => onToggle(domain, checked)}
          disabled={readOnly || isUpdating}
        />
      ) : (
        <Button variant="outline" size="sm" onClick={() => handleVerify(domain)}>
          {t('table.columns.verify')}
        </Button>
      )}
    </div>
  );
}

import { getComponentStyles } from '@auth0/web-ui-components-core';
import React from 'react';

import { FormActions } from '../../../../components/ui/form-actions';
import { useTheme } from '../../../../hooks/use-theme';
import { useTranslator } from '../../../../hooks/use-translator';
import { cn } from '../../../../lib/theme-utils';
import type { SsoProviderDetailsProps } from '../../../../types/my-org/idp-management/sso-provider/sso-provider-tab-types';
import {
  ProviderConfigureFields,
  type ProviderConfigureFormHandle,
} from '../sso-provider-create/provider-configure/provider-configure-fields';
import {
  ProviderDetails,
  type ProviderDetailsFormHandle,
} from '../sso-provider-create/provider-details';

/**
 * SsoProviderDetails Component
 * Combines ProviderDetails and ProviderConfigureFields for editing SSO provider
 */
export function SsoProviderDetails({
  provider,
  readOnly = false,
  idpConfig,
  formActions,
  customMessages = {},
  styling = {
    variables: { common: {}, light: {}, dark: {} },
    classes: {},
  },
}: SsoProviderDetailsProps) {
  const { t } = useTranslator('idp_management.sso_provider_details', customMessages);
  const { isDarkMode } = useTheme();
  const providerDetailsRef = React.useRef<ProviderDetailsFormHandle>(null);
  const providerConfigureRef = React.useRef<ProviderConfigureFormHandle>(null);
  const [isDetailsDirty, setIsDetailsDirty] = React.useState(false);
  const [isConfigureDirty, setIsConfigureDirty] = React.useState(false);

  const currentStyles = React.useMemo(
    () => getComponentStyles(styling, isDarkMode),
    [styling, isDarkMode],
  );

  const providerDetailsData = React.useMemo(() => {
    if (!provider) return undefined;

    return {
      name: provider.name ?? undefined,
      display_name: provider.display_name ?? undefined,
    };
  }, [provider]);

  const hasUnsavedChanges = isDetailsDirty || isConfigureDirty;

  const handleSave = async () => {
    if (!formActions?.nextAction?.onClick || !provider?.strategy) return;

    const isDetailsValid = await providerDetailsRef.current?.validate();
    const isConfigureValid = await providerConfigureRef.current?.validate();

    if (!isDetailsValid || !isConfigureValid) {
      return;
    }

    const detailsData = providerDetailsRef.current?.getData();
    const configureData = providerConfigureRef.current?.getData();
    const updateData = {
      strategy: provider.strategy,
      ...detailsData,
      ...configureData,
    };

    await formActions.nextAction.onClick(updateData);

    setIsDetailsDirty(false);
    setIsConfigureDirty(false);
  };

  if (!provider) {
    return null;
  }

  return (
    <div style={currentStyles.variables} className={cn('space-y-8')}>
      <div className="space-y-4">
        <ProviderDetails
          ref={providerDetailsRef}
          initialData={providerDetailsData}
          readOnly={readOnly}
          customMessages={customMessages.details_fields}
          className={currentStyles.classes?.['ProviderDetails-root']}
          hideHeader
          onFormDirty={setIsDetailsDirty}
        />
      </div>

      <div className="space-y-4">
        <ProviderConfigureFields
          ref={providerConfigureRef}
          strategy={provider.strategy}
          initialData={provider.options}
          readOnly={readOnly}
          idpConfig={idpConfig}
          mode="edit"
          customMessages={customMessages.configure_fields}
          className={currentStyles.classes?.['ProviderConfigure-root']}
          onFormDirty={setIsConfigureDirty}
        />
      </div>

      {formActions && (
        <FormActions
          hasUnsavedChanges={hasUnsavedChanges}
          showUnsavedChanges
          isLoading={formActions.isLoading}
          nextAction={{
            label: t('submit_button_label'),
            disabled:
              !hasUnsavedChanges ||
              formActions?.nextAction?.disabled ||
              formActions.isLoading ||
              readOnly,
            type: 'button',
            onClick: handleSave,
          }}
          showPrevious={false}
          align={formActions?.align}
          className={currentStyles.classes?.['SsoProviderDetails-FormActions']}
        />
      )}
    </div>
  );
}

import { getComponentStyles } from '@auth0/web-ui-components-core';
import React from 'react';

import {
  Card,
  CardContent,
  CardHeader,
  CardTitle,
  CardDescription,
} from '../../../../components/ui/card';
import { useTheme } from '../../../../hooks/use-theme';
import { useTranslator } from '../../../../hooks/use-translator';
import type { SsoProviderTabProps } from '../../../../types/my-org/idp-management/sso-provider/sso-provider-tab-types';
import { SsoProviderDelete } from '../sso-provider-delete/provider-delete';
import { SsoProviderRemoveFromOrg } from '../sso-provider-remove/provider-remove';

import { SsoProviderDetails } from './sso-provider-details';

/**
 * SsoProviderTab Component
 */
export function SsoProviderTab({
  customMessages = {},
  styling = {
    variables: { common: {}, light: {}, dark: {} },
    classes: {},
  },
  readOnly = false,
  provider,
  onDelete,
  onRemove,
  organization,
  isDeleting,
  isRemoving,
  idpConfig,
  shouldAllowDeletion,
  formActions,
}: SsoProviderTabProps) {
  const { t } = useTranslator('idp_management.edit_sso_provider.tabs.sso', customMessages);

  const { isDarkMode } = useTheme();
  const currentStyles = React.useMemo(
    () => getComponentStyles(styling, isDarkMode),
    [styling, isDarkMode],
  );

  return (
    <div style={currentStyles.variables} className="space-y-8">
      <Card>
        <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
          <div className="space-y-1">
            <CardTitle className="text-lg text-left font-medium">{t('content.title')}</CardTitle>
            <CardDescription className="text-sm text-left text-muted-foreground">
              {t('content.description')}
            </CardDescription>
          </div>
        </CardHeader>
        <CardContent>
          {provider && (
            <SsoProviderDetails
              provider={provider}
              readOnly={readOnly}
              formActions={formActions}
              customMessages={customMessages.details}
              styling={styling}
              idpConfig={idpConfig}
            />
          )}
        </CardContent>
      </Card>

      <div className="space-y-4">
        {provider && organization && (
          <SsoProviderRemoveFromOrg
            provider={provider}
            organizationName={organization?.name}
            onRemove={onRemove}
            isLoading={isRemoving}
            readOnly={readOnly}
            customMessages={customMessages.remove}
          />
        )}

        {provider && shouldAllowDeletion && (
          <SsoProviderDelete
            provider={provider}
            onDelete={onDelete}
            isLoading={isDeleting}
            readOnly={readOnly}
            customMessages={customMessages.delete}
          />
        )}
      </div>
    </div>
  );
}

import * as React from 'react';

import { Button } from '../../../../components/ui/button';
import { Card, CardContent } from '../../../../components/ui/card';
import { useTranslator } from '../../../../hooks/use-translator';
import { cn } from '../../../../lib/theme-utils';
import type { SsoProviderDeleteProps } from '../../../../types/my-org/idp-management/sso-provider/sso-provider-delete-types';

import { SsoProviderDeleteModal } from './provider-delete-modal';

export function SsoProviderDelete({
  provider,
  onDelete,
  customMessages = {},
  isLoading,
  readOnly,
}: SsoProviderDeleteProps) {
  const { t } = useTranslator('idp_management.delete_sso_provider', customMessages);
  const [isModalOpen, setIsModalOpen] = React.useState(false);

  const openModal = React.useCallback(() => {
    setIsModalOpen(true);
  }, []);

  const closeModal = React.useCallback(() => {
    setIsModalOpen(false);
  }, []);

  return (
    <>
      <div className={cn('w-full')}>
        <Card className="p-6">
          <CardContent className="flex items-start justify-between gap-6 p-0">
            <div className="flex-1 space-y-2">
              <h3
                className={cn('text-lg font-semibold text-left text-(length:--font-size-subtitle)')}
              >
                {t('title', { providerName: provider.name })}
              </h3>
              <p
                className={cn(
                  'text-sm text-muted-foreground text-left text-(length:--font-size-paragraph)',
                )}
              >
                {t('description')}
              </p>
            </div>

            <Button
              variant="destructive"
              onClick={openModal}
              disabled={readOnly}
              className="shrink-0"
            >
              {t('delete_button_label')}
            </Button>
          </CardContent>
        </Card>
      </div>

      <SsoProviderDeleteModal
        isOpen={isModalOpen}
        onClose={closeModal}
        provider={provider}
        onDelete={onDelete}
        isLoading={isLoading}
        customMessages={customMessages.modal}
      />
    </>
  );
}

import * as React from 'react';

import { Modal } from '../../../../components/ui/modal';
import { useTranslator } from '../../../../hooks/use-translator';
import { cn } from '../../../../lib/theme-utils';
import type { SsoProviderDeleteModalProps } from '../../../../types/my-org/idp-management/sso-provider/sso-provider-delete-types';

import { SsoProviderDeleteModalContent } from './provider-delete-modal-content';

export function SsoProviderDeleteModal({
  className,
  isOpen,
  onClose,
  provider,
  onDelete,
  isLoading = false,
  customMessages = {},
}: SsoProviderDeleteModalProps) {
  const { t } = useTranslator('idp_management.delete_sso_provider', customMessages);
  const [confirmationText, setConfirmationText] = React.useState('');

  const handleModalContentChange = React.useCallback((value: string) => {
    setConfirmationText(value);
  }, []);

  const handleDelete = React.useCallback(async () => {
    if (provider) {
      await onDelete(provider);
      onClose();
    }
  }, [onDelete, provider, onClose]);

  const handleOpenChange = React.useCallback(
    (open: boolean) => {
      if (!open) {
        onClose();
      }
    },
    [onClose],
  );

  React.useEffect(() => {
    if (isOpen) {
      setConfirmationText('');
    }
  }, [isOpen]);

  return (
    <Modal
      open={isOpen}
      onOpenChange={handleOpenChange}
      className={cn('p-10', className)}
      title={t('modal.title', { providerName: provider.name })}
      content={
        <div className="space-y-6">
          <p className={cn('text-sm text-muted-foreground text-(length:--font-size-paragraph)')}>
            {t('modal.description', { providerName: provider.name })}
          </p>

          <SsoProviderDeleteModalContent
            onChange={handleModalContentChange}
            customMessages={customMessages.modal?.content}
          />
        </div>
      }
      modalActions={{
        isLoading,
        nextAction: {
          type: 'button',
          label: t('modal.actions.delete_button_label'),
          onClick: handleDelete,
          variant: 'destructive',
          disabled: isLoading || confirmationText !== provider.name,
        },
        previousAction: {
          label: t('modal.actions.cancel_button_label'),
          onClick: onClose,
        },
      }}
    />
  );
}

import React from 'react';

import { useTranslator } from '../../../../hooks/use-translator';
import { cn } from '../../../../lib/theme-utils';
import type { SsoProviderDeleteModalContentProps } from '../../../../types/my-org/idp-management/sso-provider/sso-provider-delete-types';
