'use client';

import {
  getComponentStyles,
  type ProvisioningDetailsFormValues,
  ssoProvisioningSchema,
} from '@auth0/web-ui-components-core';
import { zodResolver } from '@hookform/resolvers/zod';
import { useMemo } from 'react';
import { useForm } from 'react-hook-form';

import { useCoreClient } from '../../../../../hooks/use-core-client';
import { useTheme } from '../../../../../hooks/use-theme';
import { useTranslator } from '../../../../../hooks/use-translator';
import { cn } from '../../../../../lib/theme-utils';
import type { SsoProvisioningDetailsProps } from '../../../../../types/my-org/idp-management/sso-provisioning/sso-provisioning-tab-types';
import { CopyableTextField } from '../../../../ui/copyable-text-field';
import {
  Form,
  FormControl,
  FormDescription,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from '../../../../ui/form';
import { TextField } from '../../../../ui/text-field';

import { ProvisioningManageToken } from './provisioning-manage-token';
import { ProvisioningFieldMappings } from './provisioning-mappings';

export function SsoProvisioningDetails({
  provider,
  provisioningConfig,
  isScimTokensLoading,
  isScimTokenCreating,
  isScimTokenDeleting,
  onListScimTokens,
  onCreateScimToken,
  onDeleteScimToken,
  customMessages = {},
  styling = {
    variables: { common: {}, light: {}, dark: {} },
    classes: {},
  },
}: SsoProvisioningDetailsProps) {
  const { t } = useTranslator(
    'idp_management.edit_sso_provider.tabs.provisioning.content.details',
    customMessages,
  );
  const { isDarkMode } = useTheme();

  const currentStyles = useMemo(
    () => getComponentStyles(styling, isDarkMode),
    [styling, isDarkMode],
  );

  const { coreClient } = useCoreClient();

  const scimEndpointUrl = useMemo(() => {
    const domain = coreClient?.auth?.domain || 'your domain';
    return `https://${domain}/scim/v2/connections/${provider.id}/`;
  }, [coreClient?.auth?.domain]);

  const form = useForm<ProvisioningDetailsFormValues>({
    resolver: zodResolver(ssoProvisioningSchema),
    defaultValues: {
      userIdAttribute: provisioningConfig?.user_id_attribute || '',
      scimEndpointUrl: scimEndpointUrl || '',
    },
  });

  return (
    <div
      style={currentStyles.variables}
      className={cn('space-y-6', currentStyles.classes?.['SsoProvisioningDetails-root'])}
    >
      <Form {...form}>
        <form className="space-y-6">
          <FormField
            control={form.control}
            name="userIdAttribute"
            render={({ field, fieldState }) => (
              <FormItem>
                <FormLabel className="text-sm text-(length:--font-size-label) font-medium">
                  {t('fields.user_id_attribute.label')}
                </FormLabel>
                <FormControl>
                  <TextField readOnly error={Boolean(fieldState.error)} {...field} />
                </FormControl>
                <FormMessage
                  className="text-left text-sm text-(length:--font-size-paragraph)"
                  role="alert"
                />
                <FormDescription className="text-sm text-(length:--font-size-paragraph) font-normal text-left">
                  {t('fields.user_id_attribute.helper_text')}
                </FormDescription>
              </FormItem>
            )}
          />

          <ProvisioningManageToken
            isScimTokensLoading={isScimTokensLoading}
            isScimTokenCreating={isScimTokenCreating}
            isScimTokenDeleting={isScimTokenDeleting}
            onListScimTokens={onListScimTokens}
            onCreateScimToken={onCreateScimToken}
            onDeleteScimToken={onDeleteScimToken}
            customMessages={customMessages?.manage_tokens}
            styling={styling}
          />

          <FormField
            control={form.control}
            name="scimEndpointUrl"
            render={({ field, fieldState }) => (
              <FormItem>
                <FormLabel className="text-sm text-(length:--font-size-label) font-medium">
                  {t('fields.scim_endpoint_url.label')}
                </FormLabel>
                <FormControl>
                  <CopyableTextField error={Boolean(fieldState.error)} readOnly {...field} />
                </FormControl>
                <FormMessage
                  className="text-left text-sm text-(length:--font-size-paragraph)"
                  role="alert"
                />
              </FormItem>
            )}
          />
        </form>
      </Form>
      <div className="mt-6">
        <ProvisioningFieldMappings
          provisioningFieldMap={provisioningConfig?.fields ?? null}
          customMessages={customMessages.mappings}
          className={currentStyles.classes?.['SsoProvisioningDetails-provisioningMapping']}
        />
      </div>
    </div>
  );
}

import { getComponentStyles } from '@auth0/web-ui-components-core';
import * as React from 'react';

import { useSsoProviderEdit } from '../../../../../hooks/my-org/idp-management/use-sso-provider-edit';
import { useTheme } from '../../../../../hooks/use-theme';
import { useTranslator } from '../../../../../hooks/use-translator';
import { cn } from '../../../../../lib/theme-utils';
import type { SsoProvisioningTabProps } from '../../../../../types/my-org/idp-management/sso-provisioning/sso-provisioning-tab-types';
import {
  Card,
  CardAction,
  CardDescription,
  CardHeader,
  CardTitle,
  CardContent,
} from '../../../../ui/card';
import { Spinner } from '../../../../ui/spinner';
import { Switch } from '../../../../ui/switch';
import { Tooltip, TooltipTrigger, TooltipContent } from '../../../../ui/tooltip';

import { SsoProvisioningDeleteModal } from './sso-provisioning-delete-modal';
import { SsoProvisioningDetails } from './sso-provisioning-details';

export function SsoProvisioningTab({
  provider,
  styling = { variables: { common: {}, light: {}, dark: {} }, classes: {} },
  customMessages = {},
}: SsoProvisioningTabProps): React.JSX.Element {
  const { t } = useTranslator(
    'idp_management.edit_sso_provider.tabs.provisioning.content',
    customMessages,
  );

  const {
    provisioningConfig,
    isProvisioningLoading,
    isProvisioningUpdating,
    isProvisioningDeleting,
    isScimTokensLoading,
    isScimTokenCreating,
    isScimTokenDeleting,
    fetchProvisioning,
    createProvisioning,
    deleteProvisioning,
    listScimTokens,
    createScimToken,
    deleteScimToken,
  } = useSsoProviderEdit(provider?.id || '');

  const { isDarkMode } = useTheme();
  const currentStyles = React.useMemo(
    () => getComponentStyles(styling, isDarkMode),
    [styling, isDarkMode],
  );

  const [isDeleteModalOpen, setDeleteModalOpen] = React.useState(false);

  React.useEffect(() => {
    if (provider?.id) {
      fetchProvisioning();
    }
  }, [provider?.id, fetchProvisioning]);

  const handleProvisioningToggle = async (enabled: boolean) => {
    if (!provider?.id) return;

    if (enabled) {
      await createProvisioning();
      await fetchProvisioning();
    } else {
      setDeleteModalOpen(true);
    }
  };

  const handleDeleteProvisioningConfirm = async () => {
    await deleteProvisioning();
    setDeleteModalOpen(false);
  };

  const isLoading = isProvisioningLoading || isProvisioningUpdating || isProvisioningDeleting;
  const isProvisioningEnabled = !!provisioningConfig;
  const enableProvisioningToggle = isLoading || !provider?.id || !provider.is_enabled;

  return (
    <div
      style={currentStyles.variables}
      className={cn('space-y-8', currentStyles.classes?.['SsoProvisioningTab-root'])}
    >
      <Card>
        <CardHeader>
          <CardTitle className="text-lg font-medium text-foreground text-left">
            {t('header.title')}
          </CardTitle>
          <CardDescription className="text-sm text-muted-foreground text-left">
            {t('header.description')}
          </CardDescription>
          <CardAction>
            <div className="flex items-center gap-3">
              <div className="flex items-center gap-2">
                {isLoading ? (
