const ALIGNMENT_CLASSES = {
  text: {
    left: 'text-left',
    center: 'text-center',
    right: 'text-right',
  },
  flex: {
    left: 'justify-start',
    center: 'justify-center',
    right: 'justify-end',
  },
} as const;

const isEmpty = (value: unknown): boolean => {
  return value === null || value === undefined || value === '';
};

const formatDate = (value: Date | string | number, format: string = 'medium'): string => {
  const date = new Date(value);
  if (isNaN(date.getTime())) return 'Invalid Date';

  switch (format) {
    case 'short':
      return date.toLocaleDateString();
    case 'long':
      return date.toLocaleDateString(undefined, {
        weekday: 'long',
        year: 'numeric',
        month: 'long',
        day: 'numeric',
      });
    case 'relative': {
      const now = new Date();
      const diffTime = date.getTime() - now.getTime();
      const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

      if (Math.abs(diffDays) === 0) return 'Today';
      if (diffDays === 1) return 'Tomorrow';
      if (diffDays === -1) return 'Yesterday';
      if (diffDays > 0) return `In ${diffDays} days`;
      return `${Math.abs(diffDays)} days ago`;
    }
    case 'medium':
    default:
      return date.toLocaleDateString(undefined, {
        year: 'numeric',
        month: 'short',
        day: 'numeric',
      });
  }
};

const DEFAULT_COPY_LABELS: Required<CopyColumnLabels> = {
  copyTooltip: 'Copy to clipboard',
  copiedTooltip: 'Copied!',
};

function CopyButton({
  value,
  labels = DEFAULT_COPY_LABELS,
}: {
  value: unknown;
  labels?: CopyColumnLabels;
}) {
  const [copied, setCopied] = useState(false);
  const [tooltipOpen, setTooltipOpen] = useState(false);

  const handleCopy = async (e: React.MouseEvent) => {
    e.stopPropagation();
    if (!value) return;

    try {
      await navigator.clipboard.writeText(String(value));
      setCopied(true);
      setTooltipOpen(true);

      setTimeout(() => {
        setCopied(false);
        setTooltipOpen(false);
      }, 2000);
    } catch (error) {
      console.error('Failed to copy text:', error);
    }
  };

  return (
    <InlineCode className="w-full flex items-center justify-between gap-2 pr-1">
      <span className="truncate text-muted-foreground">{String(value)}</span>

      <Tooltip open={tooltipOpen} onOpenChange={setTooltipOpen}>
        <TooltipTrigger asChild>
          <Button
            variant="ghost"
            size="sm"
            onClick={handleCopy}
            className="h-6 w-6 p-0 hover:bg-muted/50 shrink-0"
            aria-label={copied ? labels.copiedTooltip : labels.copyTooltip}
          >
            <Copy className="h-3 w-3" />
          </Button>
        </TooltipTrigger>
        <TooltipContent>
          <p>{copied ? labels.copiedTooltip : labels.copyTooltip}</p>
        </TooltipContent>
      </Tooltip>
    </InlineCode>
  );
}

function renderTextColumn<Item>(
  item: Item,
  value: unknown,
  column: TextColumn<Item>,
): React.ReactNode {
  if (column.render) {
    return column.render(item, value);
  }

  return <span className="text-muted-foreground">{String(value)}</span>;
}

function renderDateColumn<Item>(
  item: Item,
  value: Date | string | number,
  column: DateColumn<Item>,
): React.ReactNode {
  if (column.render) {
    return column.render(item, value);
  }
  const formattedDate = formatDate(value, column.format);

  return (
    <span className="text-muted-foreground" title={new Date(value).toISOString()}>
      {formattedDate}
    </span>
  );
}

function renderSwitchColumn<Item>(
  item: Item,
  value: boolean,
  column: SwitchColumn<Item>,
): React.ReactNode {
  const handleToggle = (checked: boolean) => {
    column.onToggle?.(checked, item);
  };

  return (
    <div onClick={(e) => e.stopPropagation()}>
      <Switch checked={!!value} onCheckedChange={handleToggle} />
    </div>
  );
}

function renderButtonColumn<Item>(item: Item, column: ButtonColumn<Item>): React.ReactNode {
  const handleClick = (e: React.MouseEvent) => {
    e.stopPropagation();
    column.onClick(item);
  };

  return (
    <Button variant={column.variant} size="sm" onClick={handleClick}>
      {column.buttonText}
    </Button>
  );
}

function renderBadgeColumn<Item>(value: unknown, column: BadgeColumn<Item>): React.ReactNode {
  return <Badge variant={column.variant}>{String(value)}</Badge>;
}

function renderCopyColumn(value: unknown): React.ReactNode {
  return <CopyButton value={value} />;
}

function EmptyState({ title, subtitle, action }: EmptyStateProps) {
  return (
    <div className="text-center py-12">
      <h3 className="text-md font-medium text-foreground mb-2">{title}</h3>
      {subtitle && <p className="text-sm text-muted-foreground mb-4">{subtitle}</p>}
      {action && (
        <Button variant={action.variant} onClick={action.onClick}>
          {action.label}
        </Button>
      )}
    </div>
  );
}

export function DataTable<Item>({
  data,
  columns,
  loading = false,
  loader,
  emptyState,
  onRowClick,
  className,
  headerAlign = 'left',
}: DataTableProps<Item>) {
  const [sorting, setSorting] = useState<SortingState>([]);

  const tableColumns = useMemo<ColumnDef<Item>[]>(() => {
    return columns.map((column, index) => {
      return {
        id: `column-${index}`,
        accessorKey: column.accessorKey as string,
        header: column.title,
        size: column.width
          ? isNaN(Number(column.width))
            ? undefined
            : Number(column.width)
          : undefined,
        enableSorting: column.enableSorting !== false && !!column.accessorKey,
        meta: {
          headerAlign: column.headerAlign || headerAlign || 'left',
          column: column,
        },

        cell: ({ getValue, row }) => {
          const value = getValue();
          const item = row.original;

          if (column.type === 'actions') {
            return <div onClick={(e) => e.stopPropagation()}>{column.render(item)}</div>;
          }

          if (column.type === 'custom') {
            return <>{column.render(item, value)}</>;
          }

          if (column.type === 'switch') {
            return renderSwitchColumn(item, value as boolean, column);
          }

          if (column.type === 'button') {
            return renderButtonColumn(item, column);
          }

          if (isEmpty(value)) {
            return null;
          }

          switch (column.type) {
            case 'text':
              return renderTextColumn(item, value, column);
            case 'date':
              return renderDateColumn(item, value as Date | string | number, column);
            case 'copy':
              return renderCopyColumn(value);
            case 'badge':
              return renderBadgeColumn(value, column);
            default:
              return <span className="text-foreground">{String(value)}</span>;
          }
        },
      };
    });
  }, [columns, headerAlign]);

  const table = useReactTable({
    data,
    columns: tableColumns,
    state: {
      sorting,
    },
    onSortingChange: setSorting,
    getCoreRowModel: getCoreRowModel(),
    getSortedRowModel: getSortedRowModel(),
    manualPagination: true,
  });

  if (loading) {
    return (
      <div className={cn('w-full flex items-center justify-center py-8', className)}>
        {loader || <Spinner />}
      </div>
    );
  }

  return (
    <div className={cn('w-full', className)}>
      <Table>
        <TableHeader>
          {table.getHeaderGroups().map((headerGroup) => (
            <TableRow key={headerGroup.id}>
              {headerGroup.headers.map((header) => {
                const canSort = header.column.getCanSort();
                const sortDirection = header.column.getIsSorted();
                const meta = header.column.columnDef.meta as {
                  headerAlign: AlignmentType;
                  column: Column<Item>;
                };

                return (
                  <TableHead
                    key={header.id}
                    className={cn(
                      canSort && 'hover:bg-muted/50',
                      'transition-colors',
                      ALIGNMENT_CLASSES.text[meta.headerAlign],
                    )}
                    style={{
                      width: meta.column.width,
                      minWidth: meta.column.width,
                      maxWidth: meta.column.width,
                    }}
                    onClick={canSort ? header.column.getToggleSortingHandler() : undefined}
                    isSortable={canSort}
                    sortDirection={sortDirection}
                  >
                    {flexRender(header.column.columnDef.header, header.getContext())}
                  </TableHead>
                );
              })}
            </TableRow>
          ))}
        </TableHeader>
        <TableBody>
          {table.getRowModel().rows.length === 0 ? (
            <TableRow disableHover>
              <TableCell colSpan={columns.length}>
                <EmptyState
                  {...(emptyState ?? {
                    title: 'No data available',
                    subtitle: 'There are no items to display.',
                  })}
                />
              </TableCell>
            </TableRow>
          ) : (
            table.getRowModel().rows.map((row) => (
              <TableRow
                key={row.id}
                className={cn(
                  onRowClick && 'cursor-pointer hover:bg-muted/50',
                  'transition-colors',
                )}
                onClick={() => onRowClick?.(row.original)}
              >
                {row.getVisibleCells().map((cell) => {
                  const meta = cell.column.columnDef.meta as {
                    column: Column<Item>;
                  };
                  return (
                    <TableCell
                      key={cell.id}
                      className="text-left"
                      style={{
                        width: meta.column.width,
                        minWidth: meta.column.width,
                        maxWidth: meta.column.width,
                      }}
                    >
                      {flexRender(cell.column.columnDef.cell, cell.getContext())}
                    </TableCell>
                  );
                })}
              </TableRow>
            ))
          )}
        </TableBody>
      </Table>
    </div>
  );
}

'use client';

import * as DialogPrimitive from '@radix-ui/react-dialog';
import { XIcon } from 'lucide-react';
import * as React from 'react';

import { cn } from '../../lib/theme-utils';

import { Button } from './button';

function Dialog({ ...props }: React.ComponentProps<typeof DialogPrimitive.Root>) {
  return <DialogPrimitive.Root data-slot="dialog" {...props} />;
}

function DialogTrigger({ ...props }: React.ComponentProps<typeof DialogPrimitive.Trigger>) {
  const { children, asChild = false, ...elementProps } = props;

  if (asChild && React.isValidElement(children)) {
    return React.cloneElement(children, {
      ...(typeof children.props === 'object' ? children.props : {}),
      ...elementProps,
    });
  }

  return (
    <DialogPrimitive.Trigger data-slot="dialog-trigger" asChild {...props}>
      <Button {...elementProps} data-slot="dialog-trigger">
        {children}
      </Button>
    </DialogPrimitive.Trigger>
  );
}

function DialogPortal({ ...props }: React.ComponentProps<typeof DialogPrimitive.Portal>) {
  return <DialogPrimitive.Portal data-slot="dialog-portal" {...props} />;
}

function DialogClose({ ...props }: React.ComponentProps<typeof DialogPrimitive.Close>) {
  const { children } = props;

  return (
    <DialogPrimitive.Close data-slot="dialog-close" asChild>
      <Button variant="outline" data-slot="dialog-close">
        {children}
      </Button>
    </DialogPrimitive.Close>
  );
}

function DialogOverlay({
  className,
  ...props
}: React.ComponentProps<typeof DialogPrimitive.Overlay>) {
  return (
    <DialogPrimitive.Overlay
      data-slot="dialog-overlay"
      className={cn(
        'data-[state=open]:animate-in data-[state=closed]:animate-out data-[state=closed]:fade-out-0 data-[state=open]:fade-in-0 fixed inset-0 z-50 bg-black/50 duration-200 ease-in-out',
        className,
      )}
      {...props}
    />
  );
}

function DialogContent({
  className,
  children,
  showCloseButton = true,
  ...props
}: React.ComponentProps<typeof DialogPrimitive.Content> & {
  showCloseButton?: boolean;
}) {
  return (
    <DialogPortal data-slot="dialog-portal">
      <DialogOverlay />
      <DialogPrimitive.Content
        data-slot="dialog-content"
        className={cn(
          'bg-background data-[state=open]:animate-in data-[state=closed]:animate-out data-[state=closed]:fade-out-0 data-[state=open]:fade-in-0 data-[state=closed]:zoom-out-95 data-[state=open]:zoom-in-95 shadow-bevel-2xl rounded-5xl fixed top-[50%] left-[50%] z-[999] grid w-full translate-x-[-50%] translate-y-[-50%] gap-4 p-6 duration-200 ease-in-out sm:max-w-lg',
          className,
        )}
        {...props}
      >
        {children}
        {showCloseButton && (
          <DialogPrimitive.Close
            asChild
            data-slot="dialog-close"
            className="ring-offset-background absolute top-4 right-4"
          >
            <Button variant="ghost" size="icon">
              <XIcon />
              <span className="sr-only">Close</span>
            </Button>
          </DialogPrimitive.Close>
        )}
      </DialogPrimitive.Content>
    </DialogPortal>
  );
}

function DialogHeader({ className, ...props }: React.ComponentProps<'div'>) {
  return (
    <div
      data-slot="dialog-header"
      className={cn('flex flex-col gap-2 text-center sm:text-left', className)}
      {...props}
    />
  );
}

function DialogFooter({ className, ...props }: React.ComponentProps<'div'>) {
  return (
    <div
      data-slot="dialog-footer"
      className={cn('flex flex-row gap-2 justify-end', className)}
      {...props}
    />
  );
}

function DialogTitle({ className, ...props }: React.ComponentProps<typeof DialogPrimitive.Title>) {
  return (
    <DialogPrimitive.Title
      data-slot="dialog-title"
      className={cn('text-lg text-primary leading-none font-semibold', className)}
      {...props}
    />
  );
}

function DialogDescription({
  className,
  ...props
}: React.ComponentProps<typeof DialogPrimitive.Description>) {
  return (
    <DialogPrimitive.Description
      data-slot="dialog-description"
      className={cn('text-muted-foreground text-sm', className)}
      {...props}
    />
  );
}

export {
  Dialog,
  DialogClose,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogOverlay,
  DialogPortal,
  DialogTitle,
  DialogTrigger,
};

'use client';

import type * as LabelPrimitive from '@radix-ui/react-label';
import { Slot } from '@radix-ui/react-slot';
import * as React from 'react';
import {
  Controller,
  FormProvider,
  useFormContext,
  useFormState,
  type ControllerProps,
  type FieldPath,
  type FieldValues,
} from 'react-hook-form';

import { cn } from '../../lib/theme-utils';

import { Label } from './label';

const Form: typeof FormProvider = FormProvider;

type FormFieldContextValue<
  TFieldValues extends FieldValues = FieldValues,
  TName extends FieldPath<TFieldValues> = FieldPath<TFieldValues>,
> = {
  name: TName;
};

const FormFieldContext = React.createContext<FormFieldContextValue>({} as FormFieldContextValue);

const FormField = <
  TFieldValues extends FieldValues = FieldValues,
  TName extends FieldPath<TFieldValues> = FieldPath<TFieldValues>,
>({
  ...props
}: ControllerProps<TFieldValues, TName>) => {
  return (
    <FormFieldContext.Provider value={{ name: props.name }}>
      <Controller
        {...props}
        render={({ field, fieldState, formState }) => {
          const { onChange, onBlur, ...rest } = field;
          return props.render({
            field: {
              ...rest,
              onChange: (...args: unknown[]) => {
                onChange(...(args as Parameters<typeof onChange>));
              },
              onBlur: (...args: unknown[]) => {
                onBlur(...(args as Parameters<typeof onBlur>));
              },
            },
            fieldState,
            formState,
          });
        }}
      />
    </FormFieldContext.Provider>
  );
};

const useFormField = () => {
  const fieldContext = React.useContext(FormFieldContext);
  const itemContext = React.useContext(FormItemContext);
  const { getFieldState } = useFormContext();
  const formState = useFormState({ name: fieldContext.name });
  const fieldState = getFieldState(fieldContext.name, formState);

  if (!fieldContext) {
    throw new Error('useFormField should be used within <FormField>');
  }

  const { id } = itemContext;

  return {
    id,
    name: fieldContext.name,
    formItemId: `${id}-form-item`,
    formDescriptionId: `${id}-form-item-description`,
    formMessageId: `${id}-form-item-message`,
    ...fieldState,
  };
};

type FormItemContextValue = {
  id: string;
};

const FormItemContext = React.createContext<FormItemContextValue>({} as FormItemContextValue);

function FormItem({ className, ...props }: React.ComponentProps<'div'>) {
  const id = React.useId();

  return (
    <FormItemContext.Provider value={{ id }}>
      <div data-slot="form-item" className={cn('grid gap-2', className)} {...props} />
    </FormItemContext.Provider>
  );
}

function FormLabel({ className, ...props }: React.ComponentProps<typeof LabelPrimitive.Root>) {
  const { error, formItemId } = useFormField();

  return (
    <Label
      data-slot="form-label"
      data-error={!!error}
      className={cn('data-[error=true]:text-destructive-foreground', className)}
      htmlFor={formItemId}
      {...props}
    />
  );
}

function FormControl({ ...props }: React.ComponentProps<typeof Slot>) {
  const { error, formItemId, formDescriptionId, formMessageId } = useFormField();

  return (
    <Slot
      data-slot="form-control"
      id={formItemId}
      aria-describedby={!error ? `${formDescriptionId}` : `${formDescriptionId} ${formMessageId}`}
      aria-invalid={!!error}
      {...props}
    />
  );
}

function FormDescription({ className, ...props }: React.ComponentProps<'p'>) {
  const { formDescriptionId } = useFormField();

  return (
    <p
      data-slot="form-description"
      id={formDescriptionId}
      className={cn('text-muted-foreground text-sm', className)}
      {...props}
    />
  );
}

function FormMessage({ className, ...props }: React.ComponentProps<'p'>) {
  const { error, formMessageId } = useFormField();
  const body = error ? String(error?.message ?? '') : props.children;

  if (!body) {
    return null;
  }

  return (
    <p
      data-slot="form-message"
      id={formMessageId}
      className={cn('text-destructive-foreground text-sm', className)}
      {...props}
    >
      {body}
    </p>
  );
}

export {
  useFormField,
  Form,
  FormItem,
  FormLabel,
  FormControl,
  FormDescription,
  FormMessage,
  FormField,
};

import type { ActionButton } from '@auth0/web-ui-components-core';
import * as React from 'react';

import { cn } from '../../lib/theme-utils';

import { Button } from './button';
import { Spinner } from './spinner';

export interface FormActionsProps<T = void> {
  hasUnsavedChanges?: boolean;
  isLoading?: boolean;
  nextAction?: Partial<ActionButton<T>>;
  previousAction?: Partial<ActionButton<T>>;
  showPrevious?: boolean;
  showNext?: boolean;
  showUnsavedChanges?: boolean;
  align?: 'left' | 'right';
  className?: string;
  unsavedChangesText?: string;
}

const DEFAULT_NEXT_ACTION: ActionButton = {
  type: 'submit',
  label: 'Save',
  variant: 'primary',
  onClick: () => {},
};

const DEFAULT_PREVIOUS_ACTION: ActionButton = {
  label: 'Cancel',
  variant: 'outline',
  onClick: () => {},
};

export const FormActions: React.FC<FormActionsProps> = ({
  hasUnsavedChanges = false,
  isLoading = false,
  nextAction,
  previousAction,
  className,
  showPrevious = true,
  showNext = true,
  showUnsavedChanges = true,
  align = 'right',
  unsavedChangesText = 'Unsaved changes',
}) => {
  const nextButtonProps = React.useMemo(
    () => ({ ...DEFAULT_NEXT_ACTION, ...nextAction }),
    [nextAction],
  );

  const previousButtonProps = React.useMemo(
    () => ({ ...DEFAULT_PREVIOUS_ACTION, ...previousAction }),
    [previousAction],
  );

  const handleNextClick = React.useCallback(
    (e: React.MouseEvent<HTMLButtonElement>) => {
      nextButtonProps.onClick(e.nativeEvent);
    },
    [nextButtonProps.onClick],
  );

  const handlePreviousClick = React.useCallback(
    (e: React.MouseEvent<HTMLButtonElement>) => {
      previousButtonProps.onClick(e.nativeEvent);
    },
    [previousButtonProps.onClick],
  );

  const showUnsavedIndicator = showUnsavedChanges && hasUnsavedChanges;

  return (
    <div
      className={cn(
        'flex flex-row items-center gap-2 p-2',
        align === 'right' ? 'justify-end' : 'justify-start',
        className,
      )}
    >
      {showUnsavedIndicator && (
        <div className="flex items-center gap-2">
          <div
            className="w-2 h-2 rounded-full bg-orange-500 transition-colors"
            aria-hidden="true"
          />
          <span className="text-sm text-muted-foreground">{unsavedChangesText}</span>
        </div>
      )}

      {showPrevious && (
        <Button
          type="button"
          variant={previousButtonProps.variant}
          size={previousButtonProps.size}
          onClick={handlePreviousClick}
          disabled={previousButtonProps.disabled || isLoading}
          className="FormActions-previous"
        >
          {previousButtonProps.label}
        </Button>
      )}

      {showNext && (
        <Button
          type={nextButtonProps.type}
          variant={nextButtonProps.variant}
          size={nextButtonProps.size}
          disabled={nextButtonProps.disabled || isLoading}
          className="FormActions-next"
          {...(nextButtonProps.type !== 'submit' && { onClick: handleNextClick })}
        >
          {isLoading ? (
            <Spinner
              colorScheme={nextButtonProps.variant === 'destructive' ? 'primary' : 'foreground'}
              size="sm"
              aria-hidden="true"
            />
          ) : (
            <>
              {nextButtonProps.icon && (
                <span className="mr-2" aria-hidden="true">
                  {nextButtonProps.icon as React.ReactNode}
                </span>
              )}
              {nextButtonProps.label}
            </>
          )}
        </Button>
      )}
    </div>
  );
};

import { Upload, X } from 'lucide-react';
import * as React from 'react';

import { cn } from '../../lib/theme-utils';

export interface FileUploadProps
  extends Omit<React.InputHTMLAttributes<HTMLInputElement>, 'value' | 'onChange'> {
  value?: File[];
  onChange?: (files: File[]) => void;
  onRemove?: (file: File) => void;
  maxSize?: number;
  accept?: string;
  maxFiles?: number;
  preview?: boolean;
  className?: string;
}

export function FileUpload({
  value = [],
  onChange,
  onRemove,
  maxSize,
  accept,
  maxFiles = 1,
  preview = true,
  className,
  disabled,
  ...props
}: FileUploadProps) {
  const inputRef = React.useRef<HTMLInputElement>(null);
  const [dragActive, setDragActive] = React.useState(false);

  const handleDrag = React.useCallback((e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === 'dragenter' || e.type === 'dragover') {
      setDragActive(true);
    } else if (e.type === 'dragleave') {
      setDragActive(false);
    }
  }, []);

  const handleDrop = React.useCallback((e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);

    const files = Array.from(e.dataTransfer.files);
    handleFiles(files);
  }, []);

  const handleChange = React.useCallback((e: React.ChangeEvent<HTMLInputElement>) => {
    const files = Array.from(e.target.files || []);
    handleFiles(files);
  }, []);

  const handleFiles = React.useCallback(
    (files: File[]) => {
      if (disabled) return;
      const validFiles = files.filter((file) => {
        if (maxSize && file.size > maxSize) return false;
        if (
          accept &&
          !accept.split(',').some((type) => {
            const fileType = type.trim().toLowerCase();
            const fileName = file.name.toLowerCase();
            const fileMimeType = file.type.toLowerCase();
            if (fileType.startsWith('.')) {
              return fileName.endsWith(fileType);
            }

            if (fileType === fileMimeType) {
              return true;
            }

            if (fileType.endsWith('/*')) {
              const baseType = fileType.slice(0, -2);
              return fileMimeType.startsWith(`${baseType}/`);
            }

            return false;
          })
        )
          return false;
        return true;
      });

      if (maxFiles === 1) {
        onChange?.(validFiles.slice(0, 1));
      } else {
        onChange?.([...value, ...validFiles].slice(0, maxFiles));
      }

      if (inputRef.current) inputRef.current.value = '';
    },
    [disabled, maxSize, accept, maxFiles, onChange, value],
  );

  const removeFile = React.useCallback(
    (file: File) => {
      onRemove?.(file);
      onChange?.(value.filter((f) => f !== file));
    },
    [onChange, onRemove, value],
  );

  return (
    <div className={cn('space-y-4', className)}>
      <div
        onDragEnter={handleDrag}
        onDragLeave={handleDrag}
        onDragOver={handleDrag}
        onDrop={handleDrop}
        className={cn(
          'border-muted-foreground/25 relative flex min-h-[150px] cursor-pointer items-center justify-center rounded-lg border-2 border-dashed p-4 text-center transition-colors',
          dragActive && 'border-primary',
          disabled && 'cursor-not-allowed opacity-60',
          className,
        )}
      >
        <input
          ref={inputRef}
          type="file"
          multiple={maxFiles !== 1}
          accept={accept}
          onChange={handleChange}
          disabled={disabled}
          className="absolute inset-0 cursor-pointer opacity-0 disabled:cursor-not-allowed"
          {...props}
        />
        <div className="flex flex-col items-center gap-2">
          <Upload className="text-muted-foreground h-8 w-8" />
          <p className="text-muted-foreground text-sm">
            Drag & drop files here, or click to select files
          </p>
          {accept && <p className="text-muted-foreground text-xs">Allowed files: {accept}</p>}
          {maxSize && (
            <p className="text-muted-foreground text-xs">
              Max size: {(maxSize / 1024 / 1024).toFixed(2)}MB
            </p>
          )}
        </div>
      </div>

      {preview && value.length > 0 && (
        <div className="grid gap-4">
          {value.map((file, i) => (
            <div key={i} className="flex items-center gap-2 rounded-md border p-2">
              <div className="flex-1 truncate">
                <p className="text-sm font-medium">{file.name}</p>
                <p className="text-muted-foreground text-xs">{(file.size / 1024).toFixed(2)}KB</p>
              </div>
              <button
                type="button"
                onClick={() => removeFile(file)}
                className="hover:bg-muted shrink-0 rounded-md p-1 transition-colors"
                disabled={disabled}
              >
                <X className="size-4" />
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

import type { ActionButton as CoreActionButton } from '@auth0/web-ui-components-core';
