import type { CreateOrganizationDomainRequestContent } from '@auth0/web-ui-components-core';
import { BusinessError, type Domain, type IdpId } from '@auth0/web-ui-components-core';
import { useCallback, useState, useEffect } from 'react';

import { showToast } from '../../../components/ui/toast';
import { useErrorHandler } from '../../../hooks/use-error-handler';
import type {
  UseSsoDomainTabOptions,
  UseSsoDomainTabReturn,
} from '../../../types/my-org/idp-management/sso-domain/sso-domain-tab-types';
import { useCoreClient } from '../../use-core-client';
import { useTranslator } from '../../use-translator';

export function useSsoDomainTab(
  idpId: IdpId,
  { customMessages = {}, domains, provider }: Partial<UseSsoDomainTabOptions> = {},
): UseSsoDomainTabReturn {
  const { coreClient } = useCoreClient();
  const { t } = useTranslator('idp_management.notifications', customMessages);
  const { handleError } = useErrorHandler();

  const [isLoading, setIsLoading] = useState(false);
  const [domainsList, setDomainsList] = useState<Domain[]>([]);
  const [idpDomains, setIdpDomains] = useState<string[]>([]);
  const [isCreating, setIsCreating] = useState(false);
  const [selectedDomain, setSelectedDomain] = useState<Domain | null>(null);
  const [showVerifyModal, setShowVerifyModal] = useState(false);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [isVerifying, setIsVerifying] = useState(false);
  const [verifyError, setVerifyError] = useState<string | undefined>(undefined);
  const [isDeleting, setIsDeleting] = useState(false);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [isUpdating, setIsUpdating] = useState(false);
  const [isUpdatingId, setIsUpdatingId] = useState<string | null>(null);

  const fetchProviderfromDomain = useCallback(
    async (domainId: string): Promise<string | undefined> => {
      if (!coreClient) {
        return undefined;
      }

      const response = await coreClient
        .getMyOrgApiClient()
        .organization.domains.identityProviders.get(domainId);

      const isIdpEnabled = response.identity_providers?.some((idp) => idp.id === idpId);

      if (isIdpEnabled) {
        setIdpDomains((prevIdpDomains) =>
          prevIdpDomains.includes(domainId) ? prevIdpDomains : [...prevIdpDomains, domainId],
        );
        return domainId;
      }

      return undefined;
    },
    [coreClient, idpId],
  );

  const getAllProviderDomains = useCallback(
    async (fetchedDomains: Domain[]) => {
      try {
        setIsLoading(true);

        await Promise.all(
          fetchedDomains.map(async (domain) => await fetchProviderfromDomain(domain.id)),
        );
      } catch (error) {
        handleError(error, {
          fallbackMessage: t('general_error'),
        });
      } finally {
        setIsLoading(false);
      }
    },
    [domainsList, fetchProviderfromDomain, handleError, t],
  );

  const listDomains = useCallback(async (): Promise<void> => {
    if (!coreClient) {
      return;
    }

    try {
      setIsLoading(true);
      const response = await coreClient.getMyOrgApiClient().organization.domains.list();

      setDomainsList(response.organization_domains);

      await getAllProviderDomains(response.organization_domains);
    } catch (error) {
      handleError(error, {
        fallbackMessage: t('general_error'),
      });
    } finally {
      setIsLoading(false);
    }
  }, [coreClient, t]);

  const onCreateDomain = useCallback(
    async (data: CreateOrganizationDomainRequestContent): Promise<Domain | null> => {
      if (domains?.createAction?.onBefore) {
        const canProceed = domains.createAction.onBefore(data as Domain);
        if (!canProceed) {
          setIsCreating(false);
          throw new BusinessError({ message: t('domain_create.on_before') });
        }
      }

      const result: Domain = await coreClient!
        .getMyOrgApiClient()
        .organization.domains.create(data);

      domains?.createAction?.onAfter?.(result);

      await listDomains();

      return result;
    },
    [coreClient, domains, t],
  );

  const onVerifyDomain = useCallback(
    async (selectedDomain: Domain): Promise<boolean> => {
      if (domains?.verifyAction?.onBefore) {
        const canProceed = domains.verifyAction.onBefore(selectedDomain);
        if (!canProceed) {
          setIsVerifying(false);
          throw new BusinessError({ message: t('domain_verify.on_before') });
        }
      }

      const updatedDomain = await coreClient!
        .getMyOrgApiClient()
        .organization.domains.verify.create(selectedDomain.id);

      if (domains?.verifyAction?.onAfter) {
        await domains.verifyAction.onAfter(selectedDomain);
      }

      setIsVerifying(false);

      if (updatedDomain.status === 'verified') {
        setDomainsList((prevDomains) =>
          prevDomains.map((prevDomain) =>
            prevDomain.id === selectedDomain.id ? { ...prevDomain, ...updatedDomain } : prevDomain,
          ),
        );
      }
      return updatedDomain.status === 'verified';
    },
    [domains, t, coreClient],
  );

  const onDeleteDomain = useCallback(
    async (selectedDomain: Domain) => {
      if (!coreClient) {
        return;
      }

      if (domains?.deleteAction?.onBefore) {
        const canProceed = domains.deleteAction.onBefore(selectedDomain);
        if (!canProceed) {
          setIsDeleting(false);
          throw new BusinessError({ message: t('domain_delete.on_before') });
        }
      }

      await coreClient.getMyOrgApiClient().organization.domains.delete(selectedDomain.id);

      if (domains?.deleteAction?.onAfter) {
        await domains.deleteAction.onAfter(selectedDomain);
      }

      await listDomains();
    },
    [domains, listDomains, t, coreClient],
  );

  const onAssociateToProvider = useCallback(
    async (domain: Domain) => {
      if (domains?.associateToProviderAction?.onBefore) {
        const canProceed = domains.associateToProviderAction.onBefore(domain, provider);
        if (!canProceed) {
          throw new BusinessError({ message: t('domain_associate_provider.on_before') });
        }
      }

      await coreClient!.getMyOrgApiClient().organization.identityProviders.domains.create(idpId, {
        domain: domain.domain,
      });

      // Update IdP domains
      setIdpDomains((prevIdpDomains) =>
        prevIdpDomains.includes(domain.id) ? prevIdpDomains : [...prevIdpDomains, domain.id],
      );

      if (domains?.associateToProviderAction?.onAfter) {
        await domains.associateToProviderAction.onAfter(domain, provider);
      }
    },
    [domains, provider, t, coreClient],
  );

  const onDeleteFromProvider = useCallback(
    async (selectedDomain: Domain) => {
      if (!provider) {
        return;
      }

      if (domains?.deleteFromProviderAction?.onBefore) {
        const canProceed = domains.deleteFromProviderAction.onBefore(selectedDomain, provider);
        if (!canProceed) {
          throw new BusinessError({ message: t('domain_delete_provider.on_before') });
        }
      }

      await coreClient!
        .getMyOrgApiClient()
        .organization.identityProviders.domains.delete(provider.id!, selectedDomain.domain);

      // Update IdP domains
      setIdpDomains((prevDomains) =>
        prevDomains.filter((prevDomain) => prevDomain !== selectedDomain.id),
      );

      if (domains?.deleteFromProviderAction?.onAfter) {
        await domains.deleteFromProviderAction.onAfter(selectedDomain);
      }
    },
    [domains, t, coreClient, domainsList],
  );

  // ===== Handlers =====

  const handleCreate = useCallback(
    async (domainUrl: string) => {
      setIsCreating(true);

      try {
        const newDomain = await onCreateDomain({ domain: domainUrl });

        showToast({
          type: 'success',
          message: t('domain_create.success', {
            domainName: newDomain?.domain,
          }),
        });

        setSelectedDomain(newDomain);
        setShowCreateModal(false);
        setShowVerifyModal(true);
      } catch (error) {
        handleError(error, {
          fallbackMessage: t('domain_create.error'),
        });
      } finally {
        setIsCreating(false);
      }
    },
    [onCreateDomain, t],
  );

  const handleCloseVerifyModal = useCallback(() => {
    setShowVerifyModal(false);
    setVerifyError(undefined);
  }, []);

  const handleVerify = useCallback(
    async (domain: Domain) => {
      setIsVerifying(true);

      try {
        const isVerified = await onVerifyDomain(domain);
        if (isVerified) {
          setShowVerifyModal(false);

          showToast({
            type: 'success',
            message: t('domain_verify.success', {
              domainName: domain.domain,
            }),
          });

          await onAssociateToProvider(domain);
        } else {
          setVerifyError(t('domain_verify.verification_failed', { domainName: domain.domain }));
        }
      } catch (error) {
        handleError(error, {
          fallbackMessage: t('domain_verify.verification_failed'),
        });
      } finally {
        setIsVerifying(false);
      }
    },
    [onVerifyDomain, t, handleError, onAssociateToProvider],
  );

  const handleDeleteClick = useCallback((domain: Domain) => {
    setSelectedDomain(domain);
    setShowVerifyModal(false);
    setShowDeleteModal(true);
  }, []);

  const handleDelete = useCallback(
    async (domain: Domain) => {
      setIsDeleting(true);

      try {
        await onDeleteDomain(domain);

        showToast({
          type: 'success',
          message: t('domain_delete.success', {
            domainName: domain.domain,
          }),
        });

        setShowDeleteModal(false);
        setShowVerifyModal(false);
      } catch (error) {
        handleError(error, {
          fallbackMessage: t('domain_delete.error'),
        });
      } finally {
        setIsDeleting(false);
      }
    },
    [onDeleteDomain, t],
  );

  const handleVerifyActionColumn = useCallback(
    async (domain: Domain) => {
      setIsUpdating(true);
      setIsUpdatingId(domain.id);

      try {
        const isVerified = await onVerifyDomain(domain);
        if (isVerified) {
          showToast({
            type: 'success',
            message: t('domain_verify.success', {
              domainName: domain.domain,
            }),
          });

          await onAssociateToProvider(domain);
        } else {
          showToast({
            type: 'error',
            message: t('domain_verify.verification_failed', {
              domainName: domain.domain,
            }),
          });
        }
      } catch (error) {
        handleError(error, {
          fallbackMessage: t('domain_verify.verification_failed', { domainName: domain.domain }),
        });
      } finally {
        setIsUpdating(false);
        setIsUpdatingId(null);
      }
    },
    [onVerifyDomain, t, handleError, onAssociateToProvider],
  );

  const handleToggleSwitch = useCallback(
    async (domain: Domain, newCheckedValue: boolean) => {
      setIsUpdating(true);
      setIsUpdatingId(domain.id);

      if (newCheckedValue) {
        try {
          await onAssociateToProvider(domain);

          showToast({
            type: 'success',
            message: t('domain_associate_provider.success', {
              domain: domain.domain,
              idp: provider?.name,
            }),
          });
        } catch (error) {
          handleError(error, {
            fallbackMessage: t('general_error'),
          });
        } finally {
          setIsUpdating(false);
          setIsUpdatingId(null);
        }
      } else {
        try {
          await onDeleteFromProvider(domain);

          showToast({
            type: 'success',
            message: t('domain_delete_provider.success', {
              domain: domain.domain,
              idp: provider?.name,
            }),
          });
        } catch (error) {
          handleError(error, {
            fallbackMessage: t('general_error'),
          });
        } finally {
          setIsUpdating(false);
          setIsUpdatingId(null);
        }
      }
    },
    [onAssociateToProvider, t, provider, handleError, onDeleteFromProvider],
  );

  useEffect(() => {
    if (!idpId) return;

    setIsLoading(true);
    Promise.allSettled([listDomains()]).finally(() => {
      setIsLoading(false);
    });
  }, [idpId, listDomains]);

  return {
    isLoading,
    domainsList,
    isCreating,
    selectedDomain,
    showVerifyModal,
    showDeleteModal,
    isVerifying,
    verifyError,
    isDeleting,
    showCreateModal,
    handleCreate,
    handleCloseVerifyModal,
    handleVerify,
    handleDeleteClick,
    handleDelete,
    setShowCreateModal,
    setShowDeleteModal,
    idpDomains,
    handleVerifyActionColumn,
    isUpdating,
    isUpdatingId,
    handleToggleSwitch,
  };
}

import {
  OrgDetailsFactory,
  OrgDetailsMappers,
  SsoProviderMappers,
  type IdentityProvider,
  type IdpId,
  type OrganizationPrivate,
  type UpdateIdentityProviderRequestContent,
  type CreateIdpProvisioningScimTokenRequestContent,
  type GetIdPProvisioningConfigResponseContent,
  getStatusCode,
} from '@auth0/web-ui-components-core';
import { useCallback, useState, useEffect } from 'react';

import { showToast } from '../../../components/ui/toast';
import type {
  UseSsoProviderEditOptions,
  UseSsoProviderEditReturn,
} from '../../../types/my-org/idp-management/sso-provider/sso-provider-edit-types';
import { useCoreClient } from '../../use-core-client';
import { useTranslator } from '../../use-translator';

export function useSsoProviderEdit(
  idpId: IdpId,
  { sso, provisioning, customMessages = {} }: Partial<UseSsoProviderEditOptions> = {},
): UseSsoProviderEditReturn {
  const { coreClient } = useCoreClient();
  const { t } = useTranslator('idp_management.notifications', customMessages);

  const [provider, setProvider] = useState<IdentityProvider | null>(null);
  const [organization, setOrganization] = useState<OrganizationPrivate>(OrgDetailsFactory.create());
  const [isLoading, setIsLoading] = useState(false);
  const [isUpdating, setIsUpdating] = useState(false);
  const [isDeleting, setIsDeleting] = useState(false);
  const [isRemoving, setIsRemoving] = useState(false);
  const [isProvisioningUpdating, setIsProvisioningUpdating] = useState(false);
  const [isProvisioningDeleting, setIsProvisioningDeleting] = useState(false);
  const [isScimTokensLoading, setIsScimTokensLoading] = useState(false);
  const [isScimTokenCreating, setIsScimTokenCreating] = useState(false);
  const [isScimTokenDeleting, setIsScimTokenDeleting] = useState(false);
  const [isProvisioningLoading, setIsProvisioningLoading] = useState(false);
  const [provisioningConfig, setProvisioningConfig] =
    useState<GetIdPProvisioningConfigResponseContent | null>(null);

  const fetchProvider = useCallback(async (): Promise<IdentityProvider | null> => {
    if (!coreClient || !idpId) {
      return null;
    }

    try {
      setIsLoading(true);
      const response = await coreClient
        .getMyOrgApiClient()
        .organization.identityProviders.get(idpId);

      setProvider(response);
      return response;
    } catch (error) {
      showToast({
        type: 'error',
        message: t('general_error'),
      });
      return null;
    } finally {
      setIsLoading(false);
    }
  }, [coreClient, idpId, t]);

  const fetchOrganizationDetails = useCallback(async (): Promise<void> => {
    if (!coreClient) {
      return;
    }

    try {
      setIsLoading(true);

      const response = await coreClient.getMyOrgApiClient().organizationDetails.get();
      const orgData = OrgDetailsMappers.fromAPI(response);

      setOrganization(orgData);
    } catch (error) {
      const errorMessage =
        error instanceof Error
          ? t('general_error', { message: error.message })
          : t('general_error');

      showToast({
        type: 'error',
        message: errorMessage,
      });
    } finally {
      setIsLoading(false);
    }
  }, [coreClient, t]);

  const fetchProvisioning = useCallback(async () => {
    if (!coreClient || !idpId) {
      return null;
    }

    try {
      setIsProvisioningLoading(true);
      const result = await coreClient
        .getMyOrgApiClient()
        .organization.identityProviders.provisioning.get(idpId);

      setProvisioningConfig(result);
      return result;
    } catch (error) {
      const status = getStatusCode(error);
      if (status === 404) {
        setProvisioningConfig(null);
        return null;
      }

      showToast({
        type: 'error',
        message: t('general_error'),
      });
      return null;
    } finally {
      setIsProvisioningLoading(false);
    }
  }, [coreClient, idpId, t]);

  const updateProvider = useCallback(
    async (data: UpdateIdentityProviderRequestContent): Promise<void> => {
      if (!coreClient || !provider || !idpId) {
        return;
      }

      try {
        setIsUpdating(true);

        if (sso?.updateAction?.onBefore) {
          const canProceed = sso?.updateAction.onBefore(provider);
          if (!canProceed) {
            return;
          }
        }

        const apiRequestData: UpdateIdentityProviderRequestContent = SsoProviderMappers.updateToAPI(
          { strategy: provider.strategy, ...data },
        );
        const result = await coreClient
          .getMyOrgApiClient()
          .organization.identityProviders.update(idpId, apiRequestData);
        setProvider(result);

        showToast({
          type: 'success',
          message: t('update_success', { providerName: provider.display_name }),
        });

        if (sso?.updateAction?.onAfter) {
          await sso.updateAction.onAfter(provider, result);
        }
      } catch (error) {
        showToast({
          type: 'error',
          message: t('general_error'),
        });
        throw error;
      } finally {
        setIsUpdating(false);
      }
    },
    [coreClient, provider, idpId, sso, t],
  );

  const createProvisioning = useCallback(async (): Promise<void> => {
    if (!coreClient || !provider || !idpId) {
      return;
    }

    try {
      setIsProvisioningUpdating(true);

      if (provisioning?.createAction?.onBefore) {
        const canProceed = provisioning.createAction.onBefore(provider);
        if (!canProceed) {
          return;
        }
      }

      const result = await coreClient
        .getMyOrgApiClient()
        .organization.identityProviders.provisioning.create(idpId);

      const updatedProvider = await fetchProvider();
      if (updatedProvider) {
        setProvider(updatedProvider);
      }

      showToast({
        type: 'success',
        message: t('update_success', { providerName: provider.display_name }),
      });

      if (provisioning?.createAction?.onAfter) {
        await provisioning.createAction.onAfter(provider, result);
      }
    } catch (error) {
      showToast({
        type: 'error',
        message: t('general_error'),
      });
      throw error;
    } finally {
      setIsProvisioningUpdating(false);
    }
  }, [coreClient, provider, idpId, provisioning, t, fetchProvider]);

  const deleteProvisioning = useCallback(async (): Promise<void> => {
    if (!coreClient || !provider || !idpId) {
      return;
    }

    try {
      setIsProvisioningDeleting(true);

      if (provisioning?.deleteAction?.onBefore) {
        const canProceed = provisioning.deleteAction.onBefore(provider);
        if (!canProceed) {
          return;
        }
      }

      await coreClient
        .getMyOrgApiClient()
        .organization.identityProviders.provisioning.delete(idpId);

      setProvisioningConfig(null);

      const updatedProvider = await fetchProvider();
      if (updatedProvider) {
        setProvider(updatedProvider);
      }

      showToast({
        type: 'success',
        message: t('update_success', { providerName: provider.display_name }),
      });

      if (provisioning?.deleteAction?.onAfter) {
        await provisioning.deleteAction.onAfter(provider);
      }
    } catch (error) {
      showToast({
        type: 'error',
        message: t('general_error'),
      });
      throw error;
    } finally {
      setIsProvisioningDeleting(false);
    }
  }, [coreClient, provider, idpId, provisioning, t, fetchProvider]);

  const listScimTokens = useCallback(async () => {
    if (!coreClient || !idpId) {
      return null;
    }

    try {
      setIsScimTokensLoading(true);
      const result = await coreClient
        .getMyOrgApiClient()
        .organization.identityProviders.provisioning.scimTokens.list(idpId);
      return result;
    } catch (error) {
      showToast({
        type: 'error',
        message: t('general_error'),
      });
      return null;
    } finally {
      setIsScimTokensLoading(false);
    }
  }, [coreClient, idpId, t]);

  const createScimToken = useCallback(
    async (data: CreateIdpProvisioningScimTokenRequestContent) => {
      if (!coreClient || !idpId) {
        return;
      }

      try {
        setIsScimTokenCreating(true);

        if (provisioning?.createScimTokenAction?.onBefore) {
          const canProceed = provisioning.createScimTokenAction.onBefore(provider!);
          if (!canProceed) {
            return;
          }
        }

        const result = await coreClient
          .getMyOrgApiClient()
          .organization.identityProviders.provisioning.scimTokens.create(idpId, data);

        showToast({
          type: 'success',
          message: t('scim_token_create_success'),
        });

        if (provisioning?.createScimTokenAction?.onAfter) {
          await provisioning.createScimTokenAction.onAfter(provider!, result);
        }

        return result;
      } catch (error) {
        showToast({
          type: 'error',
          message: t('general_error'),
        });
        throw error;
      } finally {
        setIsScimTokenCreating(false);
      }
    },
    [coreClient, idpId, provider, provisioning, t],
  );

  const deleteScimToken = useCallback(
    async (idpScimTokenId: string): Promise<void> => {
      if (!coreClient || !idpId) {
        return;
      }

      try {
        setIsScimTokenDeleting(true);

        if (provisioning?.deleteScimTokenAction?.onBefore) {
          const canProceed = provisioning.deleteScimTokenAction.onBefore(provider!);
          if (!canProceed) {
            return;
          }
        }

        await coreClient
          .getMyOrgApiClient()
          .organization.identityProviders.provisioning.scimTokens.delete(idpId, idpScimTokenId);

        showToast({
          type: 'success',
          message: t('scim_token_delete_sucess'),
        });

        if (provisioning?.deleteScimTokenAction?.onAfter) {
          await provisioning.deleteScimTokenAction.onAfter(provider!);
        }
      } catch (error) {
        showToast({
          type: 'error',
          message: t('general_error'),
        });
        throw error;
      } finally {
        setIsScimTokenDeleting(false);
      }
    },
    [coreClient, idpId, provider, provisioning, t],
  );

  const onDeleteConfirm = useCallback(async (): Promise<void> => {
    if (!provider || !coreClient || !provider.id) {
      return;
    }

    try {
      setIsDeleting(true);

      await coreClient.getMyOrgApiClient().organization.identityProviders.delete(provider.id);

      showToast({
        type: 'success',
        message: t('delete_success', { providerName: provider.display_name }),
      });

      if (sso?.deleteAction?.onAfter) {
        await sso.deleteAction.onAfter(provider);
      }
    } catch (error) {
      showToast({
        type: 'error',
        message: t('general_error'),
      });
      throw error;
    } finally {
      setIsDeleting(false);
    }
  }, [sso, provider, t, coreClient]);

  const onRemoveConfirm = useCallback(async (): Promise<void> => {
    if (!provider || !coreClient || !provider.id) {
      return;
    }

    try {
      setIsRemoving(true);

      if (sso?.deleteFromOrgAction?.onBefore) {
        const canProceed = sso.deleteFromOrgAction.onBefore(provider);
        if (!canProceed) {
          return;
        }
      }

      await fetchOrganizationDetails();

      await coreClient.getMyOrgApiClient().organization.identityProviders.detach(provider.id);

      showToast({
        type: 'success',
        message: t('remove_success', {
          providerName: provider.display_name,
          organizationName: organization?.display_name,
        }),
      });
      if (sso?.deleteFromOrgAction?.onAfter) {
        await sso.deleteFromOrgAction.onAfter(provider);
      }
    } catch (error) {
      showToast({
        type: 'error',
        message: t('general_error'),
      });
      throw error;
    } finally {
      setIsRemoving(false);
    }
  }, [sso, provider, fetchOrganizationDetails, t, coreClient, organization]);

  useEffect(() => {
    if (!idpId) return;

    setIsLoading(true);
    Promise.allSettled([fetchProvider(), fetchOrganizationDetails()]).finally(() => {
      setIsLoading(false);
    });
  }, [idpId]);

  return {
    provider,
    organization,
    provisioningConfig,
    isLoading,
    isUpdating,
    isDeleting,
    isRemoving,
    isProvisioningUpdating,
    isProvisioningDeleting,
    isProvisioningLoading,
    isScimTokensLoading,
    isScimTokenCreating,
    isScimTokenDeleting,
    fetchProvider,
    fetchOrganizationDetails,
    fetchProvisioning,
    updateProvider,
    createProvisioning,
    deleteProvisioning,
    listScimTokens,
    createScimToken,
    deleteScimToken,
    onDeleteConfirm,
    onRemoveConfirm,
  };
}

