import { type EnhancedTranslationFunction } from '@auth0/web-ui-components-core';
import { useMemo, useCallback } from 'react';

import { useCoreClient } from './use-core-client';

/**
 * Custom hook for accessing the i18n service from CoreClient.
 *
 * This hook provides access to the i18n service from the CoreClient context,
 * including enhanced translation functions with Trans component support and language change capabilities.
 *
 * @param namespace - The translation namespace (e.g., 'mfa', 'common')
 * @param overrides - Optional translation overrides for the namespace
 * @returns An object containing the enhanced translator function and changeLanguage function
 *
 * @example
 * ```tsx
 * // Basic usage with namespace only
 * const { t, changeLanguage } = useTranslator('common');
 *
 * // Usage with overrides
 * const { t } = useTranslator('mfa', {
 *   title: 'Custom Title',
 *   'sms.title': 'Text Message'
 * });
 *
 * // Using the basic translator
 * const title = t('title');
 * const message = t('welcome', { name: 'John' });
 *
 * // Using the trans method for safe HTML rendering
 * const elements = t.trans('help.message', {
 *   components: {
 *     link: (children) => <a href="/help" target="_blank">{children}</a>,
 *     strong: (children) => <strong>{children}</strong>
 *   },
 *   vars: { name: 'John' }
 * });
 *
 * // Render the elements
 * return (
 *   <div>
 *     {elements.map((element, index) => (
 *       <Fragment key={index}>{element}</Fragment>
 *     ))}
 *   </div>
 * );
 *
 * // Changing language
 * await changeLanguage('es-ES');
 * ```
 */
export function useTranslator(
  namespace: string,
  overrides?: Record<string, unknown>,
): {
  t: EnhancedTranslationFunction;
  changeLanguage: (language: string, fallbackLanguage?: string) => Promise<void>;
  currentLanguage: string;
  fallbackLanguage: string | undefined;
} {
  const { coreClient } = useCoreClient();

  if (!coreClient) {
    throw new Error(
      'useTranslator must be used within Auth0ComponentProvider with initialized CoreClient',
    );
  }

  const translator = useMemo(() => {
    return coreClient.i18nService.translator(namespace, overrides);
  }, [coreClient, namespace, overrides]);

  const changeLanguage = useCallback(
    async (language: string, fallbackLanguage?: string) => {
      await coreClient.i18nService.changeLanguage(language, fallbackLanguage);
    },
    [coreClient],
  );

  return {
    t: translator,
    changeLanguage,
    currentLanguage: coreClient.i18nService.currentLanguage,
    fallbackLanguage: coreClient.i18nService.fallbackLanguage,
  };
}

import {
  AVAILABLE_STRATEGY_LIST,
  type GetConfigurationResponseContent,
  type IdpStrategy,
} from '@auth0/web-ui-components-core';
import { useCallback, useEffect, useMemo, useState } from 'react';

import type { UseConfigResult } from '../../../types/my-org/config/config-types';
import { useCoreClient } from '../../use-core-client';

export function useConfig(): UseConfigResult {
  const { coreClient } = useCoreClient();
  const [config, setConfig] = useState<GetConfigurationResponseContent | null>(null);
  const [isLoadingConfig, setIsLoadingConfig] = useState(false);

  const fetchConfig = useCallback(async (): Promise<void> => {
    if (!coreClient) {
      return;
    }
    setIsLoadingConfig(true);

    try {
      const result: GetConfigurationResponseContent = await coreClient
        .getMyOrgApiClient()
        .organization.configuration.get();
      setConfig(result);
    } finally {
      setIsLoadingConfig(false);
    }
  }, [coreClient]);

  const filteredStrategies = useMemo((): IdpStrategy[] => {
    const allowedStrategies = config?.allowed_strategies;

    if (!allowedStrategies) {
      return AVAILABLE_STRATEGY_LIST;
    }

    return AVAILABLE_STRATEGY_LIST.filter((strategy) => allowedStrategies.includes(strategy));
  }, [config]);

  const shouldAllowDeletion = useMemo((): boolean => {
    return (
      config?.connection_deletion_behavior === 'allow' ||
      config?.connection_deletion_behavior === 'allow_if_empty'
    );
  }, [config]);

  // Fetch config on mount
  useEffect(() => {
    fetchConfig();
  }, []);

  return {
    config,
    isLoadingConfig,
    fetchConfig,
    filteredStrategies,
    shouldAllowDeletion,
  };
}

import type { IdpStrategy } from '@auth0/web-ui-components-core';
import { useCallback, useEffect, useState } from 'react';

import type { IdpConfig, UseConfigIdpResult } from '../../../types/my-org/config/config-idp-types';
import { useCoreClient } from '../../use-core-client';

export function useIdpConfig(): UseConfigIdpResult {
  const { coreClient } = useCoreClient();
  const [idpConfig, setIdpConfig] = useState<IdpConfig | null>(null);
  const [isLoadingIdpConfig, setIsLoadingIdpConfig] = useState(false);

  const fetchIdpConfig = useCallback(async (): Promise<void> => {
    if (!coreClient) {
      return;
    }
    setIsLoadingIdpConfig(true);

    try {
      const result = (await coreClient
        .getMyOrgApiClient()
        .organization.configuration.identityProviders.get()) as unknown as IdpConfig;

      setIdpConfig(result);
    } finally {
      setIsLoadingIdpConfig(false);
    }
  }, [coreClient]);

  const isProvisioningEnabled = useCallback(
    (strategy: IdpStrategy | undefined): boolean => {
      if (!strategy || !idpConfig?.strategies?.[strategy]) {
        return false;
      }
      return idpConfig.strategies[strategy].enabled_features.includes('provisioning');
    },
    [idpConfig],
  );

  const isProvisioningMethodEnabled = useCallback(
    (strategy: IdpStrategy | undefined): boolean => {
      if (!strategy || !idpConfig?.strategies?.[strategy]) {
        return false;
      }
      return idpConfig.strategies[strategy].provisioning_methods.includes('scim');
    },
    [idpConfig],
  );

  // Fetch config on mount
  useEffect(() => {
    fetchIdpConfig();
  }, []);

  return {
    idpConfig,
    isLoadingIdpConfig,
    fetchIdpConfig,
    isProvisioningEnabled,
    isProvisioningMethodEnabled,
  };
}

