'use client';

import * as React from 'react';

import { Toaster } from '../components/ui/sonner';
import { Spinner } from '../components/ui/spinner';
import { CoreClientContext } from '../hooks/use-core-client';
import { useCoreClientInitialization } from '../hooks/use-core-client-initialization';
import type { Auth0ComponentProviderProps } from '../types/auth-types';

import { ScopeManagerProvider } from './scope-manager-provider';
import { ThemeProvider } from './theme-provider';

/**
 * Auth0 Proxy Provider for Next.js and server-side authentication
 *
 * Use this when you have a backend proxy that handles Auth0 authentication.
 *
 * @example
 * ```tsx
 * <Auth0ProxyProvider
 *   authDetails={{ authProxyUrl: '/api/auth' }}
 *   themeSettings={{ mode: 'dark', theme: 'rounded' }}
 * >
 *   <YourApp />
 * </Auth0ProxyProvider>
 * ```
 */
export const Auth0ComponentProvider = ({
  i18n,
  authDetails,
  themeSettings = {
    theme: 'default',
    mode: 'light',
    variables: {
      common: {},
      light: {},
      dark: {},
    },
  },
  loader,
  children,
}: Auth0ComponentProviderProps & { children: React.ReactNode }) => {
  const memoizedAuthDetails = React.useMemo(
    () => ({
      ...authDetails,
      contextInterface: undefined,
    }),
    [authDetails],
  );

  const coreClient = useCoreClientInitialization({
    authDetails: memoizedAuthDetails,
    i18nOptions: i18n,
  });

  const coreClientValue = React.useMemo(
    () => ({
      coreClient,
    }),
    [coreClient],
  );

  return (
    <ThemeProvider
      themeSettings={{
        mode: themeSettings.mode,
        variables: themeSettings.variables,
        loader,
        theme: themeSettings.theme,
      }}
    >
      <Toaster position="top-right" />
      <React.Suspense
        fallback={
          loader || (
            <div className="flex items-center justify-center min-h-[200px]">
              <Spinner />
            </div>
          )
        }
      >
        <CoreClientContext.Provider value={coreClientValue}>
          <ScopeManagerProvider>{children}</ScopeManagerProvider>
        </CoreClientContext.Provider>
      </React.Suspense>
    </ThemeProvider>
  );
};

export default Auth0ComponentProvider;

import React, { useState, useCallback, useEffect, type ReactNode } from 'react';

import { useCoreClient } from '../hooks/use-core-client';
import { ScopeManagerContext, type Audience } from '../hooks/use-scope-manager';

export const ScopeManagerProvider: React.FC<{ children: ReactNode }> = ({ children }) => {
  const { coreClient } = useCoreClient();

  const [scopeRegistry, setScopeRegistry] = useState<Record<Audience, Set<string>>>(() => ({
    me: new Set(),
    'my-org': new Set(),
  }));

  const [ensured, setEnsured] = useState<Record<Audience, string>>({
    me: '',
    'my-org': '',
  });

  const [isReady, setIsReady] = useState(false);

  const registerScopes = useCallback((audience: Audience, scopes: string) => {
    if (!scopes?.trim()) return;

    const newScopes = scopes
      .split(/\s+/)
      .map((s) => s.trim())
      .filter(Boolean);

    if (newScopes.length === 0) return;

    setScopeRegistry((currentRegistry) => {
      const audienceSet = currentRegistry[audience];
      let changed = false;
      const nextAudienceSet = new Set(audienceSet);

      newScopes.forEach((scope) => {
        if (!nextAudienceSet.has(scope)) {
          nextAudienceSet.add(scope);
          changed = true;
        }
      });

      if (changed) {
        return {
          ...currentRegistry,
          [audience]: nextAudienceSet,
        };
      }
      return currentRegistry;
    });
  }, []);

  useEffect(() => {
    if (!coreClient) return;

    const ensureAllScopesSequential = async () => {
      let hasScopes = false;
      let anyUpdated = false;

      for (const audience of ['me', 'my-org'] as const) {
        const scopes = Array.from(scopeRegistry[audience]).sort();
        const scopeString = scopes.join(' ');

        if (scopes.length > 0 && scopeString.trim()) {
          hasScopes = true;

          if (scopeString !== ensured[audience]) {
            try {
              await coreClient.ensureScopes(scopeString, audience);
              anyUpdated = true;
            } catch (error) {
              console.error(`Failed to ensure scopes for ${audience}: ${scopeString}`, error);
            }
          }
        }
      }

      // Update ensured state to match current registry
      if (anyUpdated) {
        setEnsured({
          me: Array.from(scopeRegistry.me).sort().join(' '),
          'my-org': Array.from(scopeRegistry['my-org']).sort().join(' '),
        });
      }

      setIsReady(hasScopes);
    };

    ensureAllScopesSequential();
  }, [coreClient, scopeRegistry, ensured]);

  const contextValue = React.useMemo(
    () => ({ registerScopes, isReady, ensured }),
    [registerScopes, isReady, ensured],
  );

  return (
    <ScopeManagerContext.Provider value={contextValue}>{children}</ScopeManagerContext.Provider>
  );
};

'use client';

import { useAuth0 } from '@auth0/auth0-react';
import type { BasicAuth0ContextInterface } from '@auth0/web-ui-components-core';
import * as React from 'react';

import { Toaster } from '../components/ui/sonner';
import { Spinner } from '../components/ui/spinner';
import { CoreClientContext } from '../hooks/use-core-client';
import { useCoreClientInitialization } from '../hooks/use-core-client-initialization';
import type { Auth0ComponentProviderProps } from '../types/auth-types';

import { ScopeManagerProvider } from './scope-manager-provider';
import { ThemeProvider } from './theme-provider';

export const Auth0ComponentProvider = ({
  i18n,
  authDetails,
  themeSettings = {
    theme: 'default',
    mode: 'light',
    variables: {
      common: {},
      light: {},
      dark: {},
    },
  },
  loader,
  children,
}: Auth0ComponentProviderProps & { children: React.ReactNode }) => {
  const auth0ReactContext = useAuth0();

  const auth0ContextInterface = React.useMemo(() => {
    if (auth0ReactContext && 'isAuthenticated' in auth0ReactContext) {
      return auth0ReactContext as BasicAuth0ContextInterface;
    }

    if (authDetails?.contextInterface) {
      return authDetails.contextInterface;
    }

    throw new Error(
      'Auth0ContextInterface is not available. Make sure you wrap your app with Auth0Provider from @auth0/auth0-react, or pass a contextInterface via authDetails.',
    );
  }, [auth0ReactContext, authDetails?.contextInterface]);

  const memoizedAuthDetails = React.useMemo(
    () => ({
      ...authDetails,
      contextInterface: auth0ContextInterface,
    }),
    [authDetails, auth0ContextInterface],
  );

  const coreClient = useCoreClientInitialization({
    authDetails: memoizedAuthDetails,
    i18nOptions: i18n,
  });

  const coreClientValue = React.useMemo(
    () => ({
      coreClient,
    }),
    [coreClient],
  );

  return (
    <ThemeProvider
      themeSettings={{
        mode: themeSettings.mode,
        variables: themeSettings.variables,
        loader,
        theme: themeSettings.theme,
      }}
    >
      <Toaster position="top-right" />
      <React.Suspense
        fallback={
          loader || (
            <div className="flex items-center justify-center min-h-[200px]">
              <Spinner />
            </div>
          )
        }
      >
        <CoreClientContext.Provider value={coreClientValue}>
          <ScopeManagerProvider>{children}</ScopeManagerProvider>
        </CoreClientContext.Provider>
      </React.Suspense>
    </ThemeProvider>
  );
};

export default Auth0ComponentProvider;

'use client';

import { applyStyleOverrides, type StylingVariables } from '@auth0/web-ui-components-core';
import * as React from 'react';

import type { ThemeContextValue, ThemeInput } from '../types/theme-types';

/**
 * Default empty customer overrides. (later may be UL branding)
 */
const defaultStyleOverrides: StylingVariables = { common: {}, light: {}, dark: {} };

/**
 * ThemeContext
 *
 * Provides access to customer overrides and a merged theme object for convenience.
 */
export const ThemeContext = React.createContext<ThemeContextValue>({
  isDarkMode: false,
  variables: defaultStyleOverrides,
  loader: null,
});

/**
 * ThemeProvider
 *
 * Provides theme configuration via React Context to all components in the tree.
 * It merges optional styling overrides (CSS variables).
 *
 * @param themeSettings - Optional styling overrides
 * @param children - The components that will have access to the theme
 *
 * @example
 * ```tsx
 * <ThemeProvider
 *   themeSettings={{
 *     theme: "default" | "minimal" | "rounded";
 *     mode: 'dark',
 *     variables: {
 *       common: {
 *         "--font-size-heading": "1.5rem",
 *         "--font-size-title": "1.25rem",
 *       },
 *       light: {
 *         "--color-primary": "blue",
 *       },
 *       dark: {
 *         "--color-primary": "red",
 *       },
 *     },
 *     loader: <CustomSpinner />
 *   }}
 * >
 *   <App />
 * </ThemeProvider>
 * ```
 */
export const ThemeProvider: React.FC<{
  themeSettings?: ThemeInput;
  children: React.ReactNode;
}> = ({ themeSettings, children }) => {
  const { variables, loader, mode, theme } = React.useMemo(
    () => ({
      variables: themeSettings?.variables ?? defaultStyleOverrides,
      loader: themeSettings?.loader ?? null,
      mode: themeSettings?.mode,
      theme: themeSettings?.theme,
    }),
    [themeSettings],
  );

  React.useEffect(() => {
    applyStyleOverrides(variables, mode, theme);
  }, [variables, mode, theme]);

  return (
    <ThemeContext.Provider value={{ isDarkMode: mode === 'dark', variables, loader }}>
      {children}
    </ThemeContext.Provider>
  );
};

