    scrollbar-width: thin;
    scrollbar-color: hsl(var(--border)) transparent;
  }

  ::-webkit-scrollbar {
    width: 6px;
  }

  ::-webkit-scrollbar-thumb {
    background: hsl(var(--border));
    border-radius: 6px;
  }

  ::-webkit-scrollbar-track {
    background: transparent;
  }
}

@theme inline {
