  --color-foreground: var(--foreground, var(--color-neutral-12));
  --color-card: var(--card, var(--color-neutral-min));
  --color-card-foreground: var(--card-foreground, var(--color-neutral-max));
  --color-primary: var(--primary, var(--color-carbon-9));
  --color-primary-foreground: var(--primary-foreground, var(--color-neutral-min));
  --color-secondary: var(--secondary, var(--color-neutral-3));
  --color-secondary-foreground: var(--secondary-foreground, var(--color-neutral-1));
  --color-accent: var(--accent, var(--color-neutral-2));
  --color-accent-foreground: var(--accent-foreground, var(--color-neutral-12));
  --color-muted: var(--muted, var(--color-neutral-2));
  --color-muted-foreground: var(--muted-foreground, var(--color-neutral-11));
  --color-destructive: var(--destructive, var(--color-red-3));
  --color-destructive-foreground: var(--destructive-foreground, var(--color-red-9));
  --color-popover: var(--popover, var(--color-neutral-1));
  --color-popover-foreground: var(--popover-foreground, var(--color-neutral-12));
  --color-input: var(--input, var(--color-neutral-1));
  --color-border: var(--border, var(--color-neutral-6));
  --color-ring: var(--ring, hsla(var(--color-neutral-7), 0.2));

  /**
   * CUSTOM AUTH0 VARIABLES (no shadcn equivalents)
   * These are auth0-specific and don't map to standard shadcn variables
   */

  /* Page background */
  --color-page: var(--color-neutral-2);

  /* Status colors */
  --color-info: var(--color-blue-3);
  --color-info-foreground: var(--color-blue-9);
  --color-success: var(--color-green-3);
  --color-success-foreground: var(--color-green-9);
  --color-warning: var(--color-yellow-3);
  --color-warning-foreground: var(--color-yellow-11);

  /* Extended semantic colors */
  --color-destructive-border: var(--color-red-10);
  --color-popover-border: var(--color-neutral-6);
  --color-input-foreground: var(--color-neutral-12);
  --color-input-muted: var(--color-neutral-2);

  /* Shadows */
  --shadow-bevel-xs: var(--shadow-bevel-xs);
  --shadow-bevel-sm: var(--shadow-bevel-sm);
  --shadow-bevel-md: var(--shadow-bevel-md);
  --shadow-bevel-lg: var(--shadow-bevel-md);
  --shadow-bevel-xl: var(--shadow-bevel-xl);
  --shadow-bevel-2xl: var(--shadow-bevel-2xl);

  --shadow-button-resting: var(--shadow-button-resting);
  --shadow-button-hover: var(--shadow-button-hover);
