@import '../light.css';
@import '../dark.css';

@custom-variant theme-rounded {
  &:where([data-theme='rounded'] *) {
    @slot;
  }
}

@layer base {
  [data-theme='rounded'] {
    /**
     * STANDARD SHADCN VARIABLES with user fallbacks
     * Users can override by defining --primary, --background, etc.
     */
    --color-page: var(--color-neutral-2);
    --color-background: var(--background, var(--color-neutral-1));
    --color-foreground: var(--foreground, var(--color-neutral-12));

    --color-card: var(--card, var(--color-neutral-min));
    --color-card-foreground: var(--card-foreground, var(--color-neutral-max));

    --color-primary: var(--primary, var(--color-carbon-12));
    --color-primary-foreground: var(--primary-foreground, var(--color-neutral-min));

    --color-secondary: var(--secondary, var(--color-neutral-5));
    --color-secondary-foreground: var(--secondary-foreground, var(--color-neutral-9));

    --color-accent: var(--accent, var(--color-neutral-8));
    --color-accent-foreground: var(--accent-foreground, var(--color-neutral-10));

    --color-muted: var(--muted, var(--color-neutral-3));
    --color-muted-foreground: var(--muted-foreground, var(--color-neutral-9));

    --color-border: var(--border, var(--color-neutral-7));
    --color-ring: var(--ring, oklch(from var(--color-neutral-max) l c h / 10%));

    --color-info: var(--color-blue-3);
    --color-info-foreground: var(--color-blue-9);
    --color-success: var(--color-green-3);
    --color-success-foreground: var(--color-green-9);
    --color-warning: var(--color-yellow-3);
    --color-warning-foreground: var(--color-yellow-11);

    --color-destructive: var(--destructive, var(--color-red-4));
    --color-destructive-foreground: var(--destructive-foreground, var(--color-red-9));
    --color-destructive-border: var(--color-red-10);

    --color-popover: var(--popover, var(--color-neutral-min));
    --color-popover-foreground: var(--popover-foreground, var(--color-neutral-12));

    --color-input: var(--input, var(--color-neutral-min));
    --color-input-foreground: var(--color-neutral-12);
    --color-input-muted: var(--color-neutral-3);

    --shadow-bevel-xs:
      0px 0px 0px 1px oklch(from var(--color-neutral-max) l c h / 10%),
