      0px 2px 1px -2px oklch(from var(--color-neutral-max) l c h / 5%),
      0px 4px 2px -4px oklch(from var(--color-neutral-max) l c h / 5%),
      0px 6px 3px -6px oklch(from var(--color-neutral-max) l c h / 10%),
      0px 12px 6px -12px oklch(from var(--color-neutral-max) l c h / 10%);
    --shadow-bevel-2xl:
      0px 0px 0px 1px oklch(from var(--color-neutral-max) l c h / 10%),
      0px 1px 1px -1px oklch(from var(--color-neutral-max) l c h / 5%),
      0px 2px 1px -2px oklch(from var(--color-neutral-max) l c h / 5%),
